import React, { useState, useEffect } from 'react';
import '../styles/PropertyListings.css';
import { Link } from 'react-router-dom';
import { FiHome } from 'react-icons/fi';
import Property from './Property';

const PropertyListings = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log('API Response:', data); // Debug log to see what we're getting
        
        if (Array.isArray(data)) {
          // Optimize: Only store essential data with proper field mapping
          const optimizedData = data.map(property => {
            // Handle different possible field names for likes count
            let likesCount = 0;
            
            // Try multiple possible field names for likes
            if (property.likes_count !== undefined && property.likes_count !== null) {
              likesCount = parseInt(property.likes_count) || 0;
            } else if (property.total_likes !== undefined && property.total_likes !== null) {
              likesCount = parseInt(property.total_likes) || 0;
            } else if (property.favorites_count !== undefined && property.favorites_count !== null) {
              likesCount = parseInt(property.favorites_count) || 0;
            } else if (property.like_count !== undefined && property.like_count !== null) {
              likesCount = parseInt(property.like_count) || 0;
            }
            
            console.log(`Property ${property.id} - Title: ${property.title}, Likes: ${likesCount}`); // Debug log
            
            return {
              id: property.id,
              title: property.title,
              price: property.price,
              main_image: property.main_image,
              city: property.city,
              bedrooms: property.bedrooms || property.bedroom_count || property.bed_count || property.beds,
              bathrooms: property.bathrooms || property.bathroom_count || property.bath_count || property.baths,
              status: property.status,
              description: property.description,
              likes_count: likesCount
            };
          });
          
          console.log('Processed properties:', optimizedData); // Debug log
          
          // Sort by likes count (descending) and get top 4 most liked chalets
          const mostLikedProperties = optimizedData
            .filter(property => property.id && property.title) // Make sure we have valid properties
            .sort((a, b) => {
              // Sort by likes count first, then by ID as tiebreaker
              if (b.likes_count !== a.likes_count) {
                return b.likes_count - a.likes_count;
              }
              return b.id - a.id; // Newer properties as tiebreaker
            })
            .slice(0, 4);
          
          console.log('Most liked properties:', mostLikedProperties); // Debug log
          
          setProperties(mostLikedProperties);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <section className="property-listings">
      <div className="listings-header">
        <h2 className="listings-title">Most Loved Swiss Chalets</h2>
        <p className="listings-subtitle">
          Discover the chalets that have captured the hearts of our visitors. These are the most favorited and highly appreciated properties across Switzerland.
        </p>
      </div>

      <div className="properties-container">
        <div className="properties-grid">
          {properties.map((property, index) => (
            <div key={property.id} className="property-with-rank">
              {/* Optional: Show rank badge for most liked */}
              <div className="rank-badge">#{index + 1}</div>
              <Property 
                property={property} 
                variant="default"
                onFavoriteChange={(propertyId, isLiked) => {
                  // Update likes count when favorite status changes
                  setProperties(prevProperties => 
                    prevProperties.map(prop => 
                      prop.id === propertyId 
                        ? { ...prop, likes_count: prop.likes_count + (isLiked ? 1 : -1) }
                        : prop
                    )
                  );
                }}
              />
              {/* Show likes count */}
              <div className="likes-display">
                ❤️ {property.likes_count} likes
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="view-all-container">
        <Link to="/properties" className="view-all-btn">
          <FiHome /> View All Chalets
        </Link>
      </div>
    </section>
  );
};

export default PropertyListings;
