import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Property from '../components/Property';
import '../styles/AllPropertypage.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import SearchFilter from '../components/SearchFilter';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';

const AllPropertiesPage = () => {
  const location = useLocation();
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [initialSearchTerm, setInitialSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [propertiesPerPage] = useState(8);

  // Add a debug display to show the current page
  const [debugInfo, setDebugInfo] = useState('');

  // For tracking click events
  const logEvent = (eventName) => {
    console.log(eventName);
    setDebugInfo(`${eventName} at ${new Date().toLocaleTimeString()}`);
  };

  useEffect(() => {
    // Get search term from URL parameters
    const queryParams = new URLSearchParams(location.search);
    const searchParam = queryParams.get('search');
    if (searchParam) {
      setInitialSearchTerm(searchParam);
    }
  }, [location.search]);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        
        if (Array.isArray(data)) {
          setProperties(data);
          
          // If there's an initial search term, filter the properties accordingly
          if (initialSearchTerm) {
            const searchLower = initialSearchTerm.toLowerCase();
            const filtered = data.filter(property => 
              property.title?.toLowerCase().includes(searchLower) || 
              property.location?.toLowerCase().includes(searchLower) ||
              property.description?.toLowerCase().includes(searchLower)
            );
            setFilteredProperties(filtered);
          } else {
            setFilteredProperties(data);
          }
          
          // Reset to first page whenever properties are fetched
          setCurrentPage(1);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, [initialSearchTerm]);

  const updateFilteredProperties = (newFiltered) => {
    setFilteredProperties(newFiltered);
    setCurrentPage(1); // Reset to first page whenever filters are applied
  };

  // Log whenever currentPage changes
  useEffect(() => {
    console.log(`Current page changed to: ${currentPage}`);
  }, [currentPage]);

  // Calculate properties to display based on current page
  const indexOfLastProperty = currentPage * propertiesPerPage;
  const indexOfFirstProperty = indexOfLastProperty - propertiesPerPage;
  const currentProperties = filteredProperties.slice(indexOfFirstProperty, indexOfLastProperty);
  const totalPages = Math.ceil(filteredProperties.length / propertiesPerPage);

  // Simple and direct page changing functions
  const goToPage = (page) => {
    logEvent(`Going to page ${page}`);
    setCurrentPage(page);
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      logEvent(`Going to previous page (${currentPage - 1})`);
      setCurrentPage(currentPage - 1);
    }
  };

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      logEvent(`Going to next page (${currentPage + 1})`);
      setCurrentPage(currentPage + 1);
    }
  };

  if (loading) return <div className="loading">Loading Chalets...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="all-properties-page">
      <Navbar />
      
      {/* Debug info display */}
      {debugInfo && (
        <div style={{ 
          background: '#f0f0f0', 
          padding: '10px', 
          margin: '10px auto', 
          maxWidth: '800px',
          border: '1px solid #ddd',
          borderRadius: '5px'
        }}>
          <p><strong>Debug Info:</strong> {debugInfo}</p>
          <p><strong>Current Page:</strong> {currentPage}</p>
          <p><strong>Total Pages:</strong> {totalPages}</p>
          <p><strong>Properties on this page:</strong> {currentProperties.length}</p>
          <p><strong>Total Filtered Properties:</strong> {filteredProperties.length}</p>
          <div>
            <button 
              onClick={() => goToPage(1)} 
              style={{ margin: '5px', padding: '5px 10px' }}
            >
              Force Page 1
            </button>
            <button 
              onClick={() => goToPage(2)}
              style={{ margin: '5px', padding: '5px 10px' }}
              disabled={totalPages < 2}
            >
              Force Page 2
            </button>
            <button 
              onClick={() => goToPage(3)}
              style={{ margin: '5px', padding: '5px 10px' }}
              disabled={totalPages < 3}
            >
              Force Page 3
            </button>
          </div>
        </div>
      )}
      
      <div className="property-listings">
        <div className="listings-header">
          <SearchFilter 
            properties={properties} 
            setFilteredProperties={updateFilteredProperties}
            initialSearchTerm={initialSearchTerm}
          />
          
          <p className="properties-count">
            {filteredProperties.length} {filteredProperties.length === 1 ? 'property' : 'properties'} found
          </p>
        </div>

        {filteredProperties.length === 0 ? (
          <div className="no-results">No properties match your search criteria. Try adjusting your filters.</div>
        ) : (
          <>
            <div className="properties-container">
              <div className="properties-slider">
                {currentProperties.map(property => (
                  <Property 
                    key={property.id} 
                    property={property}
                    variant="grid" 
                  />
                ))}
              </div>
            </div>

            {/* Simplified Pagination */}
            {totalPages > 1 && (
              <div style={{
                display: 'flex',
                justifyContent: 'center',
                margin: '20px 0',
                gap: '10px'
              }}>
                {/* Previous Page */}
                <button
                  onClick={goToPreviousPage}
                  disabled={currentPage === 1}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #ddd',
                    background: '#f8f8f8',
                    cursor: currentPage === 1 ? 'not-allowed' : 'pointer',
                    opacity: currentPage === 1 ? 0.5 : 1
                  }}
                >
                  <FiChevronLeft /> Prev
                </button>
                
                {/* Page Number Display */}
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0 15px',
                  border: '1px solid #ddd',
                  background: '#fff'
                }}>
                  Page {currentPage} of {totalPages}
                </div>
                
                {/* Next Page */}
                <button
                  onClick={goToNextPage}
                  disabled={currentPage === totalPages}
                  style={{
                    padding: '8px 16px',
                    border: '1px solid #ddd',
                    background: '#f8f8f8',
                    cursor: currentPage === totalPages ? 'not-allowed' : 'pointer',
                    opacity: currentPage === totalPages ? 0.5 : 1
                  }}
                >
                  Next <FiChevronRight />
                </button>
              </div>
            )}
            
            {/* Display which properties are being shown */}
            <div style={{ textAlign: 'center', margin: '10px 0' }}>
              Showing {indexOfFirstProperty + 1}-{Math.min(indexOfLastProperty, filteredProperties.length)} of {filteredProperties.length} properties
            </div>
          </>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
