import React, { useState, useEffect } from 'react';
import '../styles/Navbar.css';
import { Link, useLocation } from 'react-router-dom';
import {
  FiHome,
  FiSearch,
  FiMail,
  FiInfo,
  FiLogIn,
  FiUser,
  FiLogOut,
} from 'react-icons/fi';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [user, setUser] = useState(null);
  const location = useLocation();

  const getActiveLink = () => {
    const path = location.pathname;
    if (path === '/' || path === '/home') return 'home';
    if (path === '/properties') return 'buy';
    if (path === '/rent') return 'rent';
    if (path === '/about') return 'about';
    if (path === '/contact') return 'contact';
    return '';
  };

  const activeLink = getActiveLink();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  const handleHomeClick = () => {
    closeMenu();
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleClickOutside = (event) => {
    if (
      isMenuOpen &&
      !event.target.closest('.navbar-links') &&
      !event.target.closest('.menu-toggle')
    ) {
      setIsMenuOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isMenuOpen]);

  useEffect(() => {
    document.body.style.overflow = isMenuOpen ? 'hidden' : 'auto';
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isMenuOpen]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    closeMenu();
    window.location.href = '/';
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-logo">
          <Link to="/" onClick={handleHomeClick}>
            <span className="navbar-logo-icon"></span>
            Chalets Suisses
          </Link>
        </div>

        <div
          className={`menu-toggle ${isMenuOpen ? 'active' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </div>

        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          <li>
            <Link
              to="/"
              className={activeLink === 'home' ? 'active' : ''}
              onClick={handleHomeClick}
            >
              <FiHome /> Home
            </Link>
          </li>

          <li>
            <Link
              to="/properties"
              className={activeLink === 'buy' ? 'active' : ''}
              onClick={closeMenu}
            >
              <FiSearch /> Buy Chalets
            </Link>
          </li>

          <li>
            <Link
              to="/ChaletDetailPage"
              className={activeLink === 'rent' ? 'active' : ''}
              onClick={closeMenu}
            >
              <FiSearch /> Rent Chalets
            </Link>
          </li>

          <li>
            <Link
              to="/about"
              className={activeLink === 'about' ? 'active' : ''}
              onClick={closeMenu}
            >
              <FiInfo /> About Us
            </Link>
          </li>

          <li>
            <Link
              to="/contact"
              className={activeLink === 'contact' ? 'active' : ''}
              onClick={() => {
                closeMenu();
                if (location.pathname === '/contact') {
                  window.history.replaceState(null, '', '/');
                  setTimeout(() => window.history.replaceState(null, '', '/contact'), 10);
                }
              }}
            >
              <FiMail /> Contact Us
            </Link>
          </li>
          <div className="navbar-auth">
              {user ? (
                <div className="user-menu">
                  <span className="user-icon">
                    <FiUser /> {user.email.split('@')[0]}
                  </span>
                  <div className="user-dropdown">
                    <Link
                      to={user.role === 'admin' ? '/admin-dashboard' : '/user-dashboard'}
                      className="dropdown-item"
                      onClick={closeMenu}
                    >
                      Dashboard
                    </Link>
                    <button className="dropdown-item logout-btn" onClick={handleLogout}>
                      <FiLogOut /> Logout
                    </button>
                  </div>
                </div>
              ) : (
                <Link
                  to="/login"
                  className="login-btn"
                  onClick={() => {
                    closeMenu();
                    window.scrollTo(0, 0);
                  }}
                >
                  <FiLogIn /> Login / Sign Up
                </Link>
              )}
            </div>

          
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
