import React, { useState, useEffect } from 'react';
import { FiEdit, FiTrash2, FiPlus, FiX, FiSave, FiSearch, FiRefreshCw, FiChevronDown, 
  FiChevronLeft, FiChevronRight, FiChevronsLeft, FiChevronsRight, FiArchive, FiRotateCcw, FiTrash, FiFilter, FiHome } from 'react-icons/fi';
import axios from 'axios'; // Make sure to install axios: npm install axios

function PropertyModal({ isOpen, onClose, property, onSave }) {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    beds: '1',
    baths: '1',
    sqft: '100',
    city: '',
    canton: '',
    address: '',
    status: 'Available',
    property_type: 'Chalet',
    main_image: ''
  });
  const [formErrors, setFormErrors] = useState({});

  // When the modal opens, set form data to property data if editing
  useEffect(() => {
    if (property) {
      // Format price from database to a form input format
      let formattedPrice = property.price;
      if (typeof formattedPrice === 'string') {
        formattedPrice = formattedPrice.replace(/[^\d.]/g, '');
      }

      setFormData({
        title: property.title || '',
        description: property.description || '',
        price: formattedPrice || '',
        beds: property.beds?.toString() || '1',
        baths: property.baths?.toString() || '1',
        sqft: property.sqft?.toString() || '100',
        city: property.city || '',
        canton: property.canton || '',
        address: property.address || '',
        status: property.status || 'Available',
        property_type: property.property_type || 'Chalet',
        main_image: property.main_image || ''
      });
    } else {
      // Reset form for new property
      setFormData({
        title: '',
        description: '',
        price: '',
        beds: '1',
        baths: '1',
        sqft: '100',
        city: '',
        canton: '',
        address: '',
        status: 'Available',
        property_type: 'Chalet',
        main_image: ''
      });
    }
    // Clear errors when modal opens
    setFormErrors({});
  }, [property, isOpen]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const validateForm = () => {
    const errors = {};
    
    if (!formData.title.trim()) {
      errors.title = 'Title is required';
    }
    
    if (!formData.city.trim()) {
      errors.city = 'City is required';
    }
    
    if (!formData.price.trim()) {
      errors.price = 'Price is required';
    } else if (isNaN(parseFloat(formData.price))) {
      errors.price = 'Price must be a valid number';
    }
    
    if (!formData.beds.trim() || isNaN(parseInt(formData.beds))) {
      errors.beds = 'Valid number of beds is required';
    }
    
    if (!formData.baths.trim() || isNaN(parseInt(formData.baths))) {
      errors.baths = 'Valid number of baths is required';
    }
    
    if (!formData.sqft.trim() || isNaN(parseInt(formData.sqft))) {
      errors.sqft = 'Valid area in sqft is required';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      // Convert numeric strings to numbers for the API
      const processedData = {
        ...formData,
        price: parseFloat(formData.price),
        beds: parseInt(formData.beds),
        baths: parseInt(formData.baths),
        sqft: parseInt(formData.sqft)
      };
      
      if (property) {
        processedData.id = property.id;
      }
      
      onSave(processedData);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>{property ? `Edit Property: ${property.title}` : 'Add New Property'}</h3>
          <button className="close-btn" onClick={onClose}>
            <FiX />
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="property-form">
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="title">Title*</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className={formErrors.title ? 'error' : ''}
              />
              {formErrors.title && <div className="error-text">{formErrors.title}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="property_type">Property Type</label>
              <select
                id="property_type"
                name="property_type"
                value={formData.property_type}
                onChange={handleChange}
              >
                <option value="Chalet">Chalet</option>
                <option value="Apartment">Apartment</option>
                <option value="Villa">Villa</option>
                <option value="Penthouse">Penthouse</option>
              </select>
            </div>
            
            <div className="form-group">
              <label htmlFor="price">Price (CHF)*</label>
              <input
                type="text"
                id="price"
                name="price"
                value={formData.price}
                onChange={handleChange}
                className={formErrors.price ? 'error' : ''}
                placeholder="e.g. 1500000"
              />
              {formErrors.price && <div className="error-text">{formErrors.price}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="status">Status</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleChange}
              >
                <option value="Available">Available</option>
                <option value="Pending">Pending</option>
                <option value="Sold">Sold</option>
              </select>
            </div>
            
            <div className="form-group">
              <label htmlFor="beds">Bedrooms*</label>
              <input
                type="number"
                id="beds"
                name="beds"
                value={formData.beds}
                onChange={handleChange}
                className={formErrors.beds ? 'error' : ''}
                min="1"
              />
              {formErrors.beds && <div className="error-text">{formErrors.beds}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="baths">Bathrooms*</label>
              <input
                type="number"
                id="baths"
                name="baths"
                value={formData.baths}
                onChange={handleChange}
                className={formErrors.baths ? 'error' : ''}
                min="1"
              />
              {formErrors.baths && <div className="error-text">{formErrors.baths}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="sqft">Area (sqft)*</label>
              <input
                type="number"
                id="sqft"
                name="sqft"
                value={formData.sqft}
                onChange={handleChange}
                className={formErrors.sqft ? 'error' : ''}
                min="1"
              />
              {formErrors.sqft && <div className="error-text">{formErrors.sqft}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="main_image">Main Image URL</label>
              <input
                type="text"
                id="main_image"
                name="main_image"
                value={formData.main_image}
                onChange={handleChange}
                placeholder="e.g. images/properties/chalet1.jpg"
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="city">City*</label>
              <input
                type="text"
                id="city"
                name="city"
                value={formData.city}
                onChange={handleChange}
                className={formErrors.city ? 'error' : ''}
              />
              {formErrors.city && <div className="error-text">{formErrors.city}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="canton">Canton</label>
              <input
                type="text"
                id="canton"
                name="canton"
                value={formData.canton}
                onChange={handleChange}
              />
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="address">Address</label>
            <input
              type="text"
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="4"
            ></textarea>
          </div>
          
          <div className="form-actions">
            <button type="submit" className="save-btn">
              <FiSave /> Save Property
            </button>
            <button type="button" className="cancel-btn" onClick={onClose}>
              <FiX /> Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

function PropertyManagement() {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProperty, setCurrentProperty] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('newest');
  const [showSortDropdown, setShowSortDropdown] = useState(false);
  const [viewMode, setViewMode] = useState('active'); // 'active' or 'trash'
  
  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);

  const apiUrl = 'http://localhost/REAL_ESTATE_PROJECT/api';

  // Fetch properties on component mount and when viewMode changes
  useEffect(() => {
    fetchProperties();
  }, [viewMode]);

  const fetchProperties = async () => {
    setLoading(true);
    try {
      // In the future, you'll need to modify get_all_chalet.php to support is_deleted filtering
      // For now, we'll filter on the client side
      const response = await axios.get(`${apiUrl}/get_all_chalet.php`);
      
      if (response.data) {
        // Add is_deleted property if it doesn't exist in the API response
        // This is temporary until you add is_deleted to the database
        const propertiesWithDeletedState = response.data.map(property => ({
          ...property,
          is_deleted: property.is_deleted || 0 // Default to not deleted
        }));
        
        setProperties(propertiesWithDeletedState);
      } else {
        setError('Failed to fetch properties');
      }
    } catch (error) {
      console.error('Error fetching properties:', error);
      setError('Error fetching properties. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  // Filter and sort properties when search term or sort order changes
  useEffect(() => {
    if (!properties.length) return;
    
    let result = [...properties].filter(property => 
      viewMode === 'active' ? property.is_deleted === 0 : property.is_deleted === 1
    );
    
    // Filter based on search term
    if (searchTerm) {
      const lowercasedSearch = searchTerm.toLowerCase();
      result = result.filter(property => 
        property.title?.toLowerCase().includes(lowercasedSearch) || 
        property.city?.toLowerCase().includes(lowercasedSearch) ||
        property.canton?.toLowerCase().includes(lowercasedSearch) ||
        property.price?.toString().includes(lowercasedSearch)
      );
    }
    
    // Sort or filter based on sort order
    switch(sortOrder) {
      case 'price-high':
        result.sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price.replace(/[^\d.]/g, '')) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price.replace(/[^\d.]/g, '')) : b.price;
          return priceB - priceA;
        });
        break;
      case 'price-low':
        result.sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price.replace(/[^\d.]/g, '')) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price.replace(/[^\d.]/g, '')) : b.price;
          return priceA - priceB;
        });
        break;
      case 'name-asc':
        result.sort((a, b) => (a.title || '').localeCompare(b.title || ''));
        break;
      case 'name-desc':
        result.sort((a, b) => (b.title || '').localeCompare(a.title || ''));
        break;
      case 'status-available':
        result = result.filter(property => property.status === 'Available');
        break;
      case 'status-pending':
        result = result.filter(property => property.status === 'Pending');
        break;
      case 'status-sold':
        result = result.filter(property => property.status === 'Sold');
        break;
      default:
        // 'newest' - default sort by ID descending
        result.sort((a, b) => b.id - a.id);
        break;
    }
    
    setFilteredProperties(result);
    // Reset to first page when filtering/sorting changes
    setCurrentPage(1);
  }, [properties, searchTerm, sortOrder, viewMode]);

  const handleEditClick = (propertyId) => {
    const property = properties.find(property => property.id === propertyId);
    if (property) {
      setCurrentProperty(property);
      setIsModalOpen(true);
    }
  };

  const handleAddClick = () => {
    setCurrentProperty(null);
    setIsModalOpen(true);
  };

  const handleMoveToTrash = async (propertyId) => {
    if (window.confirm('Are you sure you want to move this property to trash?')) {
      // In the future, you'll need a proper API endpoint for this
      // For now, we'll just update the local state
      
      try {
        // You'll need to create this endpoint
        // const response = await axios.post(`${apiUrl}/trash_property.php`, { 
        //   propertyId: propertyId 
        // });
        
        // if (response.data.success) {
        //   // Show success message
        //   alert('Property moved to trash successfully');
        // } else {
        //   alert(response.data.message || 'Failed to move property to trash');
        //   return;
        // }
        
        // For now, just update local state
        const updatedProperties = properties.map(property => 
          property.id === propertyId ? {...property, is_deleted: 1} : property
        );
        setProperties(updatedProperties);
        alert('Property moved to trash successfully');
      } catch (error) {
        console.error('Error moving property to trash:', error);
        alert('Error moving property to trash: ' + error.message);
      }
    }
  };
  
  const handleRestoreProperty = async (propertyId) => {
    if (window.confirm('Are you sure you want to restore this property?')) {
      // In the future, you'll need a proper API endpoint for this
      // For now, we'll just update the local state
      
      try {
        // You'll need to create this endpoint
        // const response = await axios.post(`${apiUrl}/restore_property.php`, { 
        //   propertyId: propertyId 
        // });
        
        // if (response.data.success) {
        //   // Show success message
        //   alert('Property restored successfully');
        // } else {
        //   alert(response.data.message || 'Failed to restore property');
        //   return;
        // }
        
        // For now, just update local state
        const updatedProperties = properties.map(property => 
          property.id === propertyId ? {...property, is_deleted: 0} : property
        );
        setProperties(updatedProperties);
        alert('Property restored successfully');
      } catch (error) {
        console.error('Error restoring property:', error);
        alert('Error restoring property: ' + error.message);
      }
    }
  };
  
  const handleDeletePermanently = async (propertyId) => {
    if (window.confirm('Are you sure you want to permanently delete this property? This action cannot be undone.')) {
      // In the future, you'll need a proper API endpoint for this
      // For now, we'll just update the local state
      
      try {
        // You'll need to create this endpoint
        // const response = await axios.post(`${apiUrl}/delete_property_permanently.php`, { 
        //   propertyId: propertyId 
        // });
        
        // if (response.data.success) {
        //   // Show success message
        //   alert('Property permanently deleted');
        // } else {
        //   alert(response.data.message || 'Failed to permanently delete property');
        //   return;
        // }
        
        // For now, just update local state
        const updatedProperties = properties.filter(property => property.id !== propertyId);
        setProperties(updatedProperties);
        alert('Property permanently deleted');
      } catch (error) {
        console.error('Error deleting property permanently:', error);
        alert('Error deleting property permanently: ' + error.message);
      }
    }
  };

  const handleSaveProperty = async (propertyData) => {
    try {
      if (propertyData.id) {
        // Update existing property
        // You'll need to create this endpoint
        // const response = await axios.post(`${apiUrl}/update_property.php`, propertyData);
        
        // if (response.data.success) {
        //   // Show success message and update local state
        //   const updatedProperties = properties.map(property => 
        //     property.id === propertyData.id ? {...propertyData, is_deleted: property.is_deleted} : property
        //   );
        //   setProperties(updatedProperties);
        //   alert('Property updated successfully');
        // } else {
        //   alert(response.data.message || 'Failed to update property');
        // }
        
        // For now, just update local state
        const updatedProperties = properties.map(property => 
          property.id === propertyData.id ? {...propertyData, is_deleted: property.is_deleted} : property
        );
        setProperties(updatedProperties);
        alert('Property updated successfully');
      } else {
        // Add new property
        // You'll need to create this endpoint
        // const response = await axios.post(`${apiUrl}/add_property.php`, propertyData);
        
        // if (response.data.success) {
        //   // Show success message and update local state
        //   const newProperty = {
        //     ...propertyData,
        //     id: response.data.id, // Get the new ID from the API response
        //     is_deleted: 0
        //   };
        //   setProperties([...properties, newProperty]);
        //   alert('Property added successfully');
        // } else {
        //   alert(response.data.message || 'Failed to add property');
        // }
        
        // For now, just update local state with a mock ID
        const newProperty = {
          ...propertyData,
          id: Math.max(...properties.map(p => p.id), 0) + 1,
          is_deleted: 0
        };
        setProperties([...properties, newProperty]);
        alert('Property added successfully');
      }
      
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error saving property:', error);
      alert('Error saving property: ' + error.message);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (value) => {
    setSortOrder(value);
    setShowSortDropdown(false);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const toggleViewMode = () => {
    setViewMode(prevMode => prevMode === 'active' ? 'trash' : 'active');
    // Reset filters when switching views
    setSearchTerm('');
    setSortOrder('newest');
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredProperties.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredProperties.length / itemsPerPage);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const prevPage = () => setCurrentPage(prev => Math.max(prev - 1, 1));
  const firstPage = () => setCurrentPage(1);
  const lastPage = () => setCurrentPage(totalPages);

  // Helper function to render status badge
  const renderStatusBadge = (status) => {
    let className = "status ";
    
    switch(status) {
      case 'Available':
        className += "active";
        break;
      case 'Pending':
        className += "pending";
        break;
      case 'Sold':
        className += "sold";
        break;
      default:
        className += "active";
    }
    
    return (
      <span className={className}>
        {status}
      </span>
    );
  };

  // Helper function to format price
  const formatPrice = (price) => {
    if (!price) return '';
    
    // If price is already a string with formatting, return it
    if (typeof price === 'string' && price.includes('CHF')) {
      return price;
    }
    
    // Otherwise format it
    return `CHF ${Number(price).toLocaleString()}`;
  };

  return (
    <section id="properties" className="dashboard-section">
      <div className="section-header">
        <div className="section-title-row">
          <h2 className="section-title">
            <FiHome className="section-icon" />
            {viewMode === 'active' ? 'Property Management' : 'Property Trash'}
          </h2>
          <div className="header-actions">
            <button 
              className={`toggle-view-btn ${viewMode === 'trash' ? 'active' : ''}`} 
              onClick={toggleViewMode}
              title={viewMode === 'active' ? 'View Trash' : 'View Active Properties'}
            >
              {viewMode === 'active' ? (
                <><FiTrash2 /> View Trash</>
              ) : (
                <><FiArchive /> View Active Properties</>
              )}
            </button>
            
            {viewMode === 'active' && (
              <button className="add-chalet-btn" onClick={handleAddClick}>
                <FiPlus /> Add New Property
              </button>
            )}
          </div>
        </div>
        <p className="section-subtitle">
          {viewMode === 'active' 
            ? 'Manage all listed properties' 
            : 'View and manage deleted properties'}
        </p>
        
        <div className="search-card">
          <div className="search-input-wrapper">
            <FiSearch className="search-icon" />
            <input
              type="text"
              className="search-input"
              placeholder="Search by title, location or price..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
            {searchTerm && (
              <button className="clear-search-btn" onClick={clearSearch}>
                <FiX />
              </button>
            )}
          </div>
          
          <div className="sort-controls">
            <div className="sort-dropdown-container">
              <button 
                className="sort-dropdown-btn" 
                onClick={() => setShowSortDropdown(!showSortDropdown)}
              >
                {sortOrder === 'newest' && 'Newest First'}
                {sortOrder === 'price-high' && 'Price (High to Low)'}
                {sortOrder === 'price-low' && 'Price (Low to High)'}
                {sortOrder === 'name-asc' && 'Name (A-Z)'}
                {sortOrder === 'name-desc' && 'Name (Z-A)'}
                {sortOrder === 'status-available' && 'Status: Available'}
                {sortOrder === 'status-pending' && 'Status: Pending'}
                {sortOrder === 'status-sold' && 'Status: Sold'}
                <FiChevronDown />
              </button>
              
              {showSortDropdown && (
                <div className="sort-dropdown-menu">
                  <div className="dropdown-section">
                    <div className="dropdown-section-title">Sort</div>
                    <button 
                      className={`sort-option ${sortOrder === 'newest' ? 'active' : ''}`}
                      onClick={() => handleSortChange('newest')}
                    >
                      Newest First
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'price-high' ? 'active' : ''}`}
                      onClick={() => handleSortChange('price-high')}
                    >
                      Price (High to Low)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'price-low' ? 'active' : ''}`}
                      onClick={() => handleSortChange('price-low')}
                    >
                      Price (Low to High)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'name-asc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('name-asc')}
                    >
                      Name (A-Z)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'name-desc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('name-desc')}
                    >
                      Name (Z-A)
                    </button>
                  </div>
                  
                  <div className="dropdown-divider"></div>
                  
                  <div className="dropdown-section">
                    <div className="dropdown-section-title">Filter by Status</div>
                    <button 
                      className={`sort-option ${sortOrder === 'newest' || sortOrder === 'price-high' || sortOrder === 'price-low' || sortOrder === 'name-asc' || sortOrder === 'name-desc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('newest')}
                    >
                      All Statuses
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-available' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-available')}
                    >
                      Available Only
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-pending' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-pending')}
                    >
                      Pending Only
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-sold' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-sold')}
                    >
                      Sold Only
                    </button>
                  </div>
                </div>
              )}
            </div>
            
            <button className="refresh-btn" onClick={fetchProperties} title="Refresh Properties">
              <FiRefreshCw />
            </button>
          </div>
        </div>
      </div>
      
      <div className="section-content">
        {loading ? (
          <div className="loading-spinner">Loading properties...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : filteredProperties.length === 0 ? (
          <div className="no-results">
            {viewMode === 'active' ? (
              searchTerm || sortOrder.startsWith('status-') ? 
                'No properties match your search criteria.' : 
                'No properties found.'
            ) : (
              'The trash is empty.'
            )}
          </div>
        ) : (
          <>
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Location</th>
                    <th>Price</th>
                    <th>Beds/Baths</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentItems.map(property => (
                    <tr key={property.id}>
                      <td>{property.id}</td>
                      <td>{property.title}</td>
                      <td>{property.city}, {property.canton}</td>
                      <td>{formatPrice(property.price)}</td>
                      <td>{property.beds} beds / {property.baths} baths</td>
                      <td>{renderStatusBadge(property.status)}</td>
                      <td>
                        <div className="action-buttons">
                          {viewMode === 'active' ? (
                            <>
                              <button 
                                className="edit-btn"
                                onClick={() => handleEditClick(property.id)}
                                title="Edit Property"
                              >
                                <FiEdit />
                              </button>
                              <button 
                                className="trash-btn"
                                onClick={() => handleMoveToTrash(property.id)}
                                title="Move to Trash"
                              >
                                <FiTrash2 />
                              </button>
                            </>
                          ) : (
                            <>
                              <button 
                                className="restore-btn"
                                onClick={() => handleRestoreProperty(property.id)}
                                title="Restore Property"
                              >
                                <FiRotateCcw />
                              </button>
                              <button 
                                className="delete-permanently-btn"
                                onClick={() => handleDeletePermanently(property.id)}
                                title="Delete Permanently"
                              >
                                <FiTrash />
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Pagination Controls */}
            <div className="pagination-container">
              <div className="pagination-info">
                Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredProperties.length)} of {filteredProperties.length} properties
              </div>
              
              <div className="pagination-controls">
                <button 
                  className="pagination-btn first-page" 
                  onClick={firstPage}
                  disabled={currentPage === 1}
                  title="First Page"
                >
                  <FiChevronsLeft />
                </button>
                
                <button 
                  className="pagination-btn prev-page" 
                  onClick={prevPage}
                  disabled={currentPage === 1}
                  title="Previous Page"
                >
                  <FiChevronLeft />
                </button>
                
                <div className="pagination-pages">
                  {Array.from({ length: Math.min(5, totalPages) }).map((_, index) => {
                    // Show pages around current page
                    let pageToShow;
                    
                    if (totalPages <= 5) {
                      // If we have 5 or fewer pages, show all pages
                      pageToShow = index + 1;
                    } else {
                      // For more than 5 pages, show a window of pages around current page
                      const offset = Math.max(0, Math.min(totalPages - 5, currentPage - 3));
                      pageToShow = index + 1 + offset;
                    }
                    
                    return (
                      <button
                        key={pageToShow}
                        onClick={() => paginate(pageToShow)}
                        className={`pagination-btn page-number ${currentPage === pageToShow ? 'active' : ''}`}
                      >
                        {pageToShow}
                      </button>
                    );
                  })}
                </div>
                
                <button 
                  className="pagination-btn next-page" 
                  onClick={nextPage}
                  disabled={currentPage === totalPages}
                  title="Next Page"
                >
                  <FiChevronRight />
                </button>
                
                <button 
                  className="pagination-btn last-page" 
                  onClick={lastPage}
                  disabled={currentPage === totalPages}
                  title="Last Page"
                >
                  <FiChevronsRight />
                </button>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Property Modal for Adding/Editing */}
      <PropertyModal
        isOpen={isModalOpen}
        onClose={closeModal}
        property={currentProperty}
        onSave={handleSaveProperty}
      />
    </section>
  );
}

export default PropertyManagement;
