import React, { useState, useEffect, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import Property from '../components/Property';
import '../styles/AllPropertypage.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import SearchFilter from '../components/SearchFilter';
import { FiChevronLeft, FiChevronRight, FiChevronsLeft, FiChevronsRight } from 'react-icons/fi';

const AllPropertiesPage = () => {
  const location = useLocation();
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [initialSearchTerm, setInitialSearchTerm] = useState('');

  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [propertiesPerPage] = useState(8);

  const updateFilteredProperties = useCallback((newFiltered) => {
    setFilteredProperties(newFiltered);
    setCurrentPage(1); // Reset to first page whenever filters are applied
  }, []);

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const searchParam = queryParams.get('search');
    if (searchParam) {
      setInitialSearchTerm(searchParam);
    }
  }, [location.search]);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        
        if (Array.isArray(data)) {
          setProperties(data);
          
          if (initialSearchTerm) {
            const searchLower = initialSearchTerm.toLowerCase();
            const filtered = data.filter(property => 
              property.title?.toLowerCase().includes(searchLower) || 
              property.location?.toLowerCase().includes(searchLower) ||
              property.description?.toLowerCase().includes(searchLower)
            );
            setFilteredProperties(filtered);
          } else {
            setFilteredProperties(data);
          }
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, [initialSearchTerm]);

  // Calculate pagination values
  const totalPages = Math.max(1, Math.ceil(filteredProperties.length / propertiesPerPage));
  
  // Ensure currentPage is within valid range
  useEffect(() => {
    if (currentPage > totalPages) {
      setCurrentPage(totalPages);
    }
  }, [filteredProperties, currentPage, totalPages]);

  // Get current properties
  const indexOfLastProperty = currentPage * propertiesPerPage;
  const indexOfFirstProperty = indexOfLastProperty - propertiesPerPage;
  const currentProperties = filteredProperties.slice(indexOfFirstProperty, indexOfLastProperty);

  // Page navigation handlers
  const goToPage = (pageNumber) => {
    const newPage = Math.max(1, Math.min(pageNumber, totalPages));
    if (newPage !== currentPage) {
      setCurrentPage(newPage);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const nextPage = () => goToPage(currentPage + 1);
  const prevPage = () => goToPage(currentPage - 1);
  const firstPage = () => goToPage(1);
  const lastPage = () => goToPage(totalPages);

  // Generate page numbers with proper range
  const getPageNumbers = () => {
    const maxVisiblePages = 5;
    let startPage, endPage;

    if (totalPages <= maxVisiblePages) {
      startPage = 1;
      endPage = totalPages;
    } else {
      const maxPagesBeforeCurrent = Math.floor(maxVisiblePages / 2);
      const maxPagesAfterCurrent = Math.ceil(maxVisiblePages / 2) - 1;

      if (currentPage <= maxPagesBeforeCurrent) {
        startPage = 1;
        endPage = maxVisiblePages;
      } else if (currentPage + maxPagesAfterCurrent >= totalPages) {
        startPage = totalPages - maxVisiblePages + 1;
        endPage = totalPages;
      } else {
        startPage = currentPage - maxPagesBeforeCurrent;
        endPage = currentPage + maxPagesAfterCurrent;
      }
    }

    return Array.from({ length: endPage - startPage + 1 }, (_, i) => startPage + i);
  };

  if (loading) return <div className="loading">Loading Chalets...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="all-properties-page">
      <Navbar />
      
      <div className="property-listings">
        <div className="listings-header">
          <SearchFilter 
            properties={properties} 
            setFilteredProperties={updateFilteredProperties}
            initialSearchTerm={initialSearchTerm}
          />
          
          <p className="properties-count">
            {filteredProperties.length} {filteredProperties.length === 1 ? 'property' : 'properties'} found
          </p>
        </div>

        {filteredProperties.length === 0 ? (
          <div className="no-results">No properties match your search criteria. Try adjusting your filters.</div>
        ) : (
          <>
            <div className="properties-container">
              <div className="properties-slider">
                {currentProperties.map(property => (
                  <Property 
                    key={property.id} 
                    property={property}
                    variant="grid" 
                  />
                ))}
              </div>
            </div>

            {/* Pagination Controls */}
            {totalPages > 1 && (
              <div className="pagination-controls">
                <button 
                  className="page-btn first-page"
                  onClick={firstPage}
                  disabled={currentPage === 1}
                  title="First Page"
                >
                  <FiChevronsLeft />
                </button>
                
                <button 
                  className="page-btn prev-page"
                  onClick={prevPage}
                  disabled={currentPage === 1}
                  title="Previous Page"
                >
                  <FiChevronLeft />
                </button>
                
                <div className="pagination-pages">
                  {getPageNumbers().map(number => (
                    <button
                      key={`page-btn-${number}`}
                      onClick={() => goToPage(number)}
                      className={`page-btn page-number ${currentPage === number ? 'active' : ''}`}
                    >
                      {number}
                    </button>
                  ))}
                </div>
                
                <button 
                  className="page-btn next-page"
                  onClick={nextPage}
                  disabled={currentPage === totalPages}
                  title="Next Page"
                >
                  <FiChevronRight />
                </button>
                
                <button 
                  className="page-btn last-page"
                  onClick={lastPage}
                  disabled={currentPage === totalPages}
                  title="Last Page"
                >
                  <FiChevronsRight />
                </button>
              </div>
            )}
            
            <div className="pagination-info">
              Showing {indexOfFirstProperty + 1}-{Math.min(indexOfLastProperty, filteredProperties.length)} of {filteredProperties.length} properties
            </div>
          </>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
