import { useState } from 'react';
import '../styles/Login.css'; // reuse the same styles if you want

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async () => {
    const response = await fetch("http://localhost/REAL_ESTATE_PROJECT/api/register.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();
    setMessage(data.message || "Registration failed.");
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <h2 className="login-title">Create Account</h2>
        <p className="login-subtitle">Sign up to get started</p>
        
        <div className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
              type="email" 
              id="email" 
              className="login-input"
              placeholder="Enter your email" 
              onChange={e => setEmail(e.target.value)} 
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              className="login-input"
              placeholder="Enter your password" 
              onChange={e => setPassword(e.target.value)} 
            />
          </div>
          
          <button className="login-btn hero-btn hero-btn-primary" onClick={handleRegister}>
            Register
          </button>
          
          {message && (
            <p className={`login-message ${message.includes("success") ? 'success' : 'error'}`}>
              {message}
            </p>
          )}
        </div>
        
        <div className="login-footer">
          <p>Already have an account? <a href="/login" className="login-link">Log in</a></p>
        </div>
      </div>
      
      <div className="login-image-container">
        <div className="login-image"></div>
      </div>
    </div>
  );
}

export default Register;
