import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Check if user is already logged in
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const response = await fetch(
          'http://localhost/real_estate_project/api/check_session.php',
          {
            method: 'GET',
            credentials: 'include'
          }
        );

        const data = await response.json();
        
        if (data.isAuthenticated) {
          // Redirect based on user type
          navigate(data.isAdmin ? '/dashboard' : '/');
        }
      } catch (err) {
        console.error('Auth check error:', err);
        // Continue showing login page if there's an error checking auth
      }
    };

    checkAuthStatus();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
    
    try {
      console.log('Attempting login with:', { email, password });
      
      const response = await fetch(
        'http://localhost/real_estate_project/api/login.php',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
          credentials: 'include'
        }
      );

      const data = await response.json();
      console.log('Login response:', data);

      if (data.success) {
        // Store auth data in localStorage for client-side checks
        localStorage.setItem('isAuthenticated', 'true');
        localStorage.setItem('isAdmin', data.is_admin ? 'true' : 'false');
        localStorage.setItem('userId', data.user_id);
        
        // Redirect based on user type
        navigate(data.is_admin ? '/dashboard' : '/');
      } else {
        setError(data.error || 'Invalid credentials');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('Connection error. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <h1>Login to Your Account</h1>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              id="email"
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              id="password"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          
          <button 
            type="submit" 
            className="login-button"
            disabled={isLoading}
          >
            {isLoading ? 'Logging in...' : 'Login'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
