import React, { useRef, useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { 
  FiMapPin, FiHome, FiLayers, FiDroplet, FiUsers, FiCalendar, 
  FiStar, FiWifi, FiCoffee, FiChevronLeft, FiChevronRight, 
  FiX, FiEye, FiPackage, FiHeart, FiFilm, 
  FiSun, FiUser, FiCheck, FiShield, FiTv,
  FiZap, FiWind, FiThermometer, FiAperture, FiCamera,
  FiMusic, FiVolume2, FiSmartphone, FiMonitor, FiTool,
  FiBook
} from 'react-icons/fi';
import { 
  FaSnowflake, FaSkiing, FaHotTub, FaFire, FaParking,
  FaDumbbell, FaSwimmingPool, FaSpa, FaGamepad,
  FaBicycle, FaUtensils, FaWineGlass, FaCar,
  FaLeaf, FaFirstAid, FaBaby, FaPaw
} from 'react-icons/fa';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../styles/ChaletDetailPage.css';

// Helper function to format image URLs properly
const formatImageUrl = (imagePath) => {
  if (!imagePath) return "/placeholder.jpg";
  
  // If it's already a full URL, return it as is
  if (imagePath.startsWith('http://') || imagePath.startsWith('https://')) {
    return imagePath;
  }
  
  // If it's a relative path, build the full URL
  // Remove any leading slash to avoid double slashes
  const cleanPath = imagePath.startsWith('/') ? imagePath.substring(1) : imagePath;
  return `http://localhost/real_estate_project/${cleanPath}`;
};

// Enhanced Location Section Component
const LocationSection = ({ chalet }) => {
  const [mapLoading, setMapLoading] = useState(true);
  const [mapError, setMapError] = useState(false);

  // Function to generate OpenStreetMap embed URL (free alternative)
  const getOpenStreetMapUrl = (lat, lng) => {
    return `https://www.openstreetmap.org/export/embed.html?bbox=${lng-0.01},${lat-0.01},${lng+0.01},${lat+0.01}&marker=${lat},${lng}`;
  };

  // Handle map load
  const handleMapLoad = () => {
    setMapLoading(false);
  };

  // Handle map error
  const handleMapError = () => {
    setMapLoading(false);
    setMapError(true);
  };

  // Get nearby attractions/amenities data
  const getNearbyAttractions = () => {
    // This would typically come from your backend or be calculated based on location
    const defaultAttractions = [
      { name: 'Ski Resort', distance: '2.5 km', type: 'recreation' },
      { name: 'Grocery Store', distance: '1.2 km', type: 'shopping' },
      { name: 'Restaurant', distance: '800 m', type: 'dining' },
      { name: 'Hiking Trails', distance: '500 m', type: 'nature' }
    ];

    return chalet.nearbyAttractions || defaultAttractions;
  };

  // Render the appropriate map based on available data
  const renderMap = () => {
    if (chalet.latitude && chalet.longitude) {
      // Use OpenStreetMap as it's free and doesn't require API key
      const mapUrl = getOpenStreetMapUrl(chalet.latitude, chalet.longitude);
      
      return (
        <div className="map-container">
          {mapLoading && (
            <div className="map-loading">
              Loading map...
            </div>
          )}
          <iframe
            className="map-iframe"
            src={mapUrl}
            onLoad={handleMapLoad}
            onError={handleMapError}
            title={`Location of ${chalet.title}`}
            style={{ display: mapLoading ? 'none' : 'block' }}
          />
        </div>
      );
    } else {
      // Fallback for no coordinates
      return (
        <div className="map-placeholder">
          <p>Interactive map for {chalet.city}, {chalet.canton || 'Switzerland'}</p>
        </div>
      );
    }
  };

  return (
    <div className="location-section">
      <h2>Location & Area</h2>
      <p>
        {chalet.locationDescription || 
         `${chalet.title} is ideally located in the heart of ${chalet.city}, ${chalet.canton || 'Switzerland'}. 
          This prime location offers easy access to local amenities, stunning mountain views, and outdoor activities.`}
      </p>
      
      {/* Interactive Map */}
      {renderMap()}
      
      {/* Location Details Grid */}
      <div className="location-details">
        <div className="location-detail-card">
          <h4>📍 Address</h4>
          <p>
            {chalet.address || `${chalet.city}, ${chalet.canton || 'Switzerland'}`}
            {chalet.postalCode && `, ${chalet.postalCode}`}
          </p>
        </div>
        
        <div className="location-detail-card">
          <h4>🚗 Transportation</h4>
          <p>
            {chalet.transportation || 
             'Easily accessible by car and public transport. Parking available on-site.'}
          </p>
        </div>
        
        <div className="location-detail-card">
          <h4>🏔️ Nearby Attractions</h4>
          <p>
            {getNearbyAttractions().slice(0, 3).map(attraction => attraction.name).join(', ')}
          </p>
        </div>
        
        <div className="location-detail-card">
          <h4>🌡️ Climate</h4>
          <p>
            {chalet.climate || 
             'Alpine climate with warm summers and snowy winters, perfect for year-round activities.'}
          </p>
        </div>
      </div>
      
      {/* Quick Distance Information */}
      {chalet.distances && (
        <div className="distance-info">
          <h3>Distances to Key Locations</h3>
          <div className="distance-grid">
            {Object.entries(chalet.distances).map(([location, distance]) => (
              <div key={location} className="distance-item">
                <span className="location-name">{location}</span>
                <span className="distance-value">{distance}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const ChaletDetailPage = () => {
  // Get the slug from the URL parameters
  const { slug } = useParams();
  
  // Get location state (contains propertyId if coming from Property component)
  const location = useLocation();
  const propertyIdFromState = location.state?.propertyId;
  
  const navigate = useNavigate();
  const contactFormRef = useRef(null);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [showLightbox, setShowLightbox] = useState(false);
  const [chalet, setChalet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchChaletData = async () => {
      try {
        setLoading(true);
        
        let apiUrl;
        // If we have a propertyId from state, use it (preferred method)
        if (propertyIdFromState) {
          apiUrl = `http://localhost/real_estate_project/api/get_chalet.php?id=${propertyIdFromState}`;
        } 
        // Otherwise, try to fetch by the slug
        else {
          apiUrl = `http://localhost/real_estate_project/api/get_chalet_by_slug.php?slug=${slug}`;
        }
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        
        // Process the images to ensure correct URLs
        if (data.images && Array.isArray(data.images)) {
          data.images = data.images.map(img => formatImageUrl(img));
        }
        
        // If main_image exists but no images array, create one
        if (data.main_image && (!data.images || !data.images.length)) {
          data.images = [formatImageUrl(data.main_image)];
        }
        
        // If no images array exists at all, create an empty one to prevent errors
        if (!data.images) {
          data.images = ["/placeholder.jpg"];
        }
        
        // Process the features to include the actual icon components
        if (data.features) {
          data.features = data.features.map(feature => {
            return {
              ...feature,
              iconComponent: getIconComponent(feature.icon)
            };
          });
        } else {
          // Set default features if none exist
          data.features = [];
        }
        
        // Enhanced amenities processing with icons and categories
        if (data.amenities) {
          // If amenities is a string, split it
          if (typeof data.amenities === 'string') {
            data.amenities = data.amenities.split(',').map(item => item.trim());
          }
          
          // Process amenities with icons and categories
          data.amenities = data.amenities.map(amenity => {
            const amenityData = getAmenityWithIcon(amenity);
            return {
              name: amenity,
              icon: amenityData.icon,
              category: amenityData.category
            };
          });
        } else {
          // Set default amenities if none exist
          data.amenities = [];
        }
        
        setChalet(data);
      } catch (err) {
        console.error('Error fetching chalet details:', err);
        setError(`Error loading chalet details: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchChaletData();
  }, [slug, propertyIdFromState]);

  // Enhanced amenity categorization and icon mapping
  const getAmenityWithIcon = (amenityName) => {
    const amenityMap = {
      // Comfort & Climate
      'Wi-Fi': { icon: <FiWifi />, category: 'connectivity' },
      'WiFi': { icon: <FiWifi />, category: 'connectivity' },
      'Internet': { icon: <FiWifi />, category: 'connectivity' },
      'Air Conditioning': { icon: <FiWind />, category: 'comfort' },
      'Heating': { icon: <FiThermometer />, category: 'comfort' },
      'Fireplace': { icon: <FaFire />, category: 'comfort' },
      'Hot Tub': { icon: <FaHotTub />, category: 'wellness' },
      'Sauna': { icon: <FaSpa />, category: 'wellness' },
      
      // Kitchen & Dining
      'Kitchen': { icon: <FiCoffee />, category: 'kitchen' },
      'Full Kitchen': { icon: <FaUtensils />, category: 'kitchen' },
      'Dishwasher': { icon: <FiDroplet />, category: 'kitchen' },
      'Coffee Machine': { icon: <FiCoffee />, category: 'kitchen' },
      'Wine Cooler': { icon: <FaWineGlass />, category: 'kitchen' },
      'Microwave': { icon: <FiZap />, category: 'kitchen' },
      'Refrigerator': { icon: <FiPackage />, category: 'kitchen' },
      'Oven': { icon: <FiZap />, category: 'kitchen' },
      'Toaster': { icon: <FiZap />, category: 'kitchen' },
      'Blender': { icon: <FiZap />, category: 'kitchen' },
      
      // Entertainment
      'TV': { icon: <FiTv />, category: 'entertainment' },
      'Cable TV': { icon: <FiTv />, category: 'entertainment' },
      'Streaming Services': { icon: <FiMonitor />, category: 'entertainment' },
      'Sound System': { icon: <FiVolume2 />, category: 'entertainment' },
      'Games': { icon: <FaGamepad />, category: 'entertainment' },
      'Music System': { icon: <FiMusic />, category: 'entertainment' },
      'DVD Player': { icon: <FiFilm />, category: 'entertainment' },
      'Board Games': { icon: <FaGamepad />, category: 'entertainment' },
      'Books': { icon: <FiBook />, category: 'entertainment' },
      
      // Outdoor & Recreation
      'Swimming Pool': { icon: <FaSwimmingPool />, category: 'recreation' },
      'Pool': { icon: <FaSwimmingPool />, category: 'recreation' },
      'Gym': { icon: <FaDumbbell />, category: 'recreation' },
      'Fitness Center': { icon: <FaDumbbell />, category: 'recreation' },
      'Skiing': { icon: <FaSkiing />, category: 'recreation' },
      'Bicycle': { icon: <FaBicycle />, category: 'recreation' },
      'Balcony': { icon: <FiEye />, category: 'outdoor' },
      'Terrace': { icon: <FiSun />, category: 'outdoor' },
      'Garden': { icon: <FaLeaf />, category: 'outdoor' },
      'Patio': { icon: <FiSun />, category: 'outdoor' },
      'Deck': { icon: <FiSun />, category: 'outdoor' },
      'BBQ Grill': { icon: <FaFire />, category: 'outdoor' },
      'Outdoor Dining': { icon: <FaUtensils />, category: 'outdoor' },
      'Mountain View': { icon: <FiEye />, category: 'outdoor' },
      'Lake View': { icon: <FiEye />, category: 'outdoor' },
      
      // Services & Safety
      'Parking': { icon: <FaParking />, category: 'services' },
      'Private Parking': { icon: <FaCar />, category: 'services' },
      'Security': { icon: <FiShield />, category: 'safety' },
      'First Aid Kit': { icon: <FaFirstAid />, category: 'safety' },
      'Smoke Detector': { icon: <FiAperture />, category: 'safety' },
      'Cleaning Service': { icon: <FiTool />, category: 'services' },
      'Linen Service': { icon: <FiTool />, category: 'services' },
      'Towels Provided': { icon: <FiTool />, category: 'services' },
      'Concierge': { icon: <FiUser />, category: 'services' },
      'Housekeeping': { icon: <FiTool />, category: 'services' },
      'Laundry': { icon: <FiTool />, category: 'services' },
      
      // Family & Pets
      'Pet Friendly': { icon: <FaPaw />, category: 'family' },
      'Baby Cot': { icon: <FaBaby />, category: 'family' },
      'High Chair': { icon: <FaBaby />, category: 'family' },
      'Child Safety': { icon: <FiShield />, category: 'family' },
      'Children\'s Toys': { icon: <FaGamepad />, category: 'family' },
      'Crib': { icon: <FaBaby />, category: 'family' },
      
      // Bathroom
      'Hair Dryer': { icon: <FiWind />, category: 'bathroom' },
      'Towels': { icon: <FiDroplet />, category: 'bathroom' },
      'Shampoo': { icon: <FiDroplet />, category: 'bathroom' },
      'Soap': { icon: <FiDroplet />, category: 'bathroom' },
      'Bathtub': { icon: <FiDroplet />, category: 'bathroom' },
      'Jacuzzi': { icon: <FaHotTub />, category: 'bathroom' },
      
      // General
      'Washing Machine': { icon: <FiTool />, category: 'general' },
      'Dryer': { icon: <FiTool />, category: 'general' },
      'Iron': { icon: <FiTool />, category: 'general' },
      'Vacuum Cleaner': { icon: <FiTool />, category: 'general' },
      'Elevator': { icon: <FiLayers />, category: 'general' },
      'Stairs': { icon: <FiLayers />, category: 'general' },
      'Wheelchair Access': { icon: <FiUser />, category: 'general' },
      'Smoking Allowed': { icon: <FaFire />, category: 'general' },
      'No Smoking': { icon: <FiX />, category: 'general' },
      'Pets Allowed': { icon: <FaPaw />, category: 'general' },
      'No Pets': { icon: <FaPaw />, category: 'general' }
    };
    
    // Find exact match first
    const exactMatch = amenityMap[amenityName];
    if (exactMatch) return exactMatch;
    
    // Find partial match
    const partialMatch = Object.keys(amenityMap).find(key => 
      amenityName.toLowerCase().includes(key.toLowerCase()) || 
      key.toLowerCase().includes(amenityName.toLowerCase())
    );
    
    if (partialMatch) return amenityMap[partialMatch];
    
    // Default fallback
    return { icon: <FiCheck />, category: 'general' };
  };

  // Helper function to get the correct icon component based on icon name
  const getIconComponent = (iconName) => {
    const iconMap = {
      'FaSkiing': <FaSkiing />,
      'FaHotTub': <FaHotTub />,
      'FaFire': <FaFire />,
      'FiWifi': <FiWifi />,
      'FiCoffee': <FiCoffee />,
      'FaParking': <FaParking />,
      'FaSnowflake': <FaSnowflake />,
      'FiUsers': <FiUsers />,
      'FiEye': <FiEye />,
      'FiPackage': <FiPackage />,
      'FiHeart': <FiHeart />,
      'FiFilm': <FiFilm />,
      'FiWine': <FiWifi />, // Using FiWifi as placeholder since FiWine doesn't exist
      'FiSun': <FiSun />,
      'FiUser': <FiUser />,
      'FiTool': <FiTool /> // Using FiTool instead of FiBroom
    };
    
    return iconMap[iconName] || <FiCheck />;
  };

  const scrollToContactForm = () => {
    if (contactFormRef.current) {
      contactFormRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const nextImage = () => {
    if (!chalet || !chalet.images || chalet.images.length === 0) return;
    setCurrentImageIndex((prevIndex) => 
      prevIndex === chalet.images.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevImage = () => {
    if (!chalet || !chalet.images || chalet.images.length === 0) return;
    setCurrentImageIndex((prevIndex) => 
      prevIndex === 0 ? chalet.images.length - 1 : prevIndex - 1
    );
  };

  const openLightbox = (index) => {
    setCurrentImageIndex(index);
    setShowLightbox(true);
  };

  const closeLightbox = () => {
    setShowLightbox(false);
  };

  const handleBackToListings = () => {
    navigate('/');
  };

  // Create a slug from title
  const createSlug = (title) => {
    if (!title) return '';
    return title.toLowerCase()
      .replace(/[^\w\s-]/g, '')
      .replace(/\s+/g, '-');
  };

  // Function to update URL if needed
  useEffect(() => {
    if (chalet && chalet.title) {
      const correctSlug = createSlug(chalet.title);
      // If the current slug doesn't match what it should be, update the URL
      if (slug !== correctSlug) {
        navigate(`/chalet/${correctSlug}`, { 
          replace: true,
          state: { propertyId: chalet.id } // Pass the ID in state for future use
        });
      }
    }
  }, [chalet, navigate, slug]);

  // Format price with commas for better display
  const formatPrice = (price) => {
    if (!price) return 'Price on request';
    
    // If price is already a string with formatting, return it
    if (typeof price === 'string' && price.includes('CHF')) {
      return price;
    }
    
    // Otherwise format it
    return `CHF ${Number(price).toLocaleString()}`;
  };

  // Group amenities by category
  const groupAmenitiesByCategory = (amenities) => {
    const categoryNames = {
      'connectivity': 'Connectivity',
      'comfort': 'Comfort & Climate',
      'kitchen': 'Kitchen & Dining',
      'entertainment': 'Entertainment',
      'recreation': 'Recreation',
      'outdoor': 'Outdoor Spaces',
      'services': 'Services',
      'safety': 'Safety & Security',
      'family': 'Family & Pets',
      'wellness': 'Wellness & Spa',
      'bathroom': 'Bathroom',
      'general': 'General Amenities'
    };

    const grouped = amenities.reduce((acc, amenity) => {
      const category = amenity.category || 'general';
      if (!acc[category]) {
        acc[category] = [];
      }
      acc[category].push(amenity);
      return acc;
    }, {});

    // Sort categories by priority
    const categoryOrder = ['comfort', 'kitchen', 'entertainment', 'recreation', 'wellness', 'outdoor', 'bathroom', 'connectivity', 'services', 'safety', 'family', 'general'];
    
    const sortedGrouped = {};
    categoryOrder.forEach(category => {
      if (grouped[category]) {
        sortedGrouped[categoryNames[category]] = grouped[category];
      }
    });

    return sortedGrouped;
  };

  if (loading) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="loading-container">Loading chalet details...</div>
        <Footer />
      </div>
    );
  }
  
  if (error && !chalet) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="error-container">
          <p>{error}</p>
          <button className="back-button" onClick={handleBackToListings}>Back to Listings</button>
        </div>
        <Footer />
      </div>
    );
  }
  
  if (!chalet) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="error-container">
          <p>Chalet not found.</p>
          <button className="back-button" onClick={handleBackToListings}>Back to Listings</button>
        </div>
        <Footer />
      </div>
    );
  }

  // Ensure all required properties exist to prevent errors
  const displayPrice = formatPrice(chalet.price);
  const displaySqft = chalet.sqft ? chalet.sqft.toLocaleString() : 'N/A';
  const displayBeds = chalet.beds || 1;
  const displayBaths = chalet.baths || 1;
  const displayRating = chalet.rating || '4.5';
  const displayReviews = chalet.reviews || '0';
  const displayAvailability = chalet.availability || 'Contact for details';
  const displayType = chalet.type || 'Chalet';
  const displayYearBuilt = chalet.yearBuilt || 'N/A';
  const displayFloors = chalet.floors || '1';
  const displayPricePerNight = chalet.pricePerNight || '';

  // Group amenities for display
  const groupedAmenities = chalet.amenities.length > 0 ? groupAmenitiesByCategory(chalet.amenities) : {};

  return (
    <div className="chalet-detail-page">
      <Navbar />
      
      <main className="chalet-container">
      
        {/* Image Gallery */}
        <div className="chalet-gallery">
          <div className="main-image">
            <img 
              src={chalet.images[currentImageIndex]} 
              alt={chalet.title} 
              onClick={() => openLightbox(currentImageIndex)}
              onError={(e) => {
                e.target.onerror = null; 
                e.target.src = "/placeholder.jpg";
              }}
            />
            <button className="nav-button left" onClick={prevImage}>
              <FiChevronLeft />
            </button>
            <button className="nav-button right" onClick={nextImage}>
              <FiChevronRight />
            </button>
          </div>
          <div className="thumbnail-grid">
            {chalet.images.slice(0, 3).map((img, index) => (
              <div 
                key={index} 
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => setCurrentImageIndex(index)}
              >
                <img 
                  src={img} 
                  alt={`${chalet.title} ${index + 1}`} 
                  onClick={() => openLightbox(index)}
                  onError={(e) => {
                    e.target.onerror = null; 
                    e.target.src = "/placeholder.jpg";
                  }}
                />
              </div>
            ))}
            {chalet.images.length > 4 && (
              <div className="thumbnail more-images" onClick={() => openLightbox(4)}>
                <div className="more-count">+{chalet.images.length - 4}</div>
                <img 
                  src={chalet.images[4]} 
                  alt={`More images`}
                  onError={(e) => {
                    e.target.onerror = null; 
                    e.target.src = "/placeholder.jpg";
                  }}
                />
              </div>
            )}
          </div>
        </div>

        {/* Lightbox Modal */}
        {showLightbox && (
          <div className="lightbox" onClick={closeLightbox}>
            <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
              <button className="close-btn" onClick={closeLightbox}>
                <FiX />
              </button>
              <img 
                src={chalet.images[currentImageIndex]} 
                alt={chalet.title}
                onError={(e) => {
                  e.target.onerror = null; 
                  e.target.src = "/placeholder.jpg";
                }}
              />
              <button className="nav-button left" onClick={(e) => { e.stopPropagation(); prevImage(); }}>
                <FiChevronLeft />
              </button>
              <button className="nav-button right" onClick={(e) => { e.stopPropagation(); nextImage(); }}>
                <FiChevronRight />
              </button>
              <div className="image-counter">
                {currentImageIndex + 1} / {chalet.images.length}
              </div>
            </div>
          </div>
        )}

        {/* Main Content */}
        <div className="chalet-content">
          <div className="chalet-header">
            <h1>{chalet.title}</h1>
            <div className="location">
              <FiMapPin /> {chalet.city}, {chalet.canton || 'Switzerland'}
            </div>
            <div className="rating">
              <FiStar /> {displayRating} ({displayReviews} reviews)
            </div>
          </div>

          {/* Price and Status */}
          <div className="price-section">
            <div className="price">
              <span className="main-price">{displayPrice}</span>
              {displayPricePerNight && (
                <span className="secondary-price">{displayPricePerNight}</span>
              )}
            </div>
            <div className={`status ${chalet.status ? chalet.status.toLowerCase() : 'available'}`}>
              {chalet.status || 'Available'}
            </div>
          </div>

          {/* Quick Facts */}
          <div className="quick-facts">
            <div className="fact">
              <FiHome /> {displayBeds} bedrooms
            </div>
            <div className="fact">
              <FiDroplet /> {displayBaths} bathrooms
            </div>
            <div className="fact">
              <FiLayers /> {displaySqft} sqft
            </div>
            <div className="fact">
              <FiUsers /> Sleeps {displayBeds * 2}
            </div>
            <div className="fact">
              <FiCalendar /> {displayAvailability}
            </div>
          </div>

          {/* Description */}
          <div className="description-section">
            <h2>About this chalet</h2>
            <p>{chalet.description || 'Detailed description coming soon.'}</p>
          </div>

          {/* Features - using the dynamic data with icons */}
          {chalet.features.length > 0 && (
            <div className="features-section">
              <h2>Chalet Features</h2>
              <div className="features-grid">
                {chalet.features.map((feature, index) => (
                  <div key={index} className="feature">
                    <span className="feature-icon">{feature.iconComponent}</span>
                    <span>{feature.name}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Enhanced Amenities Section */}
          {Object.keys(groupedAmenities).length > 0 && (
            <div className="amenities-section">
              <h2>Amenities & Services</h2>
              <div className="amenities-container">
                {Object.entries(groupedAmenities).map(([category, amenities]) => (
                  <div key={category} className="amenity-category">
                    <h3 className="category-title">{category}</h3>
                    <div className="amenities-grid">
                      {amenities.map((amenity, index) => (
                        <div key={index} className="amenity-item">
                          <div className="amenity-icon">
                            {amenity.icon}
                          </div>
                          <span className="amenity-name">{amenity.name}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Enhanced Location Section with Interactive Map */}
          <LocationSection chalet={chalet} />

          {/* Contact Form */}
          <div className="contact-section" ref={contactFormRef}>
            <h2>Contact for more information</h2>
            <form className="contact-form">
              <div className="form-group">
                <label htmlFor="name">Name</label>
                <input type="text" id="name" required />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input type="email" id="email" required />
              </div>
              <div className="form-group">
                <label htmlFor="phone">Phone</label>
                <input type="tel" id="phone" />
              </div>
              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea 
                  id="message" 
                  rows="4" 
                  required
                  placeholder={`I'm interested in ${chalet.title}...`}
                ></textarea>
              </div>
              <button type="submit" className="submit-btn">
                Send Inquiry
              </button>
            </form>
          </div>
        </div>

        {/* Booking Sidebar */}
        <aside className="booking-sidebar">
          <div className="booking-card">
            <h3>Make an Inquiry</h3>
            <div className="price-display">
              <span className="main-price">{displayPrice}</span>
              {displayPricePerNight && (
                <span className="secondary-price">{displayPricePerNight}</span>
              )}
            </div>
            
            <div className="availability">
              <FiCalendar /> Available: {displayAvailability}
            </div>
            
            <button 
              className="book-now-btn"
              onClick={scrollToContactForm}
            >
              Book Now
            </button>
            
            <button 
              className="contact-btn"
              onClick={scrollToContactForm}
            >
              Contact Agent
            </button>
            
            <div className="property-facts">
              <div className="fact">
                <FiHome /> {displayType}
              </div>
              <div className="fact">
                <FiUsers /> Sleeps {displayBeds * 2}
              </div>
              <div className="fact">
                <FiLayers /> {displaySqft} sqft
              </div>
              <div className="fact">
                Built in {displayYearBuilt}
              </div>
              <div className="fact">
                {displayFloors} floors
              </div>
            </div>
          </div>
        </aside>
      </main>
      
      <Footer />
    </div>
  );
};

export default ChaletDetailPage;
