import { useState } from 'react';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isAdmin, setIsAdmin] = useState(null);

  const handleLogin = async () => {
    const response = await fetch("http://localhost/REAL_ESTATE_PROJECT/api/login.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();

    if (data.success) {
      setMessage("Login successful!");
      setIsAdmin(data.is_admin);
      // You can also save to localStorage or redirect
    } else {
      setMessage(data.message || "Login failed.");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input type="email" placeholder="Email" onChange={e => setEmail(e.target.value)} />
      <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Lgin</button>
      <p>{message}</p>
      {isAdmin !== null && <p>You are {isAdmin ? "an Admin" : "a User"}</p>}
    </div>
  );
}

export default Login;
