import React from 'react';
import { FiMapPin, FiHome, FiLayers, FiDroplet, FiClock } from 'react-icons/fi';

const Property = ({ 
  property, 
  variant = 'default' // 'default' or 'detailed'
}) => {
  return (
    <div className={`property-card ${variant}`}>
      <div className="image-container">
        <img
          src={property.image}
          alt={property.address}
          className="property-image"
          loading="lazy"
        />
        <div className="property-badge">{property.status}</div>
      </div>
      
      <div className="property-details">
        {variant === 'detailed' && (
          <>
            <h3>{property.type}</h3>
            <div className="property-price">
              {property.price} 
              {property.pricePerNight && <span>{property.pricePerNight}</span>}
            </div>
            <div className="divider"></div>
          </>
        )}
        
        {variant === 'default' && (
          <>
            <span className="days-on-market">
              <FiClock /> {property.daysOnMarket} days listed
            </span>
            <div className="property-price">{property.price}</div>
          </>
        )}
        
        <div className="property-address">
          <FiMapPin /> {property.address}
        </div>
        
        <div className="property-meta">
          <span><FiHome /> {property.beds} {variant === 'detailed' ? 'beds' : 'bedrooms'}</span>
          <span><FiDroplet /> {property.baths} baths</span>
          <span><FiLayers /> {property.sqft.toLocaleString()} sqft</span>
        </div>
      </div>
    </div>
  );
};

export default Property;
