import React, { useState, useEffect, useRef } from 'react';
import { FiEdit, FiEye, FiTrash2, FiPlus, FiX, FiSave, FiSearch, FiRefreshCw, FiChevronDown, 
  FiChevronLeft, FiChevronRight, FiChevronsLeft, FiChevronsRight, FiArchive, FiRotateCcw, FiTrash, FiFilter, FiHome, FiUser, FiUpload, FiImage, FiMapPin, FiMap } from 'react-icons/fi';
import axios from 'axios';
import '../styles/PropertyManagement.css';

function PropertyModal({ isOpen, onClose, property, onSave }) {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    beds: '1',
    baths: '1',
    sqft: '100',
    city: '',
    canton: '',
    address: '',
    postal_code: '',
    latitude: '',
    longitude: '',
    status: 'Available',
    property_type: 'Chalet',
    main_image: '',
    gallery_images: [],
    user_id: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [users, setUsers] = useState([]);
  const [loadingUsers, setLoadingUsers] = useState(false);
  
  // Image upload states
  const [imageFile, setImageFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [dragActive, setDragActive] = useState(false);
  const fileInputRef = useRef(null);
  
  // Gallery images states
  const [galleryFiles, setGalleryFiles] = useState([]);
  const [galleryPreviews, setGalleryPreviews] = useState([]);
  const [galleryDragActive, setGalleryDragActive] = useState(false);
  const [galleryUploadProgress, setGalleryUploadProgress] = useState(0);
  const [isGalleryUploading, setIsGalleryUploading] = useState(false);
  const galleryInputRef = useRef(null);

  // Map picker states
  const [showMapPicker, setShowMapPicker] = useState(false);
  const [mapInitialized, setMapInitialized] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [isLoadingAddress, setIsLoadingAddress] = useState(false);
  const mapRef = useRef(null);
  const markerRef = useRef(null);

  // Fetch users for dropdown
  useEffect(() => {
    const fetchUsers = async () => {
      setLoadingUsers(true);
      try {
        const response = await axios.get('http://localhost/real_estate_project/api/get_users.php', {
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (response.data.success) {
          setUsers(response.data.users || []);
        } else {
          console.error('Failed to fetch users:', response.data.message);
        }
      } catch (error) {
        console.error('Error fetching users:', error);
      } finally {
        setLoadingUsers(false);
      }
    };

    if (isOpen) {
      fetchUsers();
    }
  }, [isOpen]);

  // Initialize Google Maps when map picker is opened
  useEffect(() => {
    if (showMapPicker && !mapInitialized) {
      initializeMap();
    }
  }, [showMapPicker]);

  const initializeMap = () => {
    if (!window.google) {
      // Load Google Maps API if not already loaded
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = () => {
        createMap();
      };
      document.head.appendChild(script);
    } else {
      createMap();
    }
  };

  const createMap = () => {
    // Default to Switzerland center
    const defaultCenter = { lat: 46.8182, lng: 8.2275 };
    
    // If editing existing property with coordinates, use those
    let initialCenter = defaultCenter;
    if (formData.latitude && formData.longitude) {
      initialCenter = {
        lat: parseFloat(formData.latitude),
        lng: parseFloat(formData.longitude)
      };
    }

    const map = new window.google.maps.Map(mapRef.current, {
      zoom: 8,
      center: initialCenter,
      mapTypeControl: true,
      streetViewControl: false,
      fullscreenControl: false,
    });

    // Add click listener to map
    map.addListener('click', (event) => {
      const lat = event.latLng.lat();
      const lng = event.latLng.lng();
      
      // Remove existing marker
      if (markerRef.current) {
        markerRef.current.setMap(null);
      }

      // Add new marker
      const marker = new window.google.maps.Marker({
        position: { lat, lng },
        map: map,
        draggable: true,
        title: 'Selected Location'
      });

      markerRef.current = marker;
      setSelectedLocation({ lat, lng });

      // Get address from coordinates (reverse geocoding)
      getAddressFromCoordinates(lat, lng);

      // Add drag listener to marker
      marker.addListener('dragend', (event) => {
        const newLat = event.latLng.lat();
        const newLng = event.latLng.lng();
        setSelectedLocation({ lat: newLat, lng: newLng });
        getAddressFromCoordinates(newLat, newLng);
      });
    });

    // If editing existing property, add marker
    if (formData.latitude && formData.longitude) {
      const existingMarker = new window.google.maps.Marker({
        position: initialCenter,
        map: map,
        draggable: true,
        title: 'Current Location'
      });

      markerRef.current = existingMarker;
      setSelectedLocation(initialCenter);

      existingMarker.addListener('dragend', (event) => {
        const newLat = event.latLng.lat();
        const newLng = event.latLng.lng();
        setSelectedLocation({ lat: newLat, lng: newLng });
        getAddressFromCoordinates(newLat, newLng);
      });
    }

    setMapInitialized(true);
  };

  const getAddressFromCoordinates = async (lat, lng) => {
    setIsLoadingAddress(true);
    
    try {
      const geocoder = new window.google.maps.Geocoder();
      
      geocoder.geocode(
        { location: { lat, lng } },
        (results, status) => {
          setIsLoadingAddress(false);
          
          if (status === 'OK' && results[0]) {
            const result = results[0];
            
            // Extract address components
            let streetNumber = '';
            let route = '';
            let locality = '';
            let administrativeAreaLevel1 = '';
            let postalCode = '';

            result.address_components.forEach(component => {
              const types = component.types;
              
              if (types.includes('street_number')) {
                streetNumber = component.long_name;
              }
              if (types.includes('route')) {
                route = component.long_name;
              }
              if (types.includes('locality')) {
                locality = component.long_name;
              }
              if (types.includes('administrative_area_level_1')) {
                administrativeAreaLevel1 = component.long_name;
              }
              if (types.includes('postal_code')) {
                postalCode = component.long_name;
              }
            });

            // Construct full address
            const fullAddress = `${streetNumber} ${route}`.trim();
            
            // Update form data with extracted information
            setFormData(prevData => ({
              ...prevData,
              latitude: lat.toString(),
              longitude: lng.toString(),
              address: fullAddress || result.formatted_address,
              city: locality || '',
              canton: administrativeAreaLevel1 || '',
              postal_code: postalCode || ''
            }));

          } else {
            console.error('Geocoding failed:', status);
          }
        }
      );
    } catch (error) {
      setIsLoadingAddress(false);
      console.error('Error getting address:', error);
    }
  };

  const handleMapPickerSave = () => {
    if (selectedLocation) {
      setShowMapPicker(false);
      // Data is already saved in formData via getAddressFromCoordinates
    } else {
      alert('Please select a location on the map first.');
    }
  };

  const handleMapPickerCancel = () => {
    setShowMapPicker(false);
    setSelectedLocation(null);
    if (markerRef.current) {
      markerRef.current.setMap(null);
      markerRef.current = null;
    }
  };

  useEffect(() => {
    if (property) {
      // Format price from database to a form input format
      let formattedPrice = property.price;
      if (typeof formattedPrice === 'string') {
        formattedPrice = formattedPrice.replace(/[^\d.]/g, '');
      }

      // Parse gallery_images if it's a string
      let galleryImagesArray = [];
      if (property.gallery_images) {
        try {
          if (typeof property.gallery_images === 'string') {
            galleryImagesArray = JSON.parse(property.gallery_images);
          } else if (Array.isArray(property.gallery_images)) {
            galleryImagesArray = property.gallery_images;
          }
        } catch (error) {
          console.error('Error parsing gallery_images:', error);
          galleryImagesArray = [];
        }
      }

      setFormData({
        title: property.title || '',
        description: property.description || '',
        price: formattedPrice || '',
        beds: property.beds?.toString() || '1',
        baths: property.baths?.toString() || '1',
        sqft: property.sqft?.toString() || '100',
        city: property.city || '',
        canton: property.canton || '',
        address: property.address || '',
        postal_code: property.postal_code || '',
        latitude: property.latitude?.toString() || '',
        longitude: property.longitude?.toString() || '',
        status: property.status || 'Available',
        property_type: property.property_type || 'Chalet',
        main_image: property.main_image || '',
        gallery_images: galleryImagesArray,
        user_id: property.user_id?.toString() || ''
      });
      
      // Set preview URL if there's an existing image
      if (property.main_image) {
        setPreviewUrl(property.main_image.startsWith('http') 
          ? property.main_image 
          : `http://localhost/real_estate_project/${property.main_image}`);
      } else {
        setPreviewUrl('');
      }
      
      // Set gallery preview URLs
      if (galleryImagesArray.length > 0) {
        const previews = galleryImagesArray.map(img => 
          img.startsWith('http') ? img : `http://localhost/real_estate_project/${img}`
        );
        setGalleryPreviews(previews);
      } else {
        setGalleryPreviews([]);
      }
    } else {
      // Reset form for new property
      setFormData({
        title: '',
        description: '',
        price: '',
        beds: '1',
        baths: '1',
        sqft: '100',
        city: '',
        canton: '',
        address: '',
        postal_code: '',
        latitude: '',
        longitude: '',
        status: 'Available',
        property_type: 'Chalet',
        main_image: '',
        gallery_images: [],
        user_id: users.length > 0 ? users[0].id.toString() : ''
      });
      setPreviewUrl('');
      setImageFile(null);
      setGalleryPreviews([]);
      setGalleryFiles([]);
    }
    // Clear errors when modal opens
    setFormErrors({});
    setUploadProgress(0);
    setIsUploading(false);
    setGalleryUploadProgress(0);
    setIsGalleryUploading(false);
    setShowMapPicker(false);
    setMapInitialized(false);
  }, [property, isOpen, users]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Handle file input change
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    handleFileSelected(file);
  };

  // Handle gallery file input change
  const handleGalleryFileChange = (e) => {
    const files = Array.from(e.target.files);
    handleGalleryFilesSelected(files);
  };

  // Handle drag events
  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  // Handle gallery drag events
  const handleGalleryDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setGalleryDragActive(true);
    } else if (e.type === 'dragleave') {
      setGalleryDragActive(false);
    }
  };

  // Handle drop event
  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileSelected(e.dataTransfer.files[0]);
    }
  };

  // Handle gallery drop event
  const handleGalleryDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setGalleryDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      handleGalleryFilesSelected(Array.from(e.dataTransfer.files));
    }
  };

  // Process selected file
  const handleFileSelected = (file) => {
    if (!file) return;
    
    // Validate file (type and size)
    const validImageTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
    const maxSize = 5 * 1024 * 1024; // 5MB
    
    if (!validImageTypes.includes(file.type)) {
      setFormErrors({
        ...formErrors,
        image: 'Please select a valid image file (JPEG, PNG, GIF, WEBP)'
      });
      return;
    }
    
    if (file.size > maxSize) {
      setFormErrors({
        ...formErrors,
        image: 'Image size should not exceed 5MB'
      });
      return;
    }
    
    // Clear any previous errors
    const newErrors = {...formErrors};
    delete newErrors.image;
    setFormErrors(newErrors);
    
    // Set the file and create a preview URL
    setImageFile(file);
    setPreviewUrl(URL.createObjectURL(file));
  };

  // Process selected gallery files
  const handleGalleryFilesSelected = (files) => {
    if (!files || files.length === 0) return;
    
    // Validate files (type and size)
    const validImageTypes = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];
    const maxSize = 5 * 1024 * 1024; // 5MB
    
    // Filter valid files
    const validFiles = [];
    const invalidFiles = [];
    
    files.forEach(file => {
      if (!validImageTypes.includes(file.type)) {
        invalidFiles.push(`${file.name} (invalid format)`);
      } else if (file.size > maxSize) {
        invalidFiles.push(`${file.name} (exceeds 5MB)`);
      } else {
        validFiles.push(file);
      }
    });
    
    if (invalidFiles.length > 0) {
      setFormErrors({
        ...formErrors,
        galleryImages: `Some files were not added: ${invalidFiles.join(', ')}`
      });
    } else {
      // Clear gallery errors
      const newErrors = {...formErrors};
      delete newErrors.galleryImages;
      setFormErrors(newErrors);
    }
    
    // Add new files to the existing files
    const newFiles = [...galleryFiles, ...validFiles];
    setGalleryFiles(newFiles);
    
    // Create preview URLs for all files
    const newPreviews = [
      ...galleryPreviews,
      ...validFiles.map(file => URL.createObjectURL(file))
    ];
    setGalleryPreviews(newPreviews);
  };

  // Triggers file input click
  const onButtonClick = () => {
    fileInputRef.current.click();
  };

  // Triggers gallery file input click
  const onGalleryButtonClick = () => {
    galleryInputRef.current.click();
  };

  // Upload the main image to the server
  const uploadImage = async () => {
    if (!imageFile) return null;
    
    setIsUploading(true);
    setUploadProgress(0);
    
    const formData = new FormData();
    formData.append('image', imageFile);
    
    try {
      const response = await axios.post(
        'http://localhost/real_estate_project/api/upload_image.php',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          onUploadProgress: (progressEvent) => {
            const percentCompleted = Math.round(
              (progressEvent.loaded * 100) / progressEvent.total
            );
            setUploadProgress(percentCompleted);
          },
          timeout: 30000 // 30 seconds timeout
        }
      );
      
      setIsUploading(false);
      
      if (response.data && response.data.success) {
        console.log('Upload successful:', response.data);
        return response.data.image_path;
      } else {
        setFormErrors({
          ...formErrors,
          image: response.data?.message || 'Failed to upload image'
        });
        console.error('Upload error:', response.data);
        return null;
      }
    } catch (error) {
      setIsUploading(false);
      
      // Better error handling
      let errorMessage = 'Error uploading image';
      
      if (error.response) {
        // The server responded with an error status
        errorMessage = `Server error: ${error.response.data?.message || error.response.statusText}`;
        console.error('Server response:', error.response.data);
      } else if (error.request) {
        // The request was made but no response was received
        errorMessage = 'No response received from server. Is the server running?';
      } else {
        // Something else caused the error
        errorMessage = error.message || 'Unknown error';
      }
      
      setFormErrors({
        ...formErrors,
        image: errorMessage
      });
      
      console.error('Upload exception:', error);
      return null;
    }
  };

  // Upload gallery images to the server
  const uploadGalleryImages = async () => {
    if (!galleryFiles || galleryFiles.length === 0) {
      // If no new files to upload, return the existing gallery paths
      return formData.gallery_images;
    }
    
    setIsGalleryUploading(true);
    setGalleryUploadProgress(0);
    
    const uploadPromises = [];
    const uploadResults = [];
    
    // Add existing gallery images to results if they already exist
    if (formData.gallery_images && formData.gallery_images.length > 0) {
      uploadResults.push(...formData.gallery_images);
    }
    
    // Create a promise for each file upload
    for (let i = 0; i < galleryFiles.length; i++) {
      const file = galleryFiles[i];
      const formPayload = new FormData();
      formPayload.append('image', file);
      
      const promise = axios.post(
        'http://localhost/real_estate_project/api/upload_image.php',
        formPayload,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      
      uploadPromises.push(promise);
    }
    
    try {
      const results = await Promise.allSettled(uploadPromises);
      
      // Process results
      results.forEach((result, index) => {
        if (result.status === 'fulfilled' && result.value.data && result.value.data.success) {
          uploadResults.push(result.value.data.image_path);
        } else {
          console.error(`Failed to upload gallery image ${index}:`, 
            result.status === 'rejected' ? result.reason : result.value?.data);
        }
      });
      
      setIsGalleryUploading(false);
      
      if (uploadResults.length > 0) {
        return uploadResults;
      } else {
        setFormErrors({
          ...formErrors,
          galleryImages: 'Failed to upload gallery images'
        });
        return [];
      }
    } catch (error) {
      setIsGalleryUploading(false);
      
      setFormErrors({
        ...formErrors,
        galleryImages: 'Error uploading gallery images: ' + (error.message || 'Unknown error')
      });
      
      console.error('Gallery upload exception:', error);
      return [];
    }
  };

  const validateForm = () => {
    const errors = {};
    
    if (!formData.title.trim()) errors.title = 'Title is required';
    if (!formData.city.trim()) errors.city = 'City is required';
    if (!formData.price.trim()) errors.price = 'Price is required';
    if (isNaN(parseFloat(formData.price))) errors.price = 'Price must be a valid number';
    if (!formData.beds.trim() || isNaN(parseInt(formData.beds))) errors.beds = 'Valid number of beds is required';
    if (!formData.baths.trim() || isNaN(parseInt(formData.baths))) errors.baths = 'Valid number of baths is required';
    if (!formData.sqft.trim() || isNaN(parseInt(formData.sqft))) errors.sqft = 'Valid area in sqft is required';
    if (!formData.user_id) errors.user_id = 'Owner/user selection is required';
    if (!formData.postal_code) errors.postal_code = 'Postal code is required';
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      // First, upload the image if there's a new one
      let imagePath = formData.main_image;
      
      if (imageFile) {
        imagePath = await uploadImage();
        if (!imagePath) return; // Stop if image upload failed
      }
      
      // Upload gallery images if there are any new ones
      const galleryPaths = await uploadGalleryImages();
      
      const processedData = {
        ...formData,
        price: parseFloat(formData.price),
        beds: parseInt(formData.beds),
        baths: parseInt(formData.baths),
        sqft: parseInt(formData.sqft),
        user_id: parseInt(formData.user_id),
        latitude: formData.latitude ? parseFloat(formData.latitude) : null,
        longitude: formData.longitude ? parseFloat(formData.longitude) : null,
        main_image: imagePath, // Use the uploaded image path
        gallery_images: galleryPaths // Use the uploaded gallery paths
      };
      
      if (property) {
        processedData.id = property.id;
      }
      
      onSave(processedData);
    }
  };

  const removeImage = () => {
    setImageFile(null);
    setPreviewUrl('');
    setFormData({
      ...formData,
      main_image: ''
    });
  };

  const removeGalleryImage = (index) => {
    // Create new arrays without the removed image
    const newPreviews = [...galleryPreviews];
    newPreviews.splice(index, 1);
    setGalleryPreviews(newPreviews);
    
    if (index < galleryFiles.length) {
      // It's a newly added file
      const newFiles = [...galleryFiles];
      newFiles.splice(index, 1);
      setGalleryFiles(newFiles);
    } else {
      // It's an existing image from the database
      const newGalleryImages = [...formData.gallery_images];
      // Calculate the index in the original array
      const originalIndex = index - galleryFiles.length;
      if (originalIndex >= 0 && originalIndex < newGalleryImages.length) {
        newGalleryImages.splice(originalIndex, 1);
        setFormData({
          ...formData,
          gallery_images: newGalleryImages
        });
      }
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>{property ? `Edit Property: ${property.title}` : 'Add New Property'}</h3>
          <button className="close-btn" onClick={onClose}>
            <FiX />
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="property-form">
          <div className="form-grid">
            <div className="form-group">
              <label htmlFor="title">Title*</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className={formErrors.title ? 'error' : ''}
              />
              {formErrors.title && <div className="error-text">{formErrors.title}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="property_type">Property Type</label>
              <select
                id="property_type"
                name="property_type"
                value={formData.property_type}
                onChange={handleChange}
              >
                <option value="Chalet">Chalet</option>
                <option value="Apartment">Apartment</option>
                <option value="Villa">Villa</option>
                <option value="Penthouse">Penthouse</option>
              </select>
            </div>
            
            <div className="form-group">
              <label htmlFor="price">Price (CHF)*</label>
              <input
                type="text"
                id="price"
                name="price"
                value={formData.price}
                onChange={handleChange}
                className={formErrors.price ? 'error' : ''}
                placeholder="e.g. 1500000"
              />
              {formErrors.price && <div className="error-text">{formErrors.price}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="status">Status</label>
              <select
                id="status"
                name="status"
                value={formData.status}
                onChange={handleChange}
              >
                <option value="Available">Available</option>
                <option value="Pending">Pending</option>
                <option value="Sold">Sold</option>
              </select>
            </div>
            
            <div className="form-group">
              <label htmlFor="beds">Bedrooms*</label>
              <input
                type="number"
                id="beds"
                name="beds"
                value={formData.beds}
                onChange={handleChange}
                className={formErrors.beds ? 'error' : ''}
                min="1"
              />
              {formErrors.beds && <div className="error-text">{formErrors.beds}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="baths">Bathrooms*</label>
              <input
                type="number"
                id="baths"
                name="baths"
                value={formData.baths}
                onChange={handleChange}
                className={formErrors.baths ? 'error' : ''}
                min="1"
              />
              {formErrors.baths && <div className="error-text">{formErrors.baths}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="sqft">Area (sqft)*</label>
              <input
                type="number"
                id="sqft"
                name="sqft"
                value={formData.sqft}
                onChange={handleChange}
                className={formErrors.sqft ? 'error' : ''}
                min="1"
              />
              {formErrors.sqft && <div className="error-text">{formErrors.sqft}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="postal_code">Postal Code*</label>
              <input
                type="text"
                id="postal_code"
                name="postal_code"
                value={formData.postal_code}
                onChange={handleChange}
                className={formErrors.postal_code ? 'error' : ''}
              />
              {formErrors.postal_code && <div className="error-text">{formErrors.postal_code}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="city">City*</label>
              <input
                type="text"
                id="city"
                name="city"
                value={formData.city}
                onChange={handleChange}
                className={formErrors.city ? 'error' : ''}
              />
              {formErrors.city && <div className="error-text">{formErrors.city}</div>}
            </div>
            
            <div className="form-group">
              <label htmlFor="canton">Canton</label>
              <input
                type="text"
                id="canton"
                name="canton"
                value={formData.canton}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input
                type="text"
                id="address"
                name="address"
                value={formData.address}
                onChange={handleChange}
              />
            </div>
          </div>

          {/* Location Picker Section */}
          <div className="form-section">
            <h4 className="section-title">
              <FiMapPin /> Location Details
            </h4>
            
            <div className="location-picker-container">
              <div className="location-info">
                <div className="location-coordinates">
                  <div className="coordinate-group">
                    <label htmlFor="latitude">Latitude</label>
                    <input
                      type="text"
                      id="latitude"
                      name="latitude"
                      value={formData.latitude}
                      onChange={handleChange}
                      placeholder="e.g. 46.8182"
                      readOnly={isLoadingAddress}
                    />
                  </div>
                  <div className="coordinate-group">
                    <label htmlFor="longitude">Longitude</label>
                    <input
                      type="text"
                      id="longitude"
                      name="longitude"
                      value={formData.longitude}
                      onChange={handleChange}
                      placeholder="e.g. 8.2275"
                      readOnly={isLoadingAddress}
                    />
                  </div>
                </div>
                
                <button 
                  type="button" 
                  className="map-picker-btn"
                  onClick={() => setShowMapPicker(true)}
                >
                  <FiMap /> {formData.latitude && formData.longitude ? 'Update Location' : 'Select Location on Map'}
                </button>
                
                {isLoadingAddress && (
                  <div className="loading-address">
                    <FiRefreshCw className="spin" /> Getting address...
                  </div>
                )}
              </div>
            </div>
          </div>
          
          {/* Main Image Upload Area */}
          <div className="form-group">
            <label>Main Image</label>
            <div 
              className={`image-upload-area ${dragActive ? 'drag-active' : ''}`}
              onDragEnter={handleDrag}
              onDragOver={handleDrag}
              onDragLeave={handleDrag}
              onDrop={handleDrop}
            >
              <input 
                type="file" 
                ref={fileInputRef}
                onChange={handleFileChange}
                accept="image/*"
                style={{ display: 'none' }}
              />
              
              {previewUrl ? (
                <div className="image-preview">
                  <img src={previewUrl} alt="Property preview" />
                  <button type="button" className="remove-image-btn" onClick={removeImage}>
                    <FiTrash /> Remove
                  </button>
                </div>
              ) : (
                <div className="upload-placeholder" onClick={onButtonClick}>
                  <FiUpload className="upload-icon" />
                  <p>Drag & drop a main image here, or click to select</p>
                  <span className="file-formats">Supported formats: JPEG, PNG, GIF, WEBP (max 5MB)</span>
                </div>
              )}
              
              {isUploading && (
                <div className="upload-progress">
                  <div className="progress-bar">
                    <div className="progress-fill" style={{ width: `${uploadProgress}%` }}></div>
                  </div>
                  <span>{uploadProgress}% Uploaded</span>
                </div>
              )}
              
              {formErrors.image && <div className="error-text">{formErrors.image}</div>}
            </div>
          </div>
          
          {/* Gallery Images Upload Area */}
          <div className="form-group">
            <label>Gallery Images</label>
            <div 
              className={`gallery-upload-area ${galleryDragActive ? 'drag-active' : ''}`}
              onDragEnter={handleGalleryDrag}
              onDragOver={handleGalleryDrag}
              onDragLeave={handleGalleryDrag}
              onDrop={handleGalleryDrop}
            >
              <input 
                type="file" 
                ref={galleryInputRef}
                onChange={handleGalleryFileChange}
                accept="image/*"
                multiple
                style={{ display: 'none' }}
              />
              
              <div className="gallery-upload-header">
                <div className="upload-placeholder" onClick={onGalleryButtonClick}>
                  <FiImage className="upload-icon" />
                  <p>Click to add gallery images or drop them here</p>
                  <span className="file-formats">Select multiple images if needed (max 10)</span>
                </div>
              </div>
              
              {isGalleryUploading && (
                <div className="upload-progress">
                  <div className="progress-bar">
                    <div className="progress-fill" style={{ width: `${galleryUploadProgress}%` }}></div>
                  </div>
                  <span>{galleryUploadProgress}% Uploaded</span>
                </div>
              )}
              
              {formErrors.galleryImages && 
                <div className="error-text">{formErrors.galleryImages}</div>}
              
              {galleryPreviews.length > 0 && (
                <div className="gallery-previews">
                  {galleryPreviews.map((preview, index) => (
                    <div key={`gallery-${index}`} className="gallery-preview-item">
                      <img src={preview} alt={`Gallery preview ${index + 1}`} />
                      <button 
                        type="button" 
                        className="remove-gallery-image-btn"
                        onClick={() => removeGalleryImage(index)}
                        title="Remove this image"
                      >
                        <FiX />
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="user_id">Owner/User*</label>
            <select
              id="user_id"
              name="user_id"
              value={formData.user_id}
              onChange={handleChange}
              className={formErrors.user_id ? 'error' : ''}
              disabled={loadingUsers}
            >
              <option value="">-- Select Owner --</option>
              {users.map(user => (
                <option key={user.id} value={user.id}>
                  {user.name} ({user.email})
                </option>
              ))}
            </select>
            {loadingUsers && <div className="loading-text">Loading users...</div>}
            {formErrors.user_id && <div className="error-text">{formErrors.user_id}</div>}
          </div>
          
          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="4"
            ></textarea>
          </div>
          
          <div className="form-actions">
            <button type="submit" className="save-btn" disabled={isUploading || isGalleryUploading}>
              <FiSave /> Save Property
            </button>
            <button type="button" className="cancel-btn" onClick={onClose}>
              <FiX /> Cancel
            </button>
          </div>
        </form>
      </div>

      {/* Map Picker Modal */}
      {showMapPicker && (
        <div className="map-picker-modal-overlay">
          <div className="map-picker-modal">
            <div className="map-picker-header">
              <h3>
                <FiMapPin /> Select Property Location
              </h3>
              <p>Click on the map to select the exact location of your property</p>
            </div>
            
            <div className="map-container">
              <div 
                ref={mapRef} 
                className="google-map"
                style={{ width: '100%', height: '400px' }}
              />
            </div>
            
            {selectedLocation && (
              <div className="selected-location-info">
                <FiMapPin className="location-icon" />
                <div className="location-details">
                  <strong>Selected Location:</strong><br />
                  Latitude: {selectedLocation.lat.toFixed(6)}<br />
                  Longitude: {selectedLocation.lng.toFixed(6)}
                  {isLoadingAddress && <div className="loading-small">Getting address...</div>}
                </div>
              </div>
            )}
            
            <div className="map-picker-actions">
              <button 
                type="button"
                className="map-save-btn"
                onClick={handleMapPickerSave}
                disabled={!selectedLocation}
              >
                <FiSave /> Use This Location
              </button>
              <button 
                type="button"
                className="map-cancel-btn"
                onClick={handleMapPickerCancel}
              >
                <FiX /> Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* CSS Styles for Map Picker */}
      <style jsx>{`
        .map-picker-modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.8);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 10000;
        }

        .map-picker-modal {
          background: white;
          border-radius: 12px;
          width: 90%;
          max-width: 800px;
          max-height: 90vh;
          overflow-y: auto;
          box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
        }

        .map-picker-header {
          padding: 20px;
          border-bottom: 1px solid #e5e7eb;
        }

        .map-picker-header h3 {
          margin: 0 0 8px 0;
          display: flex;
          align-items: center;
          gap: 8px;
          color: #1f2937;
        }

        .map-picker-header p {
          margin: 0;
          color: #6b7280;
          font-size: 14px;
        }

        .map-container {
          padding: 0;
        }

        .google-map {
          border: none;
          border-radius: 0;
        }

        .selected-location-info {
          padding: 15px 20px;
          background: #f3f4f6;
          border-top: 1px solid #e5e7eb;
          display: flex;
          align-items: flex-start;
          gap: 10px;
        }

        .location-icon {
          color: #059669;
          margin-top: 2px;
        }

        .location-details {
          font-size: 14px;
          line-height: 1.5;
        }

        .loading-small {
          color: #6b7280;
          font-style: italic;
          margin-top: 5px;
        }

        .map-picker-actions {
          padding: 20px;
          border-top: 1px solid #e5e7eb;
          display: flex;
          gap: 12px;
          justify-content: flex-end;
        }

        .map-save-btn, .map-cancel-btn {
          padding: 10px 20px;
          border: none;
          border-radius: 8px;
          font-weight: 500;
          cursor: pointer;
          display: flex;
          align-items: center;
          gap: 6px;
          transition: all 0.2s;
        }

        .map-save-btn {
          background: #059669;
          color: white;
        }

        .map-save-btn:hover:not(:disabled) {
          background: #047857;
        }

        .map-save-btn:disabled {
          background: #d1d5db;
          cursor: not-allowed;
        }

        .map-cancel-btn {
          background: #f3f4f6;
          color: #374151;
          border: 1px solid #d1d5db;
        }

        .map-cancel-btn:hover {
          background: #e5e7eb;
        }

        .form-section {
          margin: 20px 0;
          padding: 20px;
          background: #f9fafb;
          border-radius: 8px;
          border: 1px solid #e5e7eb;
        }

        .section-title {
          margin: 0 0 15px 0;
          display: flex;
          align-items: center;
          gap: 8px;
          color: #1f2937;
          font-size: 16px;
          font-weight: 600;
        }

        .location-picker-container {
          display: flex;
          flex-direction: column;
          gap: 15px;
        }

        .location-coordinates {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 15px;
        }

        .coordinate-group {
          display: flex;
          flex-direction: column;
          gap: 5px;
        }

        .coordinate-group label {
          font-size: 14px;
          font-weight: 500;
          color: #374151;
        }

        .coordinate-group input {
          padding: 8px 12px;
          border: 1px solid #d1d5db;
          border-radius: 6px;
          font-size: 14px;
        }

        .coordinate-group input:read-only {
          background: #f3f4f6;
          color: #6b7280;
        }

        .map-picker-btn {
          align-self: flex-start;
          padding: 10px 16px;
          background: #3b82f6;
          color: white;
          border: none;
          border-radius: 8px;
          font-weight: 500;
          cursor: pointer;
          display: flex;
          align-items: center;
          gap: 8px;
          transition: background 0.2s;
        }

        .map-picker-btn:hover {
          background: #2563eb;
        }

        .loading-address {
          display: flex;
          align-items: center;
          gap: 8px;
          color: #6b7280;
          font-size: 14px;
        }

        .spin {
          animation: spin 1s linear infinite;
        }

        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }

        @media (max-width: 768px) {
          .location-coordinates {
            grid-template-columns: 1fr;
          }
          
          .map-picker-modal {
            width: 95%;
            margin: 10px;
          }
          
          .google-map {
            height: 300px !important;
          }
        }
      `}</style>
    </div>
  );
}

function PropertyManagement() {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProperty, setCurrentProperty] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('newest');
  const [showSortDropdown, setShowSortDropdown] = useState(false);
  const [viewMode, setViewMode] = useState('active');
  const [users, setUsers] = useState([]);
  
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);

  // Update this to your correct API path
  const apiUrl = 'http://localhost/real_estate_project/api/property_manage.php';

  useEffect(() => {
    fetchProperties();
    fetchUsers();
  }, [viewMode]);

  const fetchProperties = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${apiUrl}?view=${viewMode}`, {
        headers: {
          'Accept': 'application/json'
        }
      });
      
      console.log('API response for view mode:', viewMode, response.data);
      
      if (Array.isArray(response.data)) {
        setProperties(response.data);
        setError(null);
      } else if (response.data.error) {
        setError(response.data.error);
      } else {
        setError('Unexpected response format');
      }
    } catch (error) {
      console.error('Error fetching properties:', error);
      setError('Error fetching properties. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost/real_estate_project/api/get_users.php', {
        headers: {
          'Accept': 'application/json'
        }
      });
      
      if (response.data.success) {
        setUsers(response.data.users || []);
      } else {
        console.error('Failed to fetch users:', response.data.message);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const getUserName = (userId) => {
    const user = users.find(u => u.id.toString() === userId?.toString());
    return user ? user.name : 'Unknown';
  };

  useEffect(() => {
    if (!properties.length) return;
    
    let result = [...properties];
    
    if (searchTerm) {
      const lowercasedSearch = searchTerm.toLowerCase();
      result = result.filter(property => 
        property.title?.toLowerCase().includes(lowercasedSearch) || 
        property.city?.toLowerCase().includes(lowercasedSearch) ||
        property.canton?.toLowerCase().includes(lowercasedSearch) ||
        property.price?.toString().includes(lowercasedSearch)
      );
    }
    
    switch(sortOrder) {
      case 'price-high':
        result.sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price.replace(/[^\d.]/g, '')) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price.replace(/[^\d.]/g, '')) : b.price;
          return priceB - priceA;
        });
        break;
      case 'price-low':
        result.sort((a, b) => {
          const priceA = typeof a.price === 'string' ? parseFloat(a.price.replace(/[^\d.]/g, '')) : a.price;
          const priceB = typeof b.price === 'string' ? parseFloat(b.price.replace(/[^\d.]/g, '')) : b.price;
          return priceA - priceB;
        });
        break;
      case 'name-asc':
        result.sort((a, b) => (a.title || '').localeCompare(b.title || ''));
        break;
      case 'name-desc':
        result.sort((a, b) => (b.title || '').localeCompare(a.title || ''));
        break;
      case 'status-available':
        result = result.filter(property => property.status === 'Available');
        break;
      case 'status-pending':
        result = result.filter(property => property.status === 'Pending');
        break;
      case 'status-sold':
        result = result.filter(property => property.status === 'Sold');
        break;
      default:
        result.sort((a, b) => b.id - a.id);
        break;
    }
    
    setFilteredProperties(result);
    setCurrentPage(1);
  }, [properties, searchTerm, sortOrder, viewMode]);

  const handleEditClick = (propertyId) => {
    const property = properties.find(property => property.id === propertyId);
    if (property) {
      setCurrentProperty(property);
      setIsModalOpen(true);
    }
  };

  const handleAddClick = () => {
    setCurrentProperty(null);
    setIsModalOpen(true);
  };

  const handleViewClick = (propertyId) => {
    window.location.href = `/chalet/${propertyId}`;
  };

  const handleMoveToTrash = async (propertyId) => {
    if (window.confirm('Are you sure you want to move this property to trash?')) {
      try {
        const response = await axios.post(`${apiUrl}?action=trash`, { id: propertyId });
        
        if (response.data.success) {
          fetchProperties();
          alert('Property moved to trash successfully');
        } else if (response.data.error) {
          alert(response.data.error);
        } else {
          alert('Failed to move property to trash');
        }
      } catch (error) {
        console.error('Error moving property to trash:', error);
        alert('Error moving property to trash: ' + error.message);
      }
    }
  };
  
  const handleRestoreProperty = async (propertyId) => {
    if (window.confirm('Are you sure you want to restore this property?')) {
      try {
        const response = await axios.post(`${apiUrl}?action=restore`, { id: propertyId });
        
        if (response.data.success) {
          fetchProperties();
          alert('Property restored successfully');
        } else if (response.data.error) {
          alert(response.data.error);
        } else {
          alert('Failed to restore property');
        }
      } catch (error) {
        console.error('Error restoring property:', error);
        alert('Error restoring property: ' + error.message);
      }
    }
  };
  
  const handleDeletePermanently = async (propertyId) => {
    if (window.confirm('Are you sure you want to permanently delete this property?')) {
      try {
        const response = await axios.post(`${apiUrl}?action=delete`, { id: propertyId });
        
        if (response.data.success) {
          fetchProperties();
          alert('Property permanently deleted');
        } else if (response.data.error) {
          alert(response.data.error);
        } else {
          alert('Failed to delete property');
        }
      } catch (error) {
        console.error('Error deleting property:', error);
        alert('Error deleting property: ' + error.message);
      }
    }
  };

  const handleSaveProperty = async (propertyData) => {
    try {
      let endpoint = '';
      
      if (propertyData.gallery_images && Array.isArray(propertyData.gallery_images)) {
        propertyData.gallery_images = JSON.stringify(propertyData.gallery_images);
      }
      
      if (propertyData.id) {
        endpoint = 'http://localhost/real_estate_project/api/update_property.php';
      } else {
        endpoint = 'http://localhost/real_estate_project/api/add_property.php';
      }
      
      console.log('Saving property data:', propertyData);
      console.log('Using endpoint:', endpoint);
      
      const response = await axios.post(endpoint, propertyData, {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });
      
      console.log('Server response:', response.data);
      
      if (response.data && response.data.success) {
        fetchProperties();
        setIsModalOpen(false);
        alert(propertyData.id ? 'Property updated successfully' : 'Property added successfully');
      } else {
        const errorMessage = response.data ? response.data.message || 'Failed to save property' : 'Failed to save property';
        alert(errorMessage);
        console.error('Error details:', response.data);
      }
    } catch (error) {
      console.error('Error saving property:', error);
      
      if (error.response) {
        console.error('Server response:', error.response.data);
        alert(`Error saving property: ${error.response.data?.message || error.message}`);
      } else if (error.request) {
        alert('Error: No response received from server. Please check your network connection.');
      } else {
        alert('Error saving property: ' + error.message);
      }
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (value) => {
    setSortOrder(value);
    setShowSortDropdown(false);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const toggleViewMode = () => {
    setViewMode(prevMode => prevMode === 'active' ? 'trash' : 'active');
    setSearchTerm('');
    setSortOrder('newest');
    setProperties([]);
    setFilteredProperties([]);
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredProperties.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredProperties.length / itemsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const prevPage = () => setCurrentPage(prev => Math.max(prev - 1, 1));
  const firstPage = () => setCurrentPage(1);
  const lastPage = () => setCurrentPage(totalPages);

  const renderStatusBadge = (status) => {
    let className = "status ";
    
    switch(status) {
      case 'Available':
        className += "active";
        break;
      case 'Pending':
        className += "pending";
        break;
      case 'Sold':
        className += "sold";
        break;
      default:
        className += "active";
    }
    
    return (
      <span className={className}>
        {status}
      </span>
    );
  };

  const formatPrice = (price) => {
    if (!price) return '';
    
    if (typeof price === 'string' && price.includes('CHF')) {
      return price;
    }
    
    return `CHF ${Number(price).toLocaleString()}`;
  };

  const getGalleryCount = (property) => {
    if (!property.gallery_images) return 0;
    
    try {
      if (typeof property.gallery_images === 'string') {
        const parsed = JSON.parse(property.gallery_images);
        return Array.isArray(parsed) ? parsed.length : 0;
      } else if (Array.isArray(property.gallery_images)) {
        return property.gallery_images.length;
      }
      return 0;
    } catch (error) {
      console.error('Error parsing gallery images:', error);
      return 0;
    }
  };

  const showEmptyTrashMessage = viewMode === 'trash' && (!properties.length || properties.length === 0);

  return (
    <section id="properties" className="dashboard-section">
      <div className="section-header">
        <div className="section-title-row">
          <h2 className="section-title">
            <FiHome className="section-icon" />
            {viewMode === 'active' ? 'Property Management' : 'Property Trash'}
          </h2>
          <div className="header-actions">
            <button 
              className={`toggle-view-btn ${viewMode === 'trash' ? 'active' : ''}`} 
              onClick={toggleViewMode}
              title={viewMode === 'active' ? 'View Trash' : 'View Active Properties'}
            >
              {viewMode === 'active' ? (
                <><FiTrash2 /> View Trash</>
              ) : (
                <><FiArchive /> View Active Properties</>
              )}
            </button>
            
            {viewMode === 'active' && (
              <button className="add-chalet-btn" onClick={handleAddClick}>
                <FiPlus /> Add New Property
              </button>
            )}
          </div>
        </div>
        <p className="section-subtitle">
          {viewMode === 'active' 
            ? 'Manage all listed properties' 
            : 'View and manage deleted properties'}
        </p>
        
        {!showEmptyTrashMessage && (
          <div className="search-card">
            <div className="search-input-wrapper">
              <FiSearch className="search-icon" />
              <input
                type="text"
                className="search-input"
                placeholder="Search by title, location or price..."
                value={searchTerm}
                onChange={handleSearchChange}
              />
              {searchTerm && (
                <button className="clear-search-btn" onClick={clearSearch}>
                  <FiX />
                </button>
              )}
            </div>
            
            <div className="sort-controls">
              <div className="sort-dropdown-container">
                <button 
                  className="sort-dropdown-btn" 
                  onClick={() => setShowSortDropdown(!showSortDropdown)}
                >
                  {sortOrder === 'newest' && 'Newest First'}
                  {sortOrder === 'price-high' && 'Price (High to Low)'}
                  {sortOrder === 'price-low' && 'Price (Low to High)'}
                  {sortOrder === 'name-asc' && 'Name (A-Z)'}
                  {sortOrder === 'name-desc' && 'Name (Z-A)'}
                  {sortOrder === 'status-available' && 'Status: Available'}
                  {sortOrder === 'status-pending' && 'Status: Pending'}
                  {sortOrder === 'status-sold' && 'Status: Sold'}
                  <FiChevronDown />
                </button>
                
                {showSortDropdown && (
                  <div className="sort-dropdown-menu">
                    <div className="dropdown-section">
                      <div className="dropdown-section-title">Sort</div>
                      <button 
                        className={`sort-option ${sortOrder === 'newest' ? 'active' : ''}`}
                        onClick={() => handleSortChange('newest')}
                      >
                        Newest First
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'price-high' ? 'active' : ''}`}
                        onClick={() => handleSortChange('price-high')}
                      >
                        Price (High to Low)
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'price-low' ? 'active' : ''}`}
                        onClick={() => handleSortChange('price-low')}
                      >
                        Price (Low to High)
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'name-asc' ? 'active' : ''}`}
                        onClick={() => handleSortChange('name-asc')}
                      >
                        Name (A-Z)
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'name-desc' ? 'active' : ''}`}
                        onClick={() => handleSortChange('name-desc')}
                      >
                        Name (Z-A)
                      </button>
                    </div>
                    
                    <div className="dropdown-divider"></div>
                    
                    <div className="dropdown-section">
                      <div className="dropdown-section-title">Filter by Status</div>
                      <button 
                        className={`sort-option ${sortOrder === 'newest' || sortOrder === 'price-high' || sortOrder === 'price-low' || sortOrder === 'name-asc' || sortOrder === 'name-desc' ? 'active' : ''}`}
                        onClick={() => handleSortChange('newest')}
                      >
                        All Statuses
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'status-available' ? 'active' : ''}`}
                        onClick={() => handleSortChange('status-available')}
                      >
                        Available Only
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'status-pending' ? 'active' : ''}`}
                        onClick={() => handleSortChange('status-pending')}
                      >
                        Pending Only
                      </button>
                      <button 
                        className={`sort-option ${sortOrder === 'status-sold' ? 'active' : ''}`}
                        onClick={() => handleSortChange('status-sold')}
                      >
                        Sold Only
                      </button>
                    </div>
                  </div>
                )}
              </div>
              
              <button className="refresh-btn" onClick={fetchProperties} title="Refresh Properties">
                <FiRefreshCw />
              </button>
            </div>
          </div>
        )}
      </div>
      
      <div className="section-content">
        {loading ? (
          <div className="loading-spinner">Loading properties...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : showEmptyTrashMessage ? (
          <div className="no-results">The trash is empty.</div>
        ) : filteredProperties.length === 0 ? (
          <div className="no-results">
            {viewMode === 'active' ? (
              searchTerm || sortOrder.startsWith('status-') ? 
                'No properties match your search criteria.' : 
                'No properties found.'
            ) : (
              'The trash is empty.'
            )}
          </div>
        ) : (
          <>
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Location</th>
                    <th>Price</th>
                    <th>Owner</th>
                    <th>Images</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentItems.map(property => (
                    <tr key={property.id}>
                      <td>{property.id}</td>
                      <td>{property.title}</td>
                      <td>{property.city}, {property.canton}</td>
                      <td>{formatPrice(property.price)}</td>
                      <td>{getUserName(property.user_id)}</td>
                      <td>
                        <div className="image-count">
                          <FiImage className="icon" />
                          <span className="count">{getGalleryCount(property) > 0 ? `+${getGalleryCount(property)}` : 'None'}</span>
                        </div>
                      </td>
                      <td>{renderStatusBadge(property.status)}</td>
                      <td>
                        <div className="action-buttons">
                          {viewMode === 'active' ? (
                            <>
                              <button 
                                className="view-btn"
                                onClick={() => handleViewClick(property.id)}
                                title="View Property"
                              >
                                <FiEye />
                              </button>
                              <button 
                                className="edit-btn"
                                onClick={() => handleEditClick(property.id)}
                                title="Edit Property"
                              >
                                <FiEdit />
                              </button>
                              <button 
                                className="trash-btn"
                                onClick={() => handleMoveToTrash(property.id)}
                                title="Move to Trash"
                              >
                                <FiTrash2 />
                              </button>
                            </>
                          ) : (
                            <>
                              <button 
                                className="restore-btn"
                                onClick={() => handleRestoreProperty(property.id)}
                                title="Restore Property"
                              >
                                <FiRotateCcw />
                              </button>
                              <button 
                                className="delete-permanently-btn"
                                onClick={() => handleDeletePermanently(property.id)}
                                title="Delete Permanently"
                              >
                                <FiTrash />
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            <div className="pagination-container">
              <div className="pagination-info">
                Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredProperties.length)} of {filteredProperties.length} properties
              </div>
              
              <div className="pagination-controls">
                <button 
                  className="pagination-btn first-page" 
                  onClick={firstPage}
                  disabled={currentPage === 1}
                  title="First Page"
                >
                  <FiChevronsLeft />
                </button>
                
                <button 
                  className="pagination-btn prev-page" 
                  onClick={prevPage}
                  disabled={currentPage === 1}
                  title="Previous Page"
                >
                  <FiChevronLeft />
                </button>
                
                <div className="pagination-pages">
                  {Array.from({ length: Math.min(5, totalPages) }).map((_, index) => {
                    let pageToShow;
                    
                    if (totalPages <= 5) {
                      pageToShow = index + 1;
                    } else {
                      const offset = Math.max(0, Math.min(totalPages - 5, currentPage - 3));
                      pageToShow = index + 1 + offset;
                    }
                    
                    return (
                      <button
                        key={pageToShow}
                        onClick={() => paginate(pageToShow)}
                        className={`pagination-btn page-number ${currentPage === pageToShow ? 'active' : ''}`}
                      >
                        {pageToShow}
                      </button>
                    );
                  })}
                </div>
                
                <button 
                  className="pagination-btn next-page" 
                  onClick={nextPage}
                  disabled={currentPage === totalPages}
                  title="Next Page"
                >
                  <FiChevronRight />
                </button>
                
                <button 
                  className="pagination-btn last-page" 
                  onClick={lastPage}
                  disabled={currentPage === totalPages}
                  title="Last Page"
                >
                  <FiChevronsRight />
                </button>
              </div>
            </div>
          </>
        )}
      </div>

      <PropertyModal
        isOpen={isModalOpen}
        onClose={closeModal}
        property={currentProperty}
        onSave={handleSaveProperty}
      />
    </section>
  );
}

export default PropertyManagement;
