// UserDashboard.jsx - With updated button class
import { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Property from '../components/Property';
import { 
  FiHome, 
  FiTrash2, 
  FiEye, 
  FiHeart, 
  FiPieChart, 
  FiCalendar, 
  FiUsers,
  FiBarChart2,
  FiEdit,
  FiPlus
} from 'react-icons/fi';
import '../styles/UserDashboard.css';

function UserDashboard() {
  const [chalets, setChalets] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [favoritesLoading, setFavoritesLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showChaletModal, setShowChaletModal] = useState(false);

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });

  // Function to fetch user chalets
  const fetchChalets = () => {
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_chalets.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Unauthorized');
        return res.json();
      })
      .then(data => {
        setChalets(data.chalets || data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching chalets:', error);
        setChalets([]);
        setLoading(false);
      });
  };

  // Function to fetch favorite chalets
  const fetchFavorites = () => {
    setFavoritesLoading(true);
    
    // Fetch favorites from server
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_favorites.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch favorites');
        return res.json();
      })
      .then(data => {
        console.log('Favorites data:', data);
        if (data.success && Array.isArray(data.favorites)) {
          setFavorites(data.favorites);
        } else {
          setFavorites([]);
        }
      })
      .catch((error) => {
        console.error('Error fetching favorites:', error);
        setFavorites([]);
      })
      .finally(() => {
        setFavoritesLoading(false);
      });
  };

  // Initial data loading
  useEffect(() => {
    fetchChalets();
    fetchFavorites();
    
    // Listen for events from Property components
    const handleStorageChange = () => {
      // Refresh favorites when changes are detected
      fetchFavorites();
    };
    
    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('favoritesChanged', handleStorageChange);
    
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('favoritesChanged', handleStorageChange);
    };
  }, []);

  // Handle remove favorite
  const handleRemoveFavorite = (chaletId) => {
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/remove_from_favorites.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chaletId }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          // Update local state to remove the favorite
          setFavorites(favorites.filter(chalet => chalet.id.toString() !== chaletId.toString()));
          
          // Also update localStorage for consistency
          try {
            const favoritesString = localStorage.getItem('favorites');
            if (favoritesString) {
              const storedFavorites = JSON.parse(favoritesString);
              const updatedFavorites = storedFavorites.filter(id => id.toString() !== chaletId.toString());
              localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
              
              // Dispatch storage event to notify Property components
              window.dispatchEvent(new Event('storage'));
              
              // Also dispatch a custom event with specific chalet info
              window.dispatchEvent(new CustomEvent('favoritesChanged', {
                detail: { chaletId: chaletId.toString(), isLiked: false }
              }));
            }
          } catch (e) {
            console.error('Error updating localStorage favorites:', e);
          }
          
          // Refresh favorites after removal
          fetchFavorites();
        }
      })
      .catch(error => {
        console.error('Error removing favorite:', error);
      });
  };

  const handleChangePassword = (e) => {
    e.preventDefault();
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/update_password.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || 'Password updated!');
        setShowModal(false);
        setOldPassword('');
        setNewPassword('');
      })
      .catch(() => {
        alert('Error updating password.');
      });
  };

  const handleChaletFormSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost/REAL_ESTATE_PROJECT/api/send_chalet_request.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then((data) => {
        alert('Request submitted! We will contact you soon.');
        setShowChaletModal(false);
        setForm({ name: '', email: '', phone: '', message: '' });
      })
      .catch(() => {
        alert('Error submitting request.');
      });
  };

  // Format price with commas
  const formatPrice = (price) => {
    if (!price) return 'N/A';
    return typeof price === 'number' 
      ? price.toLocaleString() 
      : Number(price) ? Number(price).toLocaleString() : price;
  };

  // Get username from localStorage
  const getUserName = () => {
    const userData = localStorage.getItem('user');
    if (!userData) return 'User';
    
    try {
      const user = JSON.parse(userData);
      if (!user.email) return 'User';
      
      const emailParts = user.email.split('@');
      if (emailParts.length > 1) {
        return emailParts[0];
      }
      
      return 'User';
    } catch (error) {
      return 'User';
    }
  };

  return (
    <div className="user-dashboard-container">
      <Navbar />
      <div className="user-dashboard-scroll">
        <aside className="user-sidebar">
          <div className="sidebar-header">
            <h2 className="sidebar-title"><span className="gradient-text">User</span> Dashboard</h2>
          </div>
          <nav className="sidebar-nav">
            <ul>
              <li><a href="#home" className="nav-link"><FiBarChart2 className="nav-icon" /> Dashboard</a></li>
              <li><a href="#my-chalets" className="nav-link"><FiHome className="nav-icon" /> My Chalets</a></li>
              <li><a href="#liked-chalets" className="nav-link"><FiHeart className="nav-icon" /> Liked Chalets</a></li>
            </ul>
          </nav>
        </aside>

        <main className="user-main">
          <header className="user-header">
            <div className="header-content">
              <div>
                <h1>Welcome, <span className="gradient-text">{getUserName()}</span></h1>
              </div>
              {/* Changed to use FiEdit icon instead of emoji */}
              <button className="profile-edit-btn" onClick={() => setShowModal(true)}>
                <FiEdit className="btn-icon" /> Edit Profile
              </button>
            </div>
          </header>

          <div className="user-content">
            <section id="home" className="dashboard-section">
              <h2><FiPieChart className="section-icon" /> Dashboard Overview</h2>
              
              {/* Enhanced Statistics */}
              <div className="stats-grid">
                <div className="stat-card">
                  <h3><FiHome className="stat-icon" /> Total Chalets</h3>
                  <p>{chalets.length}</p>
                </div>
                
                <div className="stat-card">
                  <h3><FiHeart className="stat-icon" /> Liked Chalets</h3>
                  <p>{favorites.length}</p>
                </div>
                
                <div className="stat-card">
                  <h3><FiCalendar className="stat-icon" /> Last Login</h3>
                  <p>{new Date().toLocaleDateString()}</p>
                </div>
              </div>
            </section>

            <section id="my-chalets" className="dashboard-section">
              <h2><FiHome className="section-icon" /> My Chalets</h2>
              <button className="add-chalet-btn" onClick={() => setShowChaletModal(true)}>
                <FiPlus className="btn-icon" /> Add New Chalet
              </button>

              {loading ? (
                <div className="loading-spinner">Loading your chalets...</div>
              ) : chalets.length === 0 ? (
                <div className="no-results">No chalets found.</div>
              ) : (
                <div className="chalet-list">
                  {chalets.map((chalet) => (
                    <Property 
                      key={chalet.id} 
                      property={chalet}
                      variant="dashboard"
                    />
                  ))}
                </div>
              )}
            </section>

            <section id="liked-chalets" className="dashboard-section">
              <h2><FiHeart className="section-icon" /> Liked Chalets</h2>
              <p>Chalets you've liked.</p>
              
              {favoritesLoading ? (
                <div className="loading-spinner">Loading your favorites...</div>
              ) : favorites.length === 0 ? (
                <div className="no-results">No liked chalets found. Like some chalets to see them here!</div>
              ) : (
                <div className="favorites-table-container">
                  <table className="favorites-table">
                    <thead>
                      <tr>
                        <th>Title</th>
                        <th>Location</th>
                        <th>Price (CHF)</th>
                        <th>Beds</th>
                        <th>Baths</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {favorites.map((chalet) => (
                        <tr key={chalet.id}>
                          <td>{chalet.title || 'Beautiful Chalet'}</td>
                          <td>{chalet.city || 'Unknown Location'}</td>
                          <td>{formatPrice(chalet.price)}</td>
                          <td>{chalet.bedrooms || 'N/A'}</td>
                          <td>{chalet.bathrooms || 'N/A'}</td>
                          <td className="action-buttons">
                            <a href={`/chalet/${chalet.id}`} className="view-btn" title="View Details">
                              <FiEye />
                            </a>
                            <button 
                              className="remove-btn" 
                              onClick={() => handleRemoveFavorite(chalet.id)}
                              title="Remove from Favorites"
                            >
                              <FiTrash2 />
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </section>
          </div>
        </main>
      </div>
      <Footer />

      {/* Change Password Modal */}
      {showModal && (
  <div className="modal-overlay-full">
    <div className="modal-content">
      <h3>Change Password</h3>
      <form onSubmit={handleChangePassword}>
        <input
          type="password"
          placeholder="Current Password"
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          required
        />
        <div className="modal-actions">
          <button type="submit" className="save-btn">Save</button>
          <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
        </div>
      </form>
    </div>
  </div>
)}


      {/* Add Chalet Request Modal */}
      {showChaletModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Request to Add New Chalet</h3>
            <form onSubmit={handleChaletFormSubmit}>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Your Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  placeholder="Your Email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  placeholder="Phone Number"
                  value={form.phone}
                  onChange={(e) => setForm({ ...form, phone: e.target.value })}
                />
              </div>
              <div className="form-group">
                <textarea
                  placeholder="Message (Details about the chalet you want to add)"
                  rows="5"
                  value={form.message}
                  onChange={(e) => setForm({ ...form, message: e.target.value })}
                  required
                />
              </div>
              <div className="modal-actions">
                <button type="submit" className="save-btn">Send Request</button>
                <button type="button" className="cancel-btn" onClick={() => setShowChaletModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
