import React, { useState, useEffect } from 'react';
import Property from '../components/Property';
import '../styles/AllPropertyListings.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';

const AllPropertiesPage = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        if (Array.isArray(data)) {
          setProperties(data);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <div className="all-properties-page">
      <Navbar />
      <div className="property-listings">
        <div className="listings-header">
          <h2 className="listings-title">Our Premier Swiss Chalets</h2>
          <p className="listings-subtitle">
            Discover our handpicked selection of luxury chalets in the most prestigious Swiss mountain resorts.
          </p>
        </div>

        <div className="properties-container">
          <div className="properties-slider">
            {properties.map(property => (
              <Property 
                key={property.id} 
                property={property}
              />
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>

  );
};

export default AllPropertiesPage;
