import React from 'react';
import { Link } from 'react-router-dom';
import { FiHome, FiDroplet, FiLayers, FiMapPin, FiClock } from 'react-icons/fi';
import '../styles/PropertyListings.css';

const Property = ({ property, variant = 'simple' }) => {
  // Ensure property has all required fields
  if (!property) return null;

  return (
    <div className={`property-card ${variant}`}>
      <Link to={`/chalet/${property.id}`} className="property-link">
        <div className="property-image">
          <img 
            src={property.image} 
            alt={property.title || 'Swiss Chalet'} 
            onError={(e) => {
              e.target.src = 'https://via.placeholder.com/600x400?text=No+Image';
            }}
          />
          <div className={`status-badge ${property.status?.toLowerCase()}`}>
            {property.status || 'Available'}
          </div>
        </div>
        
        <div className="property-details">
          <div className="property-price">{property.price}</div>
          
          <div className="property-specs">
            <div className="spec">
              <FiHome /> {property.beds} {variant === 'detailed' ? 'bedrooms' : 'beds'}
            </div>
            <div className="spec">
              <FiDroplet /> {property.baths} {variant === 'detailed' ? 'bathrooms' : 'baths'}
            </div>
            <div className="spec">
              <FiLayers /> {Number(property.sqft).toLocaleString()} sqft
            </div>
          </div>
          
          <div className="property-address">
            <FiMapPin /> {property.address}
          </div>
          
          {variant === 'detailed' && property.days_on_market !== undefined && (
            <div className="property-days-on-market">
              <FiClock /> {property.days_on_market} {property.days_on_market === 1 ? 'day' : 'days'} on market
            </div>
          )}
        </div>
      </Link>
    </div>
  );
};

export default Property;
