import React, { useRef } from 'react';
import styles from '../styles/PropertyListings.module.css';
import { Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import Property from './Property';

const PropertyListings = () => {
  const containerRef = useRef(null);

  const scroll = (direction) => {
    const { current } = containerRef;
    if (current) {
      const scrollAmount = 400;
      current.scrollBy({ 
        left: direction === 'left' ? -scrollAmount : scrollAmount, 
        behavior: 'smooth' 
      });
    }
  };

  const properties = [{
    id: 1,
    image: 'https://images.unsplash.com/photo-1564013799919-ab600027ffc6',
    price: 'CHF 2,300,000',
    beds: 4,
    baths: 3,
    sqft: 2800,
    status: 'Available',
    address: 'Zermatt, Switzerland',
    daysOnMarket: 5
  },
  {
    id: 2,
    image: 'https://images.unsplash.com/photo-1512917774080-9991f1c4c750',
    price: 'CHF 1,950,000',
    beds: 3,
    baths: 2,
    sqft: 2200,
    status: 'Available',
    address: 'St. Moritz, Switzerland',
    daysOnMarket: 2
  },
  {
    id: 3,
    image: 'https://images.unsplash.com/photo-1493809842364-78817add7ffb',
    price: 'CHF 2,700,000',
    beds: 5,
    baths: 4,
    sqft: 3200,
    status: 'Available',
    address: 'Verbier, Switzerland',
    daysOnMarket: 10
  },
  {
    id: 4,
    image: 'https://images.unsplash.com/photo-1502672260266-1c1ef2d93688',
    price: 'CHF 1,750,000',
    beds: 3,
    baths: 2,
    sqft: 2100,
    status: 'Available',
    address: 'Interlaken, Switzerland',
    daysOnMarket: 8
  },
  {
    id: 5,
    image: 'https://images.unsplash.com/photo-1522708323590-d24dbb6b0267',
    price: 'CHF 3,100,000',
    beds: 6,
    baths: 5,
    sqft: 3900,
    status: 'Available',
    address: 'Gstaad, Switzerland',
    daysOnMarket: 3
  }];

  return (
    <section className={styles.propertyListings}>
      <div className={styles.listingsHeader}>
        <h2 className={styles.listingsTitle}>Discover the Finest Swiss Chalets</h2>
        <p className={styles.listingsSubtitle}>
          We've handpicked the most beautiful and luxurious chalets across Switzerland, ready for you to explore.
        </p>
      </div>

      <div className={styles.propertiesContainer}>
        <div className={styles.propertiesSlider} ref={containerRef}>
          {properties.map(property => (
            <Property key={property.id} property={property} variant="default" />
          ))}
        </div>

        <div className={styles.navigationControls}>
          <button onClick={() => scroll('left')} className={styles.navButton} aria-label="Scroll left">
            <FiChevronLeft />
          </button>
          <button onClick={() => scroll('right')} className={styles.navButton} aria-label="Scroll right">
            <FiChevronRight />
          </button>
        </div>
      </div>

      <div className={styles.viewAllContainer}>
        <Link to="/properties" className={styles.viewAll}>
          View All Chalets
        </Link>
      </div>
    </section>
  );
};

export default PropertyListings;
