import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { FiMapPin, FiHome, FiDroplet } from 'react-icons/fi';
import '../styles/UserDashboard.css';

function UserDashboard() {
  const [chalets, setChalets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showChaletModal, setShowChaletModal] = useState(false);

  useEffect(() => {
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_chalets.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Unauthorized');
        return res.json();
      })
      .then(data => {
        setChalets(data.chalets || data);
      })
      .catch(() => {
        setChalets([]);
      });
  }, []);

  const handleChangePassword = (e) => {
    e.preventDefault();
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/update_password.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || 'Password updated!');
        setShowModal(false);
        setOldPassword('');
        setNewPassword('');
      })
      .catch(() => {
        alert('Error updating password.');
      });
  };

  return (
    <div className="user-dashboard-container">
      <Navbar />
      <div className="user-dashboard-scroll">
        <aside className="user-sidebar">
          <div className="sidebar-header">
            <h2 className="sidebar-title"><span className="gradient-text">User</span> Dashboard</h2>
          </div>
          <nav className="sidebar-nav">
            <ul>
              <li><a href="#home" className="nav-link">üè° Home</a></li>
              <li><a href="#my-chalets" className="nav-link">üè† My Chalets</a></li>
              <li><a href="#liked-chalets" className="nav-link">‚ù§Ô∏è Liked Chalets</a></li>
              <li><a href="#requests" className="nav-link">üì© Requests</a></li>
              <li><a href="#settings" className="nav-link">‚öôÔ∏è Settings</a></li>
            </ul>
          </nav>
        </aside>

        <main className="user-main">
          <header className="user-header">
            <div className="header-content">
              <div>
                <h1>Welcome, <span className="gradient-text">User</span></h1>
                <p>{new Date().toLocaleDateString()}</p>
              </div>
              <button className="edit-btn" onClick={() => setShowModal(true)}>‚úèÔ∏è Edit Profile</button>
            </div>
          </header>

          <div className="user-content">
            <section id="home" className="dashboard-section">
              <h2>üìä Dashboard Overview</h2>
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Total Chalets</h3>
                  <p>{chalets.length}</p>
                </div>
                <div className="stat-card">
                  <h3>Liked Chalets</h3>
                  <p>0</p>
                </div>
                <div className="stat-card">
                  <h3>Last Login</h3>
                  <p>{new Date().toLocaleString()}</p>
                </div>
              </div>
            </section>

            <section id="my-chalets" className="dashboard-section">
              <h2>üè† My Chalets</h2>
              <button className="add-chalet-btn" onClick={() => setShowChaletModal(true)}>
                ‚ûï Add New Chalet
              </button>

              {chalets.length === 0 ? (
                <div className="chalet-list">No chalets found.</div>
              ) : (
                <div className="chalet-list">
                  {chalets.map((chalet) => (
                    <Link to={`/chalet/${chalet.id}`} key={chalet.id} className="chalet-card-link">
                      <div className="chalet-card">
                        <div className="image-container">
                          <img src={chalet.main_image} alt={chalet.title} className="chalet-img" loading="lazy" />
                          <div className="property-badge">{chalet.status || 'Available'}</div>
                        </div>
                        <div className="chalet-details">
                          <h3>{chalet.title}</h3>
                          <div className="chalet-price">{Number(chalet.price).toLocaleString()} MAD</div>
                          <div className="chalet-address"><FiMapPin /> {chalet.city}</div>
                          <div className="chalet-meta">
                            <span><FiHome /> {chalet.bedrooms || 'N/A'} beds</span>
                            <span><FiDroplet /> {chalet.bathrooms || 'N/A'} baths</span>
                          </div>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </section>

            <section id="liked-chalets" className="dashboard-section">
              <h2>‚ù§Ô∏è Liked Chalets</h2>
              <p>Chalets you've liked.</p>
              <div className="chalet-list">No liked chalets.</div>
            </section>
          </div>
        </main>
      </div>
      <Footer />

      {/* Change Password Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Change Password</h3>
            <form onSubmit={handleChangePassword}>
              <input
                type="password"
                placeholder="Current Password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <div className="modal-actions">
                <button type="submit" className="save-btn">Save</button>
                <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Chalet Modal */}
      {showChaletModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Add New Chalet</h3>
            <form>
              <div className="form-group">
                <input type="text" placeholder="Chalet Title" required />
              </div>
              <div className="form-group">
                <input type="text" placeholder="City" required />
              </div>
              <div className="form-group">
                <input type="number" placeholder="Price (MAD)" required />
              </div>
              <div className="form-group">
                <input type="number" placeholder="Bedrooms" />
              </div>
              <div className="form-group">
                <input type="number" placeholder="Bathrooms" />
              </div>
              <div className="form-group">
                <textarea placeholder="Description" rows="4" required></textarea>
              </div>
              <div className="modal-actions">
                <button type="submit" className="save-btn">Add Chalet</button>
                <button type="button" className="cancel-btn" onClick={() => setShowChaletModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
