// Property.jsx - With functional like button that saves to database
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiDroplet, FiHeart } from 'react-icons/fi';
import '../styles/Property.css';

const Property = ({ property, variant = "default", onFavoriteChange }) => {
  // State to track if the property is liked
  const [isLiked, setIsLiked] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  // Make sure property exists, otherwise use an empty object
  const propertyData = property || {};
  
  const {
    id,
    title,
    price,
    main_image,
    city,
    bedrooms,
    bedroom_count,
    bed_count,
    beds,
    bathrooms,
    bathroom_count,
    bath_count,
    baths,
    status
  } = propertyData;

  // Choose the first available bedroom field
  const bedroomValue = bedrooms || bedroom_count || bed_count || beds;
  
  // Choose the first available bathroom field
  const bathroomValue = bathrooms || bathroom_count || bath_count || baths;

  // Format price with commas
  const formattedPrice = typeof price === 'number' 
    ? price.toLocaleString() 
    : Number(price) ? Number(price).toLocaleString() : 'Price on request';

  // Placeholder image if main_image is missing
  const imageSrc = main_image || "/placeholder.jpg";
  
  // Check if the property is in the user's favorites when the component mounts
  useEffect(() => {
    // Check if the user is logged in
    const user = localStorage.getItem('user');
    if (!user) return;
    
    // Get the user's favorites
    fetch('http://localhost/real_estate_project/api/check_is_favorite.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chaletId: id }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.success && data.isFavorite) {
          setIsLiked(true);
        }
      })
      .catch(error => {
        console.error('Error checking favorite status:', error);
      });
  }, [id]);
  
  // Handle like button click
  const handleLikeClick = (e) => {
    e.preventDefault(); // Prevent navigation to the chalet detail page
    
    // Check if the user is logged in
    const user = localStorage.getItem('user');
    if (!user) {
      // Redirect to login page or show login modal
      alert('Please log in to add this property to your favorites');
      return;
    }
    
    setIsLoading(true);
    
    // Toggle the favorite status
    if (!isLiked) {
      // Add to favorites
      fetch('http://localhost/real_estate_project/api/add_to_favorites.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ chaletId: id }),
      })
        .then(res => res.json())
        .then(data => {
          if (data.success) {
            setIsLiked(true);
            // Notify parent component if callback provided
            if (onFavoriteChange) {
              onFavoriteChange(id, true);
            }
          }
        })
        .catch(error => {
          console.error('Error adding to favorites:', error);
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else {
      // Remove from favorites
      fetch('http://localhost/real_estate_project/api/remove_from_favorites.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ chaletId: id }),
      })
        .then(res => res.json())
        .then(data => {
          if (data.success) {
            setIsLiked(false);
            // Notify parent component if callback provided
            if (onFavoriteChange) {
              onFavoriteChange(id, false);
            }
          }
        })
        .catch(error => {
          console.error('Error removing from favorites:', error);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };
  
  return (
    <Link to={`/chalet/${id}`} className={`property-card ${variant}`}>
      <div className="property-image-container">
        <img 
          src={imageSrc} 
          alt={title || "Swiss Chalet"} 
          className="property-image" 
          loading="lazy" 
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "/placeholder.jpg";
          }}
        />
        {status && <div className="property-badge">{status}</div>}
        
        {/* Like Button */}
        <button 
          className={`like-button ${isLiked ? 'liked' : ''} ${isLoading ? 'loading' : ''}`}
          onClick={handleLikeClick}
          aria-label={isLiked ? "Remove from favorites" : "Add to favorites"}
          disabled={isLoading}
        >
          <FiHeart />
        </button>
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{title || "Beautiful Swiss Chalet"}</h3>
        <div className="property-price">{formattedPrice} CHF</div>
        
        {city && (
          <div className="property-location">
            <FiMapPin className="property-icon" /> {city}
          </div>
        )}
        
        <div className="property-meta">
          {bedroomValue && (
            <span className="property-feature">
              <FiHome className="property-icon" /> {bedroomValue} beds
            </span>
          )}
          
          {bathroomValue && (
            <span className="property-feature">
              <FiDroplet className="property-icon" /> {bathroomValue} baths
            </span>
          )}
        </div>
      </div>
    </Link>
  );
};

export default Property;
