import React, { useState, useEffect } from 'react';
import '../styles/Hero.css';
import { FiHome, FiSearch, FiArrowRight, FiPlay, FiChevronLeft, FiChevronRight, FiMapPin } from 'react-icons/fi';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  
  const slides = [
    {
      id: 1,
      image: 'https://images.unsplash.com/photo-1564013799919-ab600027ffc6?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
      title: 'Luxury Villa with Mountain View',
      location: 'Zermatt, Switzerland',
      price: 'CHF 4,200,000'
    },
    {
      id: 2,
      image: 'https://images.unsplash.com/photo-1512917774080-9991f1c4c750?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
      title: 'Modern Lakefront Apartment',
      location: 'Geneva, Switzerland',
      price: 'CHF 2,800,000'
    },
    {
      id: 3,
      image: 'https://images.unsplash.com/photo-1493809842364-78817add7ffb?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
      title: 'Charming Alpine Chalet',
      location: 'St. Moritz, Switzerland',
      price: 'CHF 3,500,000'
    },
    {
      id: 4,
      image: 'https://images.unsplash.com/photo-1502672260266-1c1ef2d93688?ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=80',
      title: 'Contemporary City Penthouse',
      location: 'Zurich, Switzerland',
      price: 'CHF 5,100,000'
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? slides.length - 1 : prev - 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  // Auto-advance slides every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 5000);
    return () => clearInterval(interval);
  }, [currentSlide]);

  return (
    <section className="hero">
      {/* Decorative elements */}
      <div className="hero-decoration hero-circle"></div>
      <div className="hero-decoration hero-dots"></div>
      
      <div className="hero-container">
        <div className="hero-content">
          <h1 className="hero-title">
            Find Your Dream Home in Switzerland
          </h1>
          <p className="hero-subtitle">
            Discover the perfect property that matches your lifestyle. Our curated selection of homes across Switzerland offers something for everyone.
          </p>
          
          <div className="hero-buttons">
            <a href="#search" className="hero-btn hero-btn-primary">
              <FiSearch /> Browse Properties <FiArrowRight />
            </a>
            <a href="#video" className="hero-btn hero-btn-secondary">
              <FiPlay /> Watch Tour
            </a>
          </div>
          
          <div className="hero-stats">
            <div className="hero-stat">
              <span className="hero-stat-number">10K+</span>
              <span className="hero-stat-label">Properties</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">95%</span>
              <span className="hero-stat-label">Customer Satisfaction</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">15+</span>
              <span className="hero-stat-label">Years Experience</span>
            </div>
          </div>
        </div>
        
        <div className="hero-image-container">
          <div className="hero-image-slider">
            {slides.map((slide, index) => {
              let position = '';
              if (index === currentSlide) position = 'active';
              else if (index === (currentSlide + 1) % slides.length) position = 'next';
              else if (index === (currentSlide - 1 + slides.length) % slides.length) position = 'prev';
              
              return (
                <div key={slide.id} className={`hero-slide ${position}`}>
                  <img src={slide.image} alt={slide.title} />
                  <div className="hero-slide-content">
                    <h3 className="slide-title">{slide.title}</h3>
                    <div className="slide-location">
                      <FiMapPin /> {slide.location}
                    </div>
                    <div className="slide-price">{slide.price}</div>
                  </div>
                </div>
              );
            })}
            
            <div className="slide-nav">
              <button className="slide-nav-btn" onClick={prevSlide}>
                <FiChevronLeft size={24} />
              </button>
              <button className="slide-nav-btn" onClick={nextSlide}>
                <FiChevronRight size={24} />
              </button>
            </div>
            
            <div className="slide-dots">
              {slides.map((_, index) => (
                <div
                  key={index}
                  className={`slide-dot ${index === currentSlide ? 'active' : ''}`}
                  onClick={() => goToSlide(index)}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
