import React from 'react';
import { FiMapPin, FiHome, FiLayers, FiDroplet, FiClock, FiCalendar, FiTag } from 'react-icons/fi';

const Property = ({ 
  property, 
  variant = 'default' // 'default' or 'detailed'
}) => {
  // Map the new database structure to the expected props
  const mappedProperty = {
    image: property.main_image, // Changed from image to main_image
    status: property.status,
    type: property.property_type, // New field from enhanced DB
    price: `CHF ${Number(property.price).toLocaleString()}`, // Format price with comma separators
    address: property.address,
    // For detailed view, create a full address with city and canton
    fullAddress: `${property.address}, ${property.city}, ${property.canton} ${property.postal_code}`,
    beds: property.beds,
    baths: property.baths,
    sqft: property.sqft,
    daysOnMarket: property.days_on_market,
    yearBuilt: property.year_built,
    title: property.title
  };

  return (
    <div className={`property-card ${variant}`}>
      <div className="image-container">
        <img
          src={mappedProperty.image}
          alt={mappedProperty.address}
          className="property-image"
          loading="lazy"
        />
        <div className="property-badge">{mappedProperty.status}</div>
      </div>
      
      <div className="property-details">
        {variant === 'detailed' && (
          <>
            <h3>{mappedProperty.title || mappedProperty.type}</h3>
            <div className="property-price">
              {mappedProperty.price}
              {property.pricePerNight && <span>{property.pricePerNight}</span>}
            </div>
            <div className="divider"></div>
          </>
        )}
        
        {variant === 'default' && (
          <>
            <span className="days-on-market">
              <FiClock /> {mappedProperty.daysOnMarket} days listed
            </span>
            <div className="property-price">{mappedProperty.price}</div>
          </>
        )}
        
        <div className="property-address">
          <FiMapPin /> {variant === 'detailed' ? mappedProperty.fullAddress : mappedProperty.address}
        </div>
        
        <div className="property-meta">
          <span><FiHome /> {mappedProperty.beds} {variant === 'detailed' ? 'beds' : 'bedrooms'}</span>
          <span><FiDroplet /> {mappedProperty.baths} baths</span>
          <span><FiLayers /> {mappedProperty.sqft.toLocaleString()} sqft</span>
          {mappedProperty.yearBuilt && variant === 'detailed' && (
            <span><FiCalendar /> Built {mappedProperty.yearBuilt}</span>
          )}
          {mappedProperty.type && variant === 'default' && (
            <span><FiTag /> {mappedProperty.type}</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default Property;
