import React, { useState, useEffect } from 'react';
import { FiEdit, FiTrash2, FiPlus } from 'react-icons/fi';
import UserModal from './UserModal';

function UserManagement() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  // Fetch users from the API
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_users.php', {
        credentials: 'include',
      });
      
      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }
      
      const data = await response.json();
      
      if (data.success) {
        setUsers(data.users || []);
      } else {
        setError(data.message || 'Failed to fetch users');
      }
    } catch (error) {
      console.error('Error fetching users:', error);
      setError('Error fetching users. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleEditUser = (userId) => {
    const user = users.find(user => user.id === userId);
    if (user) {
      setCurrentUser(user);
      setIsModalOpen(true);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/delete_user.php', {
          method: 'POST',
          credentials: 'include',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userId }),
        });
        
        const data = await response.json();
        
        if (data.success) {
          // Remove user from state
          setUsers(users.filter(user => user.id !== userId));
          alert('User deleted successfully');
        } else {
          alert(data.message || 'Failed to delete user');
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Error deleting user. Please try again later.');
      }
    }
  };

  const handleAddUser = () => {
    setCurrentUser(null); // Reset current user for new user form
    setIsModalOpen(true);
  };

  const handleSaveUser = async (userData) => {
    try {
      const response = await fetch('http://localhost/REAL_ESTATE_PROJECT/api/save_user.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
      });
      
      const data = await response.json();
      
      if (data.success) {
        if (userData.id) {
          // Update existing user in state
          setUsers(users.map(user => 
            user.id === userData.id ? data.user : user
          ));
        } else {
          // Add new user to state
          setUsers([data.user, ...users]);
        }
        
        setIsModalOpen(false);
        alert(data.message);
      } else {
        alert(data.message || 'Failed to save user');
      }
    } catch (error) {
      console.error('Error saving user:', error);
      alert('Error saving user. Please try again later.');
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <section id="users" className="dashboard-section">
      <div className="section-header">
        <h2 className="section-title">User Management</h2>
        <p className="section-subtitle">Manage all registered users</p>
      </div>
      
      <button className="add-chalet-btn" onClick={handleAddUser}>
        <FiPlus /> Add New User
      </button>
      
      <div className="section-content">
        {loading ? (
          <div className="loading-spinner">Loading users...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : users.length === 0 ? (
          <div className="no-results">No users found.</div>
        ) : (
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id}>
                    <td>{user.id}</td>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.is_admin === 1 ? 'Admin' : 'User'}</td>
                    <td>
                      <span className="status active">Active</span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="edit-btn"
                          onClick={() => handleEditUser(user.id)}
                          title="Edit User"
                        >
                          <FiEdit />
                        </button>
                        <button 
                          className="delete-btn"
                          onClick={() => handleDeleteUser(user.id)}
                          title="Delete User"
                        >
                          <FiTrash2 />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* User Modal for Add/Edit */}
      <UserModal
        isOpen={isModalOpen}
        onClose={closeModal}
        user={currentUser}
        onSave={handleSaveUser}
      />
    </section>
  );
}

export default UserManagement;
