import React, { useRef, useState, useEffect } from 'react';
import '../styles/PropertyListings.css';
import { Link } from 'react-router-dom';
import { FiChevronLeft, FiChevronRight } from 'react-icons/fi';
import Property from '../components/Property';

const PropertyListings = () => {
  const containerRef = useRef(null);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        if (Array.isArray(data)) {
          // Show only the last 4 added properties
          // Assuming the API returns properties sorted by date (newest last)
          const lastFourProperties = data.slice(-4);
          setProperties(lastFourProperties);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  const scroll = (direction) => {
    const { current } = containerRef;
    if (current) {
      const scrollAmount = 400;
      current.scrollBy({ 
        left: direction === 'left' ? -scrollAmount : scrollAmount, 
        behavior: 'smooth' 
      });
    }
  };

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <section className="property-listings">
      <div className="listings-header">
        <h2 className="listings-title">Discover the Finest Swiss Chalets</h2>
        <p className="listings-subtitle">
          Browse our latest additions - luxurious chalets recently added to our collection.
        </p>
      </div>

      <div className="properties-container">
        <div className="properties-slider" ref={containerRef}>
          {properties.map(property => (
            <Property key={property.id} property={property} variant="default" />
          ))}
        </div>

        <div className="navigation-controls">
          <button onClick={() => scroll('left')} className="nv-btn" aria-label="Scroll left">
            <FiChevronLeft />
          </button>
          <button onClick={() => scroll('right')} className="nv-btn" aria-label="Scroll right">
            <FiChevronRight />
          </button>
        </div>
      </div>

      <div className="view-all-container">
        <button className="view-all">
          <Link to="/properties" className="view-all">
            View All Chalets
          </Link> 
        </button>
      </div>
    </section>
  );
};

export default PropertyListings;
