import React, { useState, useEffect } from 'react';
import Property from '../components/Property';
import axios from 'axios'; // Make sure axios is installed

const AllPropertiesPage = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        // Make sure this URL points to your PHP API file
        const response = await axios.get('/api/get_chalets.php');
        
        if (response.data && Array.isArray(response.data)) {
          setProperties(response.data);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <div className="all-properties-page">
      <h1>All Properties</h1>
      <div className="properties-grid">
        {properties.map(property => (
          <Property 
            key={property.id} 
            property={property} 
            variant="default" 
          />
        ))}
      </div>
    </div>
  );
};

export default AllPropertiesPage;
