import React from 'react';
import { FiMapPin, FiHome, FiLayers, FiDroplet, FiClock, FiCalendar, FiTag } from 'react-icons/fi';

const Property = ({ 
  property, 
  variant = 'default' // 'default' or 'detailed'
}) => {
  return (
    <div className={`property-card ${variant}`}>
      <div className="image-container">
        <img
          src={property.main_image} // Changed from image to main_image
          alt={property.title || property.address}
          className="property-image"
          loading="lazy"
        />
        <div className="property-badge">{property.status}</div>
      </div>
      
      <div className="property-details">
        {variant === 'detailed' && (
          <>
            <h3>{property.title || property.property_type}</h3>
            <div className="property-price">
              {`CHF ${Number(property.price).toLocaleString()}`}
              {property.pricePerNight && <span>{property.pricePerNight}</span>}
            </div>
            <div className="divider"></div>
          </>
        )}
        
        {variant === 'default' && (
          <>
            <span className="days-on-market">
              <FiClock /> {property.days_on_market} days listed
            </span>
            <div className="property-price">{`CHF ${Number(property.price).toLocaleString()}`}</div>
          </>
        )}
        
        <div className="property-address">
          <FiMapPin /> {variant === 'detailed' 
            ? `${property.address}, ${property.city}, ${property.canton} ${property.postal_code}`
            : property.address}
        </div>
        
        <div className="property-meta">
          <span><FiHome /> {property.beds} {variant === 'detailed' ? 'beds' : 'bedrooms'}</span>
          <span><FiDroplet /> {property.baths} baths</span>
          <span><FiLayers /> {property.sqft.toLocaleString()} sqft</span>
          {property.year_built && variant === 'detailed' && (
            <span><FiCalendar /> Built {property.year_built}</span>
          )}
          {property.property_type && variant === 'default' && (
            <span><FiTag /> {property.property_type}</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default Property;
