// Property.jsx - With hybrid storage for favorites
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiDroplet, FiHeart } from 'react-icons/fi';
import '../styles/Property.css';

const Property = ({ property, variant = "default", onFavoriteChange }) => {
  // State to track if the property is liked
  const [isLiked, setIsLiked] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  // Make sure property exists, otherwise use an empty object
  const propertyData = property || {};
  
  const {
    id,
    title,
    price,
    main_image,
    city,
    bedrooms,
    bathrooms,
    status
  } = propertyData;

  // Format price with commas
  const formattedPrice = typeof price === 'number' 
    ? price.toLocaleString() 
    : Number(price) ? Number(price).toLocaleString() : 'Price on request';

  // Placeholder image if main_image is missing
  const imageSrc = main_image || "/placeholder.jpg";

  // Get user's favorites from localStorage
  const getFavoritesFromStorage = () => {
    try {
      const favorites = localStorage.getItem('favorites');
      return favorites ? JSON.parse(favorites) : [];
    } catch (e) {
      console.error('Error parsing favorites from localStorage:', e);
      return [];
    }
  };

  // Save favorites to localStorage
  const saveFavoritesToStorage = (favorites) => {
    try {
      localStorage.setItem('favorites', JSON.stringify(favorites));
    } catch (e) {
      console.error('Error saving favorites to localStorage:', e);
    }
  };

  // Check if the property is in favorites when the component mounts
  useEffect(() => {
    if (!id) return;
    
    const favorites = getFavoritesFromStorage();
    if (Array.isArray(favorites) && favorites.includes(id.toString())) {
      setIsLiked(true);
    }
    
    // Also try to check with the server
    fetch('http://localhost/real_estate_project/api/test_api.php', {
      credentials: 'include'
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          console.log('Server connection confirmed');
        }
      })
      .catch(err => {
        console.log('Server connection failed, using localStorage only');
      });
  }, [id]);
  
  // Handle like button click
  const handleLikeClick = (e) => {
    e.preventDefault(); // Prevent navigation to the chalet detail page
    e.stopPropagation(); // Stop event propagation
    
    if (!id) return;
    setIsLoading(true);
    
    // Toggle like state
    const newLikedState = !isLiked;
    setIsLiked(newLikedState);
    
    console.log('Like button clicked for chalet ID:', id);
    
    // Update localStorage for immediate feedback
    let favorites = getFavoritesFromStorage();
    
    if (newLikedState) {
      // Add to favorites if not already there
      if (!favorites.includes(id.toString())) {
        favorites.push(id.toString());
      }
    } else {
      // Remove from favorites
      favorites = favorites.filter(favId => favId !== id.toString());
    }
    
    // Save updated favorites to localStorage
    saveFavoritesToStorage(favorites);
    
    // Also try to update server
    const apiUrl = newLikedState 
      ? 'http://localhost/real_estate_project/api/add_to_favorites.php' 
      : 'http://localhost/real_estate_project/api/remove_from_favorites.php';
    
    fetch(apiUrl, {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chaletId: id }),
    })
      .then(res => res.json())
      .then(data => {
        console.log(newLikedState ? 'Add to favorites response:' : 'Remove from favorites response:', data);
        if (!data.success) {
          console.warn('Server update failed, but localStorage was updated');
        }
      })
      .catch(error => {
        console.warn('Server update failed, but localStorage was updated:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
    
    // Notify parent component if callback provided
    if (onFavoriteChange) {
      onFavoriteChange(id, newLikedState);
    }
  };
  
  return (
    <Link to={`/chalet/${id}`} className={`property-card ${variant}`}>
      <div className="property-image-container">
        <img 
          src={imageSrc} 
          alt={title || "Swiss Chalet"} 
          className="property-image" 
          loading="lazy" 
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "/placeholder.jpg";
          }}
        />
        {status && <div className="property-badge">{status}</div>}
        
        {/* Like Button */}
        <div 
          className={`like-button ${isLiked ? 'liked' : ''} ${isLoading ? 'loading' : ''}`}
          onClick={handleLikeClick}
          aria-label={isLiked ? "Remove from favorites" : "Add to favorites"}
        >
          <FiHeart />
        </div>
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{title || "Beautiful Swiss Chalet"}</h3>
        <div className="property-price">{formattedPrice} CHF</div>
        
        {city && (
          <div className="property-location">
            <FiMapPin className="property-icon" /> {city}
          </div>
        )}
        
        <div className="property-meta">
          {bedrooms && (
            <span className="property-feature">
              <FiHome className="property-icon" /> {bedrooms} beds
            </span>
          )}
          
          {bathrooms && (
            <span className="property-feature">
              <FiDroplet className="property-icon" /> {bathrooms} baths
            </span>
          )}
        </div>
      </div>
    </Link>
  );
};

export default Property;
