import React, { useState, useEffect } from 'react';
import '../styles/Hero.css';
import { FiSearch, FiArrowRight } from 'react-icons/fi';

const Hero = () => {
  const images = [
    "/assets/Swiss_chalet_1.jpg",
    "/assets/Swiss_chalet_2.jpg",
    "/assets/Swiss_chalet_3.jpg",
    "/assets/Swiss_chalet_4.jpg",
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [transitionEnabled, setTransitionEnabled] = useState(true);

  // Function to move to next slide with smooth transition
  const nextSlide = () => {
    setTransitionEnabled(true);
    setCurrentImageIndex((prevIndex) => 
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Function to move to previous slide with smooth transition
  const prevSlide = () => {
    setTransitionEnabled(true);
    setCurrentImageIndex((prevIndex) => 
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  // Function to move to a specific slide
  const goToSlide = (index) => {
    setTransitionEnabled(true);
    setCurrentImageIndex(index);
  };

  // Automatic slide change with smoother timing
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // Reduced to 3 seconds for better user experience

    return () => clearInterval(interval);
  }, [currentImageIndex]); // Added dependency for better cleanup

  return (
    <section className="hero">
      <div className="hero-decoration hero-circle"></div>
      <div className="hero-decoration hero-dots"></div>
      
      <div className="hero-container">
        <div className="hero-content">
          <h1 className="hero-title">Find Your Dream Home in Switzerland</h1>
          <p className="hero-subtitle">
            Discover the perfect property that matches your lifestyle. Our curated selection of homes across Switzerland offers something for everyone, from cozy apartments to luxurious villas.
          </p>
          
          <div className="hero-buttons">
            <a href="#search" className="hero-btn hero-btn-primary">
              <FiSearch /> Browse Properties <FiArrowRight />
            </a>
          </div>
          
          <div className="hero-stats">
            <div className="hero-stat">
              <span className="hero-stat-number">10K+</span>
              <span className="hero-stat-label">Properties</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">95%</span>
              <span className="hero-stat-label">Customer Satisfaction</span>
            </div>
            <div className="hero-stat">
              <span className="hero-stat-number">15+</span>
              <span className="hero-stat-label">Years Experience</span>
            </div>
          </div>
        </div>
        
        <div className="hero-image-container">
          <div className="hero-carousel">
            <div 
              className="hero-carousel-track"
              style={{
                transform: `translateX(-${currentImageIndex * 100}%)`,
                transition: transitionEnabled ? 'transform 0.5s ease-in-out' : 'none'
              }}
            >
              {images.map((image, index) => (
                <div 
                  key={index}
                  className="hero-carousel-slide"
                >
                  <img 
                    src={image} 
                    alt={`Luxury Swiss home ${index + 1}`} 
                    className="hero-image"
                  />
                  <div className="image-overlay">
                    <div className="overlay-content">
                      <h3>Swiss Chalet {index + 1}</h3>
                      <p>Discover this beautiful property in the Swiss Alps</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Navigation arrows */}
            <button 
              className="carousel-arrow carousel-arrow-left"
              onClick={prevSlide}
              aria-label="Previous slide"
            >
              &lt;
            </button>
            <button 
              className="carousel-arrow carousel-arrow-right"
              onClick={nextSlide}
              aria-label="Next slide"
            >
              &gt;
            </button>

            {/* Dots navigation */}
            <div className="carousel-dots">
              {images.map((_, index) => (
                <button 
                  key={index} 
                  className={`carousel-dot ${index === currentImageIndex ? 'active' : ''}`}
                  onClick={() => goToSlide(index)}
                  aria-label={`Go to slide ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
