// Property.jsx - With like button
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiDroplet, FiHeart } from 'react-icons/fi';
import '../styles/Property.css';

const Property = ({ property, variant = "default" }) => {
  // State to track if the property is liked
  const [isLiked, setIsLiked] = useState(false);
  
  // Make sure property exists, otherwise use an empty object
  const propertyData = property || {};
  
  // Try alternative field names that might be in your database
  const {
    id,
    title,
    price,
    main_image,
    city,
    // Try several possible field names for bedrooms
    bedrooms,
    bedroom_count,
    bed_count,
    beds,
    // Try several possible field names for bathrooms
    bathrooms,
    bathroom_count,
    bath_count,
    baths,
    status
  } = propertyData;

  // Choose the first available bedroom field
  const bedroomValue = bedrooms || bedroom_count || bed_count || beds;
  
  // Choose the first available bathroom field
  const bathroomValue = bathrooms || bathroom_count || bath_count || baths;

  // Format price with commas
  const formattedPrice = typeof price === 'number' 
    ? price.toLocaleString() 
    : Number(price) ? Number(price).toLocaleString() : 'Price on request';

  // Placeholder image if main_image is missing
  const imageSrc = main_image || "/placeholder.jpg";
  
  // Handle like button click
  const handleLikeClick = (e) => {
    e.preventDefault(); // Prevent navigation to the chalet detail page
    setIsLiked(!isLiked);
    
    // You can add API call here to save the liked state to your backend
    // For example:
    // if (!isLiked) {
    //   fetch('http://localhost/real_estate_project/api/add_to_favorites.php', {
    //     method: 'POST',
    //     headers: { 'Content-Type': 'application/json' },
    //     body: JSON.stringify({ chaletId: id }),
    //   });
    // } else {
    //   fetch('http://localhost/real_estate_project/api/remove_from_favorites.php', {
    //     method: 'POST',
    //     headers: { 'Content-Type': 'application/json' },
    //     body: JSON.stringify({ chaletId: id }),
    //   });
    // }
  };
  
  return (
    <Link to={`/chalet/${id}`} className={`property-card ${variant}`}>
      <div className="property-image-container">
        <img 
          src={imageSrc} 
          alt={title || "Swiss Chalet"} 
          className="property-image" 
          loading="lazy" 
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "/placeholder.jpg";
          }}
        />
        {status && <div className="property-badge">{status}</div>}
        
        {/* Like Button */}
        <button 
          className={`like-button ${isLiked ? 'liked' : ''}`}
          onClick={handleLikeClick}
          aria-label={isLiked ? "Remove from favorites" : "Add to favorites"}
        >
          <FiHeart />
        </button>
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{title || "Beautiful Swiss Chalet"}</h3>
        <div className="property-price">{formattedPrice} CHF</div>
        
        {city && (
          <div className="property-location">
            <FiMapPin className="property-icon" /> {city}
          </div>
        )}
        
        <div className="property-meta">
          {bedroomValue && (
            <span className="property-feature">
              <FiHome className="property-icon" /> {bedroomValue} beds
            </span>
          )}
          
          {bathroomValue && (
            <span className="property-feature">
              <FiDroplet className="property-icon" /> {bathroomValue} baths
            </span>
          )}
        </div>
      </div>
    </Link>
  );
};

export default Property;
