import React, { useState, useEffect } from 'react';
import '../styles/PropertyListings.css';
import { Link } from 'react-router-dom';
import { FiHome, FiHeart } from 'react-icons/fi';
import Property from './Property';

const PropertyListings = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        
        if (Array.isArray(data)) {
          // Enhanced field mapping with comprehensive fallbacks
          const optimizedData = data.map(property => {
            // Extract likes count with multiple fallback options
            let likesCount = 0;
            
            // Try different possible field names for likes
            const possibleLikesFields = [
              'likes_count', 
              'total_likes', 
              'favorites_count', 
              'favourite_count',
              'like_count',
              'heart_count'
            ];
            
            for (const field of possibleLikesFields) {
              if (property[field] !== undefined && property[field] !== null) {
                likesCount = parseInt(property[field]) || 0;
                break;
              }
            }
            
            return {
              id: property.id,
              title: property.title || 'Beautiful Swiss Chalet',
              price: property.price,
              main_image: property.main_image,
              city: property.city,
              bedrooms: property.bedrooms || property.bedroom_count || property.bed_count || property.beds,
              bathrooms: property.bathrooms || property.bathroom_count || property.bath_count || property.baths,
              status: property.status || 'Available',
              description: property.description,
              likes_count: likesCount
            };
          });
          
          // Debug: Log the data to see what we're getting
          console.log('All properties with likes:', optimizedData.map(p => ({ id: p.id, title: p.title, likes: p.likes_count })));
          
          // Filter out properties with invalid IDs and sort by likes count (descending)
          const validProperties = optimizedData.filter(property => property.id && property.title);
          
          // Sort by likes count (descending) and get top 4 most liked chalets
          const mostLikedProperties = validProperties
            .sort((a, b) => {
              // Ensure we're comparing numbers
              const likesA = parseInt(a.likes_count) || 0;
              const likesB = parseInt(b.likes_count) || 0;
              return likesB - likesA;
            })
            .slice(0, 4);
          
          console.log('Top 4 most liked properties:', mostLikedProperties.map(p => ({ id: p.id, title: p.title, likes: p.likes_count })));
          
          setProperties(mostLikedProperties);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (loading) {
    return (
      <section className="property-listings">
        <div className="listings-header">
          <h2 className="listings-title">Most Loved Swiss Chalets</h2>
        </div>
        <div className="loading">Loading most liked properties...</div>
      </section>
    );
  }
  
  if (error) {
    return (
      <section className="property-listings">
        <div className="listings-header">
          <h2 className="listings-title">Most Loved Swiss Chalets</h2>
        </div>
        <div className="error">{error}</div>
      </section>
    );
  }
  
  if (properties.length === 0) {
    return (
      <section className="property-listings">
        <div className="listings-header">
          <h2 className="listings-title">Most Loved Swiss Chalets</h2>
        </div>
        <div className="no-results">No properties found.</div>
      </section>
    );
  }

  return (
    <section className="property-listings">
      <div className="listings-header">
        <h2 className="listings-title">
          <FiHeart className="title-icon" />
          Most Loved Swiss Chalets
        </h2>
        <p className="listings-subtitle">
          Discover the chalets that have captured the hearts of our visitors. These are the most favorited and highly appreciated properties across Switzerland.
        </p>
      </div>

      <div className="properties-container">
        <div className="properties-grid">
          {properties.map((property, index) => (
            <div key={property.id} className="property-wrapper">
              <Property 
                property={property} 
                variant="default"
              />
              {/* Optional: Show ranking badge */}
              <div className="likes-badge">
                <FiHeart className="heart-icon" />
                {property.likes_count || 0} {property.likes_count === 1 ? 'like' : 'likes'}
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="view-all-container">
        <Link to="/properties" className="view-all-btn">
          <FiHome /> View All Chalets
        </Link>
      </div>
    </section>
  );
};

export default PropertyListings;
