import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import '../styles/ChaletDetailPage.css';

const ChaletDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [chalet, setChalet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeImage, setActiveImage] = useState(0);

  const formatPrice = (price) => {
    return new Intl.NumberFormat('de-CH', {
      style: 'currency',
      currency: 'CHF',
      maximumFractionDigits: 0
    }).format(price);
  };

  useEffect(() => {
    const fetchChalet = async () => {
      try {
        const response = await fetch(`http://localhost/real_estate_project/api/get_chalet.php?id=${id}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        setChalet(data);
      } catch (err) {
        console.error('Error fetching chalet:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchChalet();
  }, [id]);

  const handleBackToList = () => {
    navigate('/');
  };

  if (loading) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading chalet details...</p>
        </div>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="error-container">
          <h2>Error loading chalet</h2>
          <p>{error}</p>
          <button onClick={handleBackToList} className="back-button">
            Back to Listings
          </button>
        </div>
        <Footer />
      </div>
    );
  }

  if (!chalet) {
    return (
      <div className="chalet-detail-page">
        <Navbar />
        <div className="not-found-container">
          <h2>Chalet not found</h2>
          <p>The requested chalet could not be found.</p>
          <button onClick={handleBackToList} className="back-button">
            Back to Listings
          </button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="chalet-detail-page">
      <Navbar />
      
      <div className="detail-header-bar">
        <button onClick={handleBackToList} className="back-button">
          <i className="fas fa-arrow-left"></i> Back to Listings
        </button>
        <h1>{chalet.title}</h1>
      </div>
      
      <main className="chalet-detail-container">
        {/* Image Gallery */}
        <div className="chalet-gallery">
          <div className="main-image">
            <img 
              src={chalet.gallery_images ? chalet.gallery_images[activeImage] : chalet.main_image} 
              alt={chalet.title} 
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/800x500?text=Chalet+Image';
              }}
            />
          </div>
          
          {chalet.gallery_images && chalet.gallery_images.length > 1 && (
            <div className="thumbnail-container">
              {chalet.gallery_images.map((img, index) => (
                <div 
                  key={index}
                  className={`thumbnail ${index === activeImage ? 'active' : ''}`}
                  onClick={() => setActiveImage(index)}
                >
                  <img 
                    src={img} 
                    alt={`${chalet.title} - ${index + 1}`} 
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/100x75?text=Thumbnail';
                    }}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
        
        {/* Property Details */}
        <div className="chalet-details">
          <div className="detail-header">
            <div className="price-status">
              <span className="price">{formatPrice(chalet.price)}</span>
              <span className={`status ${chalet.status.toLowerCase()}`}>
                {chalet.status}
              </span>
            </div>
            <p className="address">
              <i className="fas fa-map-marker-alt"></i> {chalet.address}, {chalet.postal_code} {chalet.city}, {chalet.canton}
            </p>
          </div>
          
          <div className="detail-section">
            <h2>Description</h2>
            <p>{chalet.description}</p>
          </div>
          
          <div className="detail-section features">
            <h2>Features</h2>
            <div className="features-grid">
              <div className="feature">
                <i className="fas fa-bed"></i>
                <span>{chalet.beds} Bedrooms</span>
              </div>
              <div className="feature">
                <i className="fas fa-bath"></i>
                <span>{chalet.baths} Bathrooms</span>
              </div>
              <div className="feature">
                <i className="fas fa-ruler-combined"></i>
                <span>{chalet.sqft} sqft</span>
              </div>
              <div className="feature">
                <i className="fas fa-calendar-alt"></i>
                <span>Year Built: {chalet.year_built}</span>
              </div>
              <div className="feature">
                <i className="fas fa-home"></i>
                <span>Type: {chalet.property_type}</span>
              </div>
              <div className="feature">
                <i className="fas fa-clock"></i>
                <span>{chalet.days_on_market} days on market</span>
              </div>
            </div>
          </div>
          
          {chalet.amenities && chalet.amenities.length > 0 && (
            <div className="detail-section amenities">
              <h2>Amenities</h2>
              <ul className="amenities-list">
                {chalet.amenities.map((amenity, index) => (
                  <li key={index}>
                    <i className="fas fa-check"></i> {amenity}
                  </li>
                ))}
              </ul>
            </div>
          )}
          
          <div className="detail-section map">
            <h2>Location</h2>
            {chalet.latitude && chalet.longitude ? (
              <div className="map-container">
                <img 
                  src={`https://maps.googleapis.com/maps/api/staticmap?center=${chalet.latitude},${chalet.longitude}&zoom=14&size=600x300&markers=color:red%7C${chalet.latitude},${chalet.longitude}&key=YOUR_API_KEY`} 
                  alt="Property location" 
                />
                <p className="map-note">
                  <i className="fas fa-info-circle"></i> Exact location provided after booking
                </p>
              </div>
            ) : (
              <p>Location information not available</p>
            )}
          </div>
        </div>
      </main>
      
      <Footer />
    </div>
  );
};

export default ChaletDetailPage;
