import React, { useState, useEffect } from 'react';
import '../styles/AllPropertyListings.css';
import Navbar from '../components/Navbar';
import PropertyFilters from '../components/PropertyFilters';  
import Footer from '../components/Footer';
import Property from '../components/Property';

const AllPropertiesPage = () => {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    searchTerm: '',
    priceRange: [0, 5000000],
    bedrooms: 0,
    bathrooms: 0
  });

  useEffect(() => {
    // Fetch properties when component mounts
    const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
    
    fetch(apiUrl)
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setProperties(data);
        setFilteredProperties(data); // Initialize filtered properties with all properties
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching chalets:', err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  // Apply filters whenever filters or properties change
  useEffect(() => {
    if (properties.length === 0) return;
    
    const filtered = properties.filter(property => {
      // Filter by search term (location or any text in the property)
      const searchLower = filters.searchTerm.toLowerCase();
      const matchesSearch = filters.searchTerm === '' || 
        property.address.toLowerCase().includes(searchLower);
      
      // Filter by price
      const price = parseFloat(property.price.replace(/[^0-9.-]+/g, ''));
      const matchesPrice = price >= filters.priceRange[0] && price <= filters.priceRange[1];
      
      // Filter by bedrooms
      const matchesBeds = filters.bedrooms === 0 || property.beds >= filters.bedrooms;
      
      // Filter by bathrooms
      const matchesBaths = filters.bathrooms === 0 || property.baths >= filters.bathrooms;
      
      return matchesSearch && matchesPrice && matchesBeds && matchesBaths;
    });
    
    setFilteredProperties(filtered);
  }, [filters, properties]);

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  if (loading) {
    return (
      <div className="all-properties-page">
        <Navbar />
        <div className="loading-message">Loading chalets...</div>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="all-properties-page">
        <Navbar />
        <div className="error-message">Error: {error}</div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="all-properties-page">
      <Navbar /> 
      <PropertyFilters onFilterChange={handleFilterChange} />
      <main className="listings-section">
        <div className="listings-header">
          <h1 className="listings-title">All Swiss Chalets</h1>
          <p className="listings-subtitle">
            Browse our complete collection of luxury chalets across Switzerland
          </p>
        </div>

        {filteredProperties.length === 0 ? (
          <div className="no-properties-message">
            No chalets match your filter criteria. Try adjusting your filters.
          </div>
        ) : (
          <div className="properties-grid">
            {filteredProperties.map((property) => (
              <Property 
                key={property.id} 
                property={property} 
                variant="detailed" 
              />
            ))}
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
