// Property.jsx - With fixed error handling for PHP HTML responses
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiDroplet, FiHeart } from 'react-icons/fi';
import '../styles/Property.css';

const Property = ({ property, variant = "default", onFavoriteChange }) => {
  // State to track if the property is liked
  const [isLiked, setIsLiked] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  // Make sure property exists, otherwise use an empty object
  const propertyData = property || {};
  
  const {
    id,
    title,
    price,
    main_image,
    city,
    bedrooms,
    bathrooms,
    status
  } = propertyData;

  // Format price with commas
  const formattedPrice = typeof price === 'number' 
    ? price.toLocaleString() 
    : Number(price) ? Number(price).toLocaleString() : 'Price on request';

  // Placeholder image if main_image is missing
  const imageSrc = main_image || "/placeholder.jpg";

  // Handle JSON response safely (with error handling for HTML responses)
  const safeJsonParse = async (response) => {
    try {
      const text = await response.text();
      
      // Check if the response is HTML (PHP error)
      if (text.trim().startsWith('<')) {
        console.log('Received HTML response:', text);
        return { success: false, message: 'Server returned HTML instead of JSON' };
      }
      
      try {
        // Try to parse as JSON
        return JSON.parse(text);
      } catch (e) {
        console.log('Failed to parse JSON:', text);
        return { success: false, message: 'Invalid JSON response' };
      }
    } catch (e) {
      console.log('Error reading response:', e);
      return { success: false, message: 'Error reading response' };
    }
  };
  
  // Handle like button click
  const handleLikeClick = (e) => {
    e.preventDefault(); // Prevent navigation to the chalet detail page
    e.stopPropagation(); // Stop event propagation
    
    console.log('Like button clicked for chalet ID:', id);
    
    // Check if user is logged in
    const user = localStorage.getItem('user');
    if (!user) {
      alert('Please log in to add this property to your favorites');
      return;
    }
    
    setIsLoading(true);
    setIsLiked(!isLiked); // Optimistically update UI
    
    const isCurrentlyLiked = isLiked; // Store current state
    
    // Toggle the favorite status
    if (!isCurrentlyLiked) {
      // Add to favorites
      fetch('http://localhost/real_estate_project/api/add_to_favorites.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ chaletId: id }),
      })
        .then(safeJsonParse)
        .then(data => {
          console.log('Add to favorites response:', data);
          if (data.success) {
            // Notify parent component if callback provided
            if (onFavoriteChange) {
              onFavoriteChange(id, true);
            }
          } else {
            // Revert UI change on error
            setIsLiked(isCurrentlyLiked);
            console.log('Favorite error:', data.message);
          }
        })
        .catch(error => {
          // Revert UI change on error
          setIsLiked(isCurrentlyLiked);
          console.log('Favorite error:', error);
        })
        .finally(() => {
          setIsLoading(false);
        });
    } else {
      // Remove from favorites
      fetch('http://localhost/real_estate_project/api/remove_from_favorites.php', {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ chaletId: id }),
      })
        .then(safeJsonParse)
        .then(data => {
          console.log('Remove from favorites response:', data);
          if (data.success) {
            // Notify parent component if callback provided
            if (onFavoriteChange) {
              onFavoriteChange(id, false);
            }
          } else {
            // Revert UI change on error
            setIsLiked(isCurrentlyLiked);
            console.log('Favorite error:', data.message);
          }
        })
        .catch(error => {
          // Revert UI change on error
          setIsLiked(isCurrentlyLiked);
          console.log('Favorite error:', error);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  };
  
  return (
    <Link to={`/chalet/${id}`} className={`property-card ${variant}`}>
      <div className="property-image-container">
        <img 
          src={imageSrc} 
          alt={title || "Swiss Chalet"} 
          className="property-image" 
          loading="lazy" 
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "/placeholder.jpg";
          }}
        />
        {status && <div className="property-badge">{status}</div>}
        
        {/* Like Button - with robust error handling */}
        <button 
          className={`like-button ${isLiked ? 'liked' : ''} ${isLoading ? 'loading' : ''}`}
          onClick={handleLikeClick}
          aria-label={isLiked ? "Remove from favorites" : "Add to favorites"}
          disabled={isLoading}
        >
          <FiHeart />
        </button>
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{title || "Beautiful Swiss Chalet"}</h3>
        <div className="property-price">{formattedPrice} CHF</div>
        
        {city && (
          <div className="property-location">
            <FiMapPin className="property-icon" /> {city}
          </div>
        )}
        
        <div className="property-meta">
          {bedrooms && (
            <span className="property-feature">
              <FiHome className="property-icon" /> {bedrooms} beds
            </span>
          )}
          
          {bathrooms && (
            <span className="property-feature">
              <FiDroplet className="property-icon" /> {bathrooms} baths
            </span>
          )}
        </div>
      </div>
    </Link>
  );
};

export default Property;
