// AllPropertiesPage.jsx
import React, { useState, useEffect } from 'react';
import Property from '../components/Property';
import '../styles/AllPropertypage.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import SearchFilter from '../components/SearchFilter';

const AllPropertiesPage = () => {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        console.log("Data received from API:", data);
        
        if (Array.isArray(data)) {
          setProperties(data);
          setFilteredProperties(data); // Initialize filtered properties with all properties
          console.log("Properties set:", data.length);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  // Debug when filtered properties change
  useEffect(() => {
    console.log("Filtered properties updated:", filteredProperties.length);
  }, [filteredProperties]);

  const updateFilteredProperties = (newFiltered) => {
    console.log("Setting filtered properties:", newFiltered.length);
    setFilteredProperties(newFiltered);
  };

  if (loading) return <div className="loading">Loading properties...</div>;
  if (error) return <div className="error">{error}</div>;
  if (properties.length === 0) return <div className="no-results">No properties found.</div>;

  return (
    <div className="all-properties-page">
      <Navbar />
      
      {/* Search and Filter Component */}
      <SearchFilter 
        properties={properties} 
        setFilteredProperties={updateFilteredProperties} 
      />
      
      <div className="property-listings">
        <div className="listings-header">
          <h2 className="listings-title">Our Premier Swiss Chalets</h2>
          <p className="listings-subtitle">
            Discover our handpicked selection of luxury chalets in the most prestigious Swiss mountain resorts.
          </p>
          <p className="results-count">
            {filteredProperties.length} {filteredProperties.length === 1 ? 'property' : 'properties'} found
          </p>
        </div>

        {filteredProperties.length === 0 ? (
          <div className="no-results">No properties match your search criteria. Try adjusting your filters.</div>
        ) : (
          <div className="properties-container">
            <div className="properties-grid">
              {filteredProperties.map(property => (
                <Property 
                  key={property.id} 
                  property={property}
                  variant="grid" // Use grid variant for this view
                />
              ))}
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
