import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Login.css';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();

  // Get Google Client ID from environment
  const googleClientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;

  // Debug: Check if Client ID is loaded
  useEffect(() => {
    console.log('Google Client ID:', googleClientId);
    if (!googleClientId) {
      setError('Google Client ID not configured. Please check your .env file.');
    }
  }, [googleClientId]);

  // Initialize Google Sign-In
  useEffect(() => {
    if (!googleClientId) {
      console.warn('Google Client ID is missing');
      return;
    }

    const initializeGoogleSignIn = () => {
      if (window.google) {
        try {
          window.google.accounts.id.initialize({
            client_id: googleClientId,
            callback: handleGoogleResponse,
            auto_select: false,
            cancel_on_tap_outside: true,
          });
          
          window.google.accounts.id.renderButton(
            document.getElementById("google-signin-button"),
            {
              theme: "outline",
              size: "large",
              width: "100%",
              text: "signin_with",
              shape: "rectangular",
            }
          );
          
          console.log('Google Sign-In initialized successfully');
        } catch (error) {
          console.error('Error initializing Google Sign-In:', error);
          setError('Failed to initialize Google Sign-In');
        }
      }
    };

    // Load Google Sign-In script
    if (!window.google) {
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = initializeGoogleSignIn;
      script.onerror = () => {
        console.error('Failed to load Google Sign-In script');
        setError('Failed to load Google Sign-In');
      };
      document.head.appendChild(script);
    } else {
      initializeGoogleSignIn();
    }
  }, [googleClientId]);

  const handleGoogleResponse = async (response) => {
    try {
      setLoading(true);
      setError('');
      
      console.log('Google response received');

      const result = await fetch(`${process.env.REACT_APP_API_BASE_URL}/google-login.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          token: response.credential
        })
      });

      const data = await result.json();
      
      if (data.success) {
        login({
          id: data.user_id, 
          email: data.email,
          is_admin: data.is_admin,
          name: data.name 
        });
      } else {
        setError(data.message || 'Google sign-in failed');
      }
    } catch (error) {
      setError('Google sign-in failed. Please try again.');
      console.error('Google sign-in error:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/login.php`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify(formData)
      });

      const data = await response.json();
      
      if (data.success) {
        login({
          id: data.user_id, 
          email: formData.email,
          is_admin: data.is_admin,
          name: data.name 
        });
      } else {
        setError(data.message || 'Invalid credentials');
      }
    } catch (error) {
      setError('Network error. Please try again.');
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="shape1"></div>
      <div className="shape2"></div>
      
      <div className="login-form-container">
        <div className="login-header">
          <h1>
            <span className="gradient-text">Welcome Back</span>
          </h1>
          <p>Log in to access your account</p>
        </div>
        
        {error && <div className="error-message">{error}</div>}
        
        {/* Google Sign-In Button - Only show if Client ID is available */}
        {googleClientId && (
          <>
            <div className="google-signin-container">
              <div id="google-signin-button"></div>
            </div>
            
            <div className="divider">
              <span>or</span>
            </div>
          </>
        )}
        
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`} 
            disabled={loading}
          >
            {loading ? '' : 'Log In'}
          </button>
        </form>
        
        <div className="login-footer">
          <p>
            Don't have an account? <Link to="/register">Sign Up</Link>
          </p>
          <p>
            <Link to="/">Return to Home</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
