// Property.jsx - Improved component with Swiss Franc currency
import React from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiDroplet } from 'react-icons/fi';
import '../styles/Property.css';

const Property = ({ property, variant = "default" }) => {
  // Handle missing data gracefully
  const {
    id,
    title = "Beautiful Chalet",
    price = "N/A",
    main_image = "/placeholder.jpg",
    city = "Unknown Location",
    bedrooms = "N/A",
    bathrooms = "N/A",
    status = "Available"
  } = property || {};

  // Format price with commas
  const formattedPrice = typeof price === 'number' 
    ? price.toLocaleString() 
    : Number(price) ? Number(price).toLocaleString() : price;

  return (
    <Link to={`/chalet/${id}`} className={`property-card ${variant}`}>
      <div className="property-image-container">
        <img 
          src={main_image} 
          alt={title} 
          className="property-image" 
          loading="lazy" 
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = "/placeholder.jpg";
          }}
        />
        <div className="property-badge">{status}</div>
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{title}</h3>
        <div className="property-price">{formattedPrice} CHF</div>
        <div className="property-location">
          <FiMapPin className="property-icon" /> {city}
        </div>
        <div className="property-meta">
          <span className="property-feature">
            <FiHome className="property-icon" /> {bedrooms} beds
          </span>
          <span className="property-feature">
            <FiDroplet className="property-icon" /> {bathrooms} baths
          </span>
        </div>
      </div>
    </Link>
  );
};

export default Property;
