// SearchFilter.jsx
import React, { useState, useEffect } from 'react';
import '../styles/SearchFilter.css';

const SearchFilter = ({ properties, setFilteredProperties }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [location, setLocation] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [bedrooms, setBedrooms] = useState('');
  const [bathrooms, setBathrooms] = useState('');
  const [sortBy, setSortBy] = useState('default');

  // Debugging: Log properties received
  useEffect(() => {
    console.log("Properties received in SearchFilter:", properties);
  }, [properties]);

  // Get unique locations for the dropdown
  const uniqueLocations = [...new Set(properties.map(prop => prop.location || prop.city))];

  const handleFilter = () => {
    console.log("Applying filters...");
    let filtered = [...properties];

    // Filter by search term (property name)
    if (searchTerm) {
      filtered = filtered.filter(prop => 
        prop.title && prop.title.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by location - check both location and city fields
    if (location) {
      filtered = filtered.filter(prop => 
        (prop.location && prop.location === location) || 
        (prop.city && prop.city === location)
      );
    }

    // Filter by price range
    if (minPrice) {
      filtered = filtered.filter(prop => {
        const propPrice = parseFloat(prop.price);
        return !isNaN(propPrice) && propPrice >= parseFloat(minPrice);
      });
    }
    if (maxPrice) {
      filtered = filtered.filter(prop => {
        const propPrice = parseFloat(prop.price);
        return !isNaN(propPrice) && propPrice <= parseFloat(maxPrice);
      });
    }

    // Filter by exact number of bedrooms
    if (bedrooms) {
      const bedroomCount = parseInt(bedrooms);
      if (bedroomCount === 6) {
        // 6+ bedrooms option
        filtered = filtered.filter(prop => {
          const propBedrooms = parseInt(prop.bedrooms || prop.bedroom_count || prop.bed_count || prop.beds || 0);
          return propBedrooms >= 6;
        });
      } else {
        // Exact bedroom count
        filtered = filtered.filter(prop => {
          const propBedrooms = parseInt(prop.bedrooms || prop.bedroom_count || prop.bed_count || prop.beds || 0);
          return propBedrooms === bedroomCount;
        });
      }
    }

    // Filter by exact number of bathrooms
    if (bathrooms) {
      const bathroomCount = parseInt(bathrooms);
      if (bathroomCount === 5) {
        // 5+ bathrooms option
        filtered = filtered.filter(prop => {
          const propBathrooms = parseInt(prop.bathrooms || prop.bathroom_count || prop.bath_count || prop.baths || 0);
          return propBathrooms >= 5;
        });
      } else {
        // Exact bathroom count
        filtered = filtered.filter(prop => {
          const propBathrooms = parseInt(prop.bathrooms || prop.bathroom_count || prop.bath_count || prop.baths || 0);
          return propBathrooms === bathroomCount;
        });
      }
    }

    // Sort filtered properties
    switch (sortBy) {
      case 'price-asc':
        filtered.sort((a, b) => parseFloat(a.price || 0) - parseFloat(b.price || 0));
        break;
      case 'price-desc':
        filtered.sort((a, b) => parseFloat(b.price || 0) - parseFloat(a.price || 0));
        break;
      case 'newest':
        filtered.sort((a, b) => new Date(b.created_at || 0) - new Date(a.created_at || 0));
        break;
      case 'oldest':
        filtered.sort((a, b) => new Date(a.created_at || 0) - new Date(b.created_at || 0));
        break;
      case 'most-liked':
        filtered.sort((a, b) => parseInt(b.likes || 0) - parseInt(a.likes || 0));
        break;
      case 'a-z':
        filtered.sort((a, b) => (a.title || '').localeCompare(b.title || ''));
        break;
      case 'z-a':
        filtered.sort((a, b) => (b.title || '').localeCompare(a.title || ''));
        break;
      default:
        // Keep original order or use default sorting
        break;
    }

    console.log("Filtered properties count:", filtered.length);
    setFilteredProperties(filtered);
  };

  // Auto-apply filters when any filter value changes - removed to use Apply button instead
  // useEffect(() => {
  //   handleFilter();
  // }, [searchTerm, location, minPrice, maxPrice, bedrooms, bathrooms, sortBy]);

  const clearFilters = () => {
    setSearchTerm('');
    setLocation('');
    setMinPrice('');
    setMaxPrice('');
    setBedrooms('');
    setBathrooms('');
    setSortBy('default');
    setFilteredProperties(properties);
  };

  // Handle input changes with debounce
  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
  };

  return (
    <div className="search-filter search-filter-inline">
      <div className="filter-container">
        <div className="filter-row-inline">
          <div className="filter-group">
            <input
              type="text"
              placeholder="Search chalets..."
              value={searchTerm}
              onChange={handleInputChange(setSearchTerm)}
            />
          </div>
          
          <div className="filter-group">
            <select 
              value={location} 
              onChange={handleInputChange(setLocation)}
            >
              <option value="">All Locations</option>
              {uniqueLocations.map((loc, index) => (
                <option key={index} value={loc}>{loc}</option>
              ))}
            </select>
          </div>
          
          <div className="filter-group price-range">
            <select
              value={minPrice}
              onChange={handleInputChange(setMinPrice)}
            >
              <option value="">Min Price</option>
              <option value="100000">100,000 CHF</option>
              <option value="500000">500,000 CHF</option>
              <option value="1000000">1,000,000 CHF</option>
              <option value="2000000">2,000,000 CHF</option>
              <option value="5000000">5,000,000 CHF</option>
              <option value="10000000">10,000,000 CHF</option>
            </select>
            <select
              value={maxPrice}
              onChange={handleInputChange(setMaxPrice)}
            >
              <option value="">Max Price</option>
              <option value="500000">500,000 CHF</option>
              <option value="1000000">1,000,000 CHF</option>
              <option value="2000000">2,000,000 CHF</option>
              <option value="5000000">5,000,000 CHF</option>
              <option value="10000000">10,000,000 CHF</option>
              <option value="20000000">20,000,000 CHF</option>
            </select>
          </div>
          
          <div className="filter-group">
            <select 
              value={bedrooms} 
              onChange={handleInputChange(setBedrooms)}
            >
              <option value="">Bedrooms</option>
              <option value="1">1 Bedroom</option>
              <option value="2">2 Bedrooms</option>
              <option value="3">3 Bedrooms</option>
              <option value="4">4 Bedrooms</option>
              <option value="5">5 Bedrooms</option>
              <option value="6">6+ Bedrooms</option>
            </select>
          </div>
          
          <div className="filter-group">
            <select 
              value={bathrooms} 
              onChange={handleInputChange(setBathrooms)}
            >
              <option value="">Bathrooms</option>
              <option value="1">1 Bathroom</option>
              <option value="2">2 Bathrooms</option>
              <option value="3">3 Bathrooms</option>
              <option value="4">4 Bathrooms</option>
              <option value="5">5+ Bathrooms</option>
            </select>
          </div>
          
          <div className="filter-group">
            <select 
              value={sortBy} 
              onChange={handleInputChange(setSortBy)}
            >
              <option value="default">Sort By</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="most-liked">Most Popular</option>
              <option value="a-z">Name: A to Z</option>
              <option value="z-a">Name: Z to A</option>
            </select>
          </div>
          
          <div className="filter-buttons">
            <button className="filter-button apply" onClick={handleFilter}>Apply</button>
            <button className="filter-button clear" onClick={clearFilters}>Clear</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchFilter;
