import React, { useState, useEffect } from 'react';
import '../styles/AllPropertyListings.css';
import Navbar from '../components/Navbar';
import PropertyFilters from '../components/PropertyFilters';  
import Footer from '../components/Footer';
import Property from '../components/Property';

const AllPropertiesPage = () => {
  const [properties, setProperties] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const response = await fetch('/api/get_chalets.php');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        
        // Transform the data to match the component's expected format
        const formattedProperties = data.map(chalet => ({
          id: chalet.id,
          image: chalet.main_image,
          title: chalet.title,
          price: `CHF ${new Intl.NumberFormat().format(chalet.price)}`,
          beds: chalet.beds,
          baths: chalet.baths,
          sqft: chalet.sqft,
          yearBuilt: chalet.year_built,
          propertyType: chalet.property_type,
          status: chalet.status,
          address: chalet.address,
          location: `${chalet.city}, ${chalet.canton} ${chalet.postal_code}`,
          daysOnMarket: chalet.days_on_market,
          amenities: chalet.amenities ? JSON.parse(chalet.amenities) : [],
          description: chalet.description,
          galleryImages: chalet.gallery_images ? JSON.parse(chalet.gallery_images) : []
        }));
        
        setProperties(formattedProperties);
        setIsLoading(false);
      } catch (error) {
        console.error("Failed to fetch properties:", error);
        setError(error.message);
        setIsLoading(false);
      }
    };

    fetchProperties();
  }, []);

  if (isLoading) {
    return (
      <div className="all-properties-page">
        <Navbar />
        <div className="loading-container">
          <p>Loading properties...</p>
        </div>
        <Footer />
      </div>
    );
  }

  if (error) {
    return (
      <div className="all-properties-page">
        <Navbar />
        <div className="error-container">
          <p>Error loading properties: {error}</p>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="all-properties-page">
      <Navbar /> 
      <PropertyFilters />
      <main className="listings-section">
        <div className="listings-header">
          <h1 className="listings-title">All Swiss Chalets</h1>
          <p className="listings-subtitle">
            Browse our complete collection of luxury chalets across Switzerland
          </p>
        </div>

        <div className="properties-grid">
          {properties.length > 0 ? (
            properties.map((property) => (
              <Property key={property.id} property={property} variant="detailed" />
            ))
          ) : (
            <p className="no-properties">No properties available at the moment.</p>
          )}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
