import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default markers in react-leaflet
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow,
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
});

L.Marker.prototype.options.icon = DefaultIcon;

// Create a custom chalet icon (optional)
const chaletIcon = L.icon({
    iconUrl: 'data:image/svg+xml;base64,' + btoa(`
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="#e53e3e" width="30" height="30">
            <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/>
        </svg>
    `),
    iconSize: [30, 30],
    iconAnchor: [15, 30],
    popupAnchor: [0, -30]
});

const ChaletMap = ({ latitude, longitude, title, address, city, canton }) => {
    // Default to Swiss coordinates if no coordinates provided
    const defaultLat = 46.8182;
    const defaultLng = 8.2275;
    
    const lat = latitude || defaultLat;
    const lng = longitude || defaultLng;
    
    // Center the map on the chalet location
    const position = [lat, lng];
    
    return (
        <div className="chalet-map-container">
            <MapContainer 
                center={position} 
                zoom={13} 
                style={{ height: '300px', width: '100%', borderRadius: '12px' }}
                scrollWheelZoom={false}
                zoomControl={true}
            >
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={position} icon={chaletIcon}>
                    <Popup>
                        <div className="map-popup">
                            <h4>{title}</h4>
                            <p>{address}</p>
                            <p>{city}, {canton}</p>
                        </div>
                    </Popup>
                </Marker>
            </MapContainer>
        </div>
    );
};

export default ChaletMap;
