import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Property.css';

const Property = ({ property, variant = 'standard' }) => {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate(`/chalet/${property.id}`);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('de-CH', {
      style: 'currency',
      currency: 'CHF',
      maximumFractionDigits: 0
    }).format(price);
  };

  return (
    <div 
      className={`property ${variant}`} 
      onClick={handleClick}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => e.key === 'Enter' && handleClick()}
    >
      <div className="property-image-container">
        <img 
          src={property.main_image} 
          alt={property.title} 
          className="property-image"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = 'https://via.placeholder.com/400x300?text=Chalet+Image';
          }}
        />
        {property.status && (
          <span className={`property-status ${property.status.toLowerCase()}`}>
            {property.status}
          </span>
        )}
      </div>
      
      <div className="property-details">
        <h3 className="property-title">{property.title}</h3>
        <p className="property-address">
          <i className="fas fa-map-marker-alt"></i> {property.address}, {property.city}
        </p>
        
        <div className="property-price">{formatPrice(property.price)}</div>
        
        <div className="property-features">
          <span className="feature">
            <i className="fas fa-bed"></i> {property.beds} beds
          </span>
          <span className="feature">
            <i className="fas fa-bath"></i> {property.baths} baths
          </span>
          <span className="feature">
            <i className="fas fa-ruler-combined"></i> {property.sqft} sqft
          </span>
        </div>
        
        {variant === 'detailed' && (
          <>
            <p className="property-description">
              {property.description.length > 150 
                ? `${property.description.substring(0, 150)}...` 
                : property.description}
            </p>
            <div className="property-meta">
              <span>On Market: {property.days_on_market} days</span>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Property;
