import React, { useState, useEffect } from 'react';
import { FiSearch, FiArrowRight } from 'react-icons/fi';

const Hero = () => {
  // Images for the carousel
  const images = [
    "/assets/Swiss_chalet_1.jpg",
    "/assets/Swiss_chalet_2.jpg",
    "/assets/Swiss_chalet_3.jpg",
    "/assets/Swiss_chalet_4.jpg",
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Function to move to next slide
  const nextSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  // Function to move to previous slide
  const prevSlide = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  // Function to move to a specific slide
  const goToSlide = (index) => {
    setCurrentImageIndex(index);
  };

  // Automatic slide change
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000); // Change slide every 3 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <section className="hero w-full min-h-screen max-h-screen flex items-center relative overflow-hidden px-4 md:px-8">
      {/* Decorative elements */}
      <div className="absolute z-10 opacity-10 w-64 h-64 md:w-96 md:h-96 rounded-full bg-gradient-to-br from-red-500 to-blue-500 -top-32 -right-32"></div>
      <div className="absolute z-10 opacity-10 bottom-1/5 left-20 w-32 h-32 bg-dots"></div>
      
      <div className="max-w-6xl w-full mx-auto grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-16 items-center relative z-20">
        <div className="flex flex-col items-center lg:items-start text-center lg:text-left animate-fadeInUp">
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-extrabold leading-tight mb-6 bg-gradient-to-r from-red-500 to-blue-500 bg-clip-text text-transparent tracking-tight">
            Find Your Dream Chalets in Switzerland
          </h1>
          <p className="text-base md:text-lg text-gray-800 opacity-90 mb-10 lg:max-w-90 leading-relaxed">
            Only the finest chalets, chosen for you.
            We have hand-selected the most beautiful and perfect Swiss chalets, offering an unmatched blend of luxury, comfort, and stunning natural surroundings.
          </p>
          
          <div className="flex flex-col sm:flex-row w-full sm:w-auto mb-4">
            <a href="#search" className="px-6 py-4 rounded-full font-semibold text-base bg-gradient-to-r from-red-500 to-blue-500 text-white shadow-lg hover:translate-y-1 transform transition duration-300 flex items-center justify-center gap-3 mb-4 sm:mb-0">
              <FiSearch /> Explore Chalets <FiArrowRight />
            </a>
          </div>
          
          <div className="flex flex-wrap justify-center lg:justify-start gap-8 mt-8">
            <div className="flex flex-col">
              <span className="text-3xl md:text-4xl font-bold text-red-500 leading-none">10+</span>
              <span className="text-sm text-gray-800 opacity-80 mt-2">Properties</span>
            </div>
            <div className="flex flex-col">
              <span className="text-3xl md:text-4xl font-bold text-red-500 leading-none">95%</span>
              <span className="text-sm text-gray-800 opacity-80 mt-2">Customer Satisfaction</span>
            </div>
            <div className="flex flex-col">
              <span className="text-3xl md:text-4xl font-bold text-red-500 leading-none">15+</span>
              <span className="text-sm text-gray-800 opacity-80 mt-2">Years Experience</span>
            </div>
          </div>
        </div>
        
        <div className="relative h-full w-full mt-8 lg:mt-0 animate-fadeIn">
          {/* Image slider */}
          <div className="relative w-full h-full overflow-hidden rounded-lg">
            {images.map((image, index) => (
              <img 
                key={index}
                src={image} 
                alt={`Luxury Swiss home ${index + 1}`} 
                className={`absolute w-full h-auto rounded-lg transition-all duration-800 ease-in-out ${
                  index === currentImageIndex ? 'opacity-100 transform translate-x-0 z-20' : 'opacity-0 transform translate-x-full z-10'
                }`}
                style={{
                  transform: index === currentImageIndex ? 'perspective(1000px) rotateY(-10deg) rotateX(5deg) translateX(0)' : 'perspective(1000px) rotateY(-10deg) rotateX(5deg) translateX(100px)'
                }}
              />
            ))}

            {/* Dots navigation */}
            <div className="absolute bottom-5 left-1/2 transform -translate-x-1/2 flex gap-2 z-30">
              {images.map((_, index) => (
                <button 
                  key={index} 
                  className={`w-3 h-3 rounded-full transition-all duration-300 ${
                    index === currentImageIndex ? 'bg-red-500 transform scale-110' : 'bg-red-300 bg-opacity-50'
                  }`}
                  onClick={() => goToSlide(index)}
                  aria-label={`Go to slide ${index + 1}`}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
