import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { FiHome, FiSearch, FiMail, FiInfo, FiLogIn, FiUser, FiSettings, FiLogOut } from 'react-icons/fi';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // For managing dropdown state
  const location = useLocation();
  const navigate = useNavigate();

  // Get the user data from localStorage
  const user = JSON.parse(localStorage.getItem('user'));

  // Set active link based on current path
  const getActiveLink = () => {
    const path = location.pathname;
    if (path === '/' || path === '/home') return 'home';
    if (path === '/properties') return 'buy';
    if (path === '/rent') return 'rent';
    if (path === '/about') return 'about';
    if (path === '/contact') return 'contact';
    return '';
  };

  const activeLink = getActiveLink();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  const handleHomeClick = () => {
    closeMenu();
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setIsDropdownOpen(false);
    navigate('/login');
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isMenuOpen && 
          !event.target.closest('.navbar-links') && 
          !event.target.closest('.menu-toggle')) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isMenuOpen]);

  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isMenuOpen]);

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-content">
        <div className="navbar-logo">
          <Link to="/" onClick={handleHomeClick}>
            <span className="navbar-logo-icon"></span>
            Chalets Suisses
          </Link>
        </div>

        <div 
          className={`menu-toggle ${isMenuOpen ? 'active' : ''}`} 
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </div>

        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          <li>
            <Link 
              to="/" 
              className={activeLink === 'home' ? 'active' : ''} 
              onClick={handleHomeClick}
            >
              <FiHome /> Home
            </Link>
          </li>
          <li>
            <Link 
              to="/properties" 
              className={activeLink === 'buy' ? 'active' : ''} 
              onClick={closeMenu}
            >
              <FiSearch /> Buy Chalets         
            </Link>
          </li>
          <li>
            <Link 
              to="/ChaletDetailPage" 
              onClick={closeMenu}
            >
              <FiSearch /> Rent Chalets
            </Link>
          </li>
          <li>
            <Link 
              to="/about" 
              className={activeLink === 'about' ? 'active' : ''} 
              onClick={closeMenu}
            >
              <FiInfo /> About Us
            </Link>
          </li>
          <li>
            <Link 
              to="/contact" 
              className={activeLink === 'contact' ? 'active' : ''} 
              onClick={() => {
                closeMenu();
                if (window.location.pathname === '/contact') {
                  window.history.replaceState(null, '', '/');
                  setTimeout(() => window.history.replaceState(null, '', '/contact'), 10);
                }
              }}
            >
              <FiMail /> Contact Us
            </Link>
          </li>

          {user ? (
            <div className="navbar-user" onClick={toggleDropdown}>
              <FiUser />
              {isDropdownOpen && (
                <div className="dropdown-menu">
                  <Link to="/user-dashboard" onClick={() => setIsDropdownOpen(false)}>
                    Dashboard
                  </Link>
                  <button onClick={handleLogout}>
                    Log out
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="navbar-auth">
              <Link to="/login" className="login-btn" onClick={closeMenu}>
                <FiLogIn /> Login / Sign Up
              </Link>
            </div>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
