// UserDashboard.jsx - Enhanced with more statistics
import { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Property from '../components/Property'; 
import { FiHome, FiHeart, FiUser, FiSettings, FiTrendingUp, FiTrendingDown, FiMinus, 
         FiCalendar, FiClock, FiEye, FiMessageSquare, FiStar } from 'react-icons/fi';
import '../styles/UserDashboard.css';

function UserDashboard() {
  const [chalets, setChalets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showChaletModal, setShowChaletModal] = useState(false);

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });

  useEffect(() => {
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_chalets.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Unauthorized');
        return res.json();
      })
      .then(data => {
        setChalets(data.chalets || data);
      })
      .catch(() => {
        setChalets([]);
      });
  }, []);

  const handleChangePassword = (e) => {
    e.preventDefault();
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/update_password.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || 'Password updated!');
        setShowModal(false);
        setOldPassword('');
        setNewPassword('');
      })
      .catch(() => {
        alert('Error updating password.');
      });
  };

  const handleChaletFormSubmit = (e) => {
    e.preventDefault();

    // Send the form data to an API or email service
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/send_chalet_request.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then((data) => {
        alert('Request submitted! We will contact you soon.');
        setShowChaletModal(false);
        setForm({ name: '', email: '', phone: '', message: '' });
      })
      .catch(() => {
        alert('Error submitting request.');
      });
  };

  return (
    <div className="user-dashboard-container">
      <Navbar />
      <div className="user-dashboard-scroll">
        <aside className="user-sidebar">
          <div className="sidebar-header">
            <h2 className="sidebar-title"><span className="gradient-text">User</span> Dashboard</h2>
          </div>
          <nav className="sidebar-nav">
            <ul>
              <li><a href="#home" className="nav-link">üè° Home</a></li>
              <li><a href="#my-chalets" className="nav-link">üè† My Chalets</a></li>
              <li><a href="#liked-chalets" className="nav-link">‚ù§Ô∏è Liked Chalets</a></li>
            </ul>
          </nav>
        </aside>

        <main className="user-main">
          <header className="user-header">
            <div className="header-content">
              <div>
                <h1>Welcome, <span className="gradient-text">User</span></h1>
                <p>{new Date().toLocaleDateString()}</p>
              </div>
              <button className="edit-btn" onClick={() => setShowModal(true)}>‚úèÔ∏è Edit Profile</button>
            </div>
          </header>

          <div className="user-content">
            <section id="home" className="dashboard-section">
              <h2>üìä Dashboard Overview</h2>
              
              {/* Enhanced Statistics Grid */}
              <div className="stats-dashboard">
                <div className="stats-grid">
                  <div className="stat-card">
                    <h3>Total Chalets</h3>
                    <p>{chalets.length}</p>
                    <div className="stat-trend up">
                      <FiTrendingUp /> 
                      <span>+2 since last month</span>
                    </div>
                  </div>
                  
                  <div className="stat-card">
                    <h3>Liked Chalets</h3>
                    <p>8</p>
                    <div className="stat-trend neutral">
                      <FiMinus /> 
                      <span>No change</span>
                    </div>
                  </div>
                  
                  <div className="stat-card">
                    <h3>Profile Views</h3>
                    <p>124</p>
                    <div className="stat-trend up">
                      <FiTrendingUp /> 
                      <span>+28% this week</span>
                    </div>
                  </div>
                  
                  <div className="stat-card">
                    <h3>Average Rating</h3>
                    <p>4.8</p>
                    <div className="stat-trend up">
                      <FiTrendingUp /> 
                      <span>+0.2 points</span>
                    </div>
                  </div>
                </div>
                
                {/* Activity Chart Section */}
                <div className="activity-chart">
                  <h3>
                    Monthly Engagement
                    <div className="chart-filters">
                      <button className="chart-filter-btn active">30d</button>
                      <button className="chart-filter-btn">90d</button>
                      <button className="chart-filter-btn">1y</button>
                    </div>
                  </h3>
                  <div className="chart-placeholder">
                    Your property engagement visualization will appear here
                  </div>
                </div>
                
                {/* Recent Activity Section */}
                <div className="recent-activity">
                  <h3>Recent Activity</h3>
                  <div className="activity-list">
                    <div className="activity-item">
                      <div className="activity-icon">
                        <FiEye />
                      </div>
                      <div className="activity-content">
                        <div className="activity-title">Someone viewed Matterhorn Chalet</div>
                        <div className="activity-time">2 hours ago</div>
                      </div>
                    </div>
                    
                    <div className="activity-item">
                      <div className="activity-icon">
                        <FiHeart />
                      </div>
                      <div className="activity-content">
                        <div className="activity-title">New like on Alpine Retreat</div>
                        <div className="activity-time">Yesterday</div>
                      </div>
                    </div>
                    
                    <div className="activity-item">
                      <div className="activity-icon">
                        <FiMessageSquare />
                      </div>
                      <div className="activity-content">
                        <div className="activity-title">New inquiry about Zermatt Lodge</div>
                        <div className="activity-time">3 days ago</div>
                      </div>
                    </div>
                    
                    <div className="activity-item">
                      <div className="activity-icon">
                        <FiStar />
                      </div>
                      <div className="activity-content">
                        <div className="activity-title">New 5-star review on Davos Suite</div>
                        <div className="activity-time">1 week ago</div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </section>

            <section id="my-chalets" className="dashboard-section">
              <h2>üè† My Chalets</h2>
              <button className="add-chalet-btn" onClick={() => setShowChaletModal(true)}>
                ‚ûï Add New Chalet
              </button>

              {chalets.length === 0 ? (
                <div className="chalet-list">No chalets found.</div>
              ) : (
                <div className="chalet-list">
                  {chalets.map((chalet) => (
                    <Property 
                      key={chalet.id} 
                      property={chalet}
                      variant="dashboard" // Use dashboard variant
                    />
                  ))}
                </div>
              )}
            </section>

            <section id="liked-chalets" className="dashboard-section">
              <h2>‚ù§Ô∏è Liked Chalets</h2>
              <p>Chalets you've liked.</p>
              <div className="chalet-list">No liked chalets.</div>
            </section>
          </div>
        </main>
      </div>
      <Footer />

      {/* Change Password Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Change Password</h3>
            <form onSubmit={handleChangePassword}>
              <input
                type="password"
                placeholder="Current Password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <div className="modal-actions">
                <button type="submit" className="save-btn">Save</button>
                <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Chalet Request Modal */}
      {showChaletModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Request to Add New Chalet</h3>
            <form onSubmit={handleChaletFormSubmit}>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Your Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  placeholder="Your Email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  placeholder="Phone Number"
                  value={form.phone}
                  onChange={(e) => setForm({ ...form, phone: e.target.value })}
                />
              </div>
              <div className="form-group">
                <textarea
                  placeholder="Message (Details about the chalet you want to add)"
                  rows="5"
                  value={form.message}
                  onChange={(e) => setForm({ ...form, message: e.target.value })}
                  required
                />
              </div>
              <div className="modal-actions">
                <button type="submit" className="save-btn">Send Request</button>
                <button type="button" className="cancel-btn" onClick={() => setShowChaletModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
