// Modified AllPropertiesPage.jsx

import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import Property from '../components/Property';
import '../styles/AllPropertypage.css';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import SearchFilter from '../components/SearchFilter';
import { FiChevronLeft, FiChevronRight, FiChevronsLeft, FiChevronsRight } from 'react-icons/fi';

const AllPropertiesPage = () => {
  const location = useLocation();
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [initialSearchTerm, setInitialSearchTerm] = useState('');
  
  // Refs for slider functionality
  const sliderRef = useRef(null);
  const [sliderPosition, setSliderPosition] = useState(0);
  const [visibleWidth, setVisibleWidth] = useState(0);

  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [propertiesPerPage] = useState(8);

  useEffect(() => {
    // Get search term from URL parameters
    const queryParams = new URLSearchParams(location.search);
    const searchParam = queryParams.get('search');
    if (searchParam) {
      setInitialSearchTerm(searchParam);
    }
  }, [location.search]);

  useEffect(() => {
    // Update visible width on window resize
    const updateVisibleWidth = () => {
      if (sliderRef.current) {
        setVisibleWidth(sliderRef.current.parentElement.offsetWidth);
      }
    };

    updateVisibleWidth();
    window.addEventListener('resize', updateVisibleWidth);
    
    return () => window.removeEventListener('resize', updateVisibleWidth);
  }, []);

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const apiUrl = 'http://localhost/real_estate_project/api/get_all_chalets.php';
        
        const response = await fetch(apiUrl, {
          method: 'GET',
          mode: 'cors',
          headers: {
            'Accept': 'application/json'
          }
        });
        
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        
        const data = await response.json();
        
        if (Array.isArray(data)) {
          setProperties(data);
          
          // If there's an initial search term, filter the properties accordingly
          if (initialSearchTerm) {
            const searchLower = initialSearchTerm.toLowerCase();
            const filtered = data.filter(property => 
              property.title?.toLowerCase().includes(searchLower) || 
              property.location?.toLowerCase().includes(searchLower) ||
              property.description?.toLowerCase().includes(searchLower)
            );
            setFilteredProperties(filtered);
          } else {
            setFilteredProperties(data);
          }
          
          // Reset to first page whenever properties are fetched
          setCurrentPage(1);
          // Reset slider position
          setSliderPosition(0);
        } else {
          throw new Error('Invalid data format received from API');
        }
      } catch (err) {
        console.error('Error fetching properties:', err);
        setError(`Error loading properties: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, [initialSearchTerm]);

  const updateFilteredProperties = (newFiltered) => {
    setFilteredProperties(newFiltered);
    setCurrentPage(1); // Reset to first page whenever filters are applied
    setSliderPosition(0); // Reset slider position
  };

  // Pagination logic
  const indexOfLastProperty = currentPage * propertiesPerPage;
  const indexOfFirstProperty = indexOfLastProperty - propertiesPerPage;
  
  // Make sure the current properties are calculated based on currentPage
  const currentProperties = filteredProperties.slice(indexOfFirstProperty, indexOfLastProperty);
  
  const totalPages = Math.ceil(filteredProperties.length / propertiesPerPage);

  // Page navigation handlers
  const handlePageClick = (pageNumber) => {
    console.log(`Changing to page ${pageNumber}`);
    setCurrentPage(pageNumber);
    setSliderPosition(0); // Reset slider position when changing pages
    window.scrollTo(0, 0); // Scroll to top when changing pages
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      console.log(`Moving to next page: ${currentPage + 1}`);
      setCurrentPage(prev => prev + 1);
      setSliderPosition(0); // Reset slider position
      window.scrollTo(0, 0);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      console.log(`Moving to previous page: ${currentPage - 1}`);
      setCurrentPage(prev => prev - 1);
      setSliderPosition(0); // Reset slider position
      window.scrollTo(0, 0);
    }
  };

  const handleFirstPage = () => {
    console.log('Moving to first page');
    setCurrentPage(1);
    setSliderPosition(0); // Reset slider position
    window.scrollTo(0, 0);
  };

  const handleLastPage = () => {
    console.log(`Moving to last page: ${totalPages}`);
    setCurrentPage(totalPages);
    setSliderPosition(0); // Reset slider position
    window.scrollTo(0, 0);
  };

  // Slider navigation handlers
  const slideLeft = () => {
    const newPosition = Math.min(0, sliderPosition + visibleWidth);
    setSliderPosition(newPosition);
  };

  const slideRight = () => {
    if (!sliderRef.current) return;
    
    const sliderWidth = sliderRef.current.scrollWidth;
    const maxPosition = -(sliderWidth - visibleWidth);
    const newPosition = Math.max(maxPosition, sliderPosition - visibleWidth);
    
    setSliderPosition(newPosition);
  };

  if (loading) return <div className="loading">Loading Chalets...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="all-properties-page">
      <Navbar />
      
      <div className="property-listings">
        <div className="listings-header">
          <SearchFilter 
            properties={properties} 
            setFilteredProperties={updateFilteredProperties}
            initialSearchTerm={initialSearchTerm}
          />
          
          <p className="properties-count">
            {filteredProperties.length} {filteredProperties.length === 1 ? 'property' : 'properties'} found
          </p>
        </div>

        {filteredProperties.length === 0 ? (
          <div className="no-results">No properties match your search criteria. Try adjusting your filters.</div>
        ) : (
          <>
            <div className="properties-container">
              {/* Slider navigation buttons */}
              <button 
                className="slider-nav-btn slider-nav-left"
                onClick={slideLeft}
                disabled={sliderPosition >= 0}
              >
                <FiChevronLeft />
              </button>
              
              <div className="slider-wrapper">
                <div 
                  ref={sliderRef}
                  className="properties-slider"
                  style={{
                    transform: `translateX(${sliderPosition}px)`,
                    transition: 'transform 0.3s ease'
                  }}
                >
                  {currentProperties.map(property => (
                    <div className="property-slide" key={property.id}>
                      <Property 
                        property={property}
                        variant="grid" 
                      />
                    </div>
                  ))}
                </div>
              </div>
              
              <button 
                className="slider-nav-btn slider-nav-right"
                onClick={slideRight}
                disabled={!sliderRef.current || sliderRef.current.scrollWidth - visibleWidth + sliderPosition <= 0}
              >
                <FiChevronRight />
              </button>
            </div>

            {/* Pagination - Using explicit handlers */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  className="page-btn"
                  onClick={handleFirstPage}
                  disabled={currentPage === 1}
                  title="First Page"
                >
                  <FiChevronsLeft />
                </button>
                
                <button 
                  className="page-btn"
                  onClick={handlePrevPage}
                  disabled={currentPage === 1}
                  title="Previous Page"
                >
                  <FiChevronLeft />
                </button>
                
                {Array.from({ length: Math.min(5, totalPages) }).map((_, index) => {
                  let pageNumber;
                  
                  if (totalPages <= 5) {
                    // If we have 5 or fewer pages, show all pages
                    pageNumber = index + 1;
                  } else {
                    // For more than 5 pages, show a window around current page
                    const offset = Math.max(0, Math.min(totalPages - 5, currentPage - 3));
                    pageNumber = index + 1 + offset;
                  }
                  
                  return (
                    <button
                      key={pageNumber}
                      onClick={() => handlePageClick(pageNumber)}
                      className={`page-btn ${currentPage === pageNumber ? 'active' : ''}`}
                    >
                      {pageNumber}
                    </button>
                  );
                })}
                
                <button 
                  className="page-btn"
                  onClick={handleNextPage}
                  disabled={currentPage === totalPages}
                  title="Next Page"
                >
                  <FiChevronRight />
                </button>
                
                <button 
                  className="page-btn"
                  onClick={handleLastPage}
                  disabled={currentPage === totalPages}
                  title="Last Page"
                >
                  <FiChevronsRight />
                </button>
              </div>
            )}
            
            {/* Display which properties are being shown */}
            <div className="pagination-info" style={{ textAlign: 'center', margin: '1rem 0' }}>
              Showing {indexOfFirstProperty + 1}-{Math.min(indexOfLastProperty, filteredProperties.length)} of {filteredProperties.length} properties
            </div>
          </>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default AllPropertiesPage;
