import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { 
  FiBarChart2, 
  FiUsers, 
  FiHome, 
  FiDollarSign, 
  FiTrendingUp, 
  FiTrendingDown,
  FiActivity,
  FiCheckCircle,
  FiClock,
  FiCalendar,
  FiFilter
} from 'react-icons/fi';
import { 
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  PieChart, Pie, Cell, LineChart, Line, AreaChart, Area
} from 'recharts';

function DashboardOverview() {
  // State for statistics
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalProperties: 0,
    totalSales: 0,
    averagePrice: 0,
    monthlyUsers: [],
    propertiesByType: [],
    propertiesByStatus: [],
    recentActivities: [],
    salesByMonth: [],
    propertyViews: [],
    isLoading: true
  });

  // State for filters
  const [timeRange, setTimeRange] = useState('year');

  // Colors for charts
  const COLORS = ['#e63946', '#457b9d', '#1d3557', '#a8dadc', '#f1faee', '#ffb703'];
  
  // Fetch data from your API
  useEffect(() => {
    const fetchData = async () => {
      try {
        // In a real app, you would fetch this data from your API
        // const response = await axios.get('http://localhost/real_estate_project/api/dashboard_stats.php');
        // setStats({ ...response.data, isLoading: false });
        
        // For demo purposes, we'll use mock data
        const mockData = generateMockData();
        setTimeout(() => {
          setStats({ ...mockData, isLoading: false });
        }, 1000);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setStats(prev => ({ ...prev, isLoading: false }));
      }
    };

    fetchData();
  }, [timeRange]);

  // Function to generate mock data for demonstration
  const generateMockData = () => {
    // Mock total statistics
    const totalUsers = Math.floor(Math.random() * 100) + 150;
    const totalProperties = Math.floor(Math.random() * 50) + 80;
    const totalSales = Math.floor(Math.random() * 30) + 40;
    const averagePrice = Math.floor(Math.random() * 500000) + 1000000;

    // Mock monthly users
    const monthlyUsers = [
      { month: 'Jan', users: Math.floor(Math.random() * 20) + 10 },
      { month: 'Feb', users: Math.floor(Math.random() * 20) + 12 },
      { month: 'Mar', users: Math.floor(Math.random() * 20) + 15 },
      { month: 'Apr', users: Math.floor(Math.random() * 20) + 18 },
      { month: 'May', users: Math.floor(Math.random() * 20) + 22 },
      { month: 'Jun', users: Math.floor(Math.random() * 20) + 25 },
      { month: 'Jul', users: Math.floor(Math.random() * 20) + 30 },
      { month: 'Aug', users: Math.floor(Math.random() * 20) + 28 },
      { month: 'Sep', users: Math.floor(Math.random() * 20) + 32 },
      { month: 'Oct', users: Math.floor(Math.random() * 20) + 35 },
      { month: 'Nov', users: Math.floor(Math.random() * 20) + 38 },
      { month: 'Dec', users: Math.floor(Math.random() * 20) + 40 }
    ];

    // Mock properties by type
    const propertiesByType = [
      { name: 'Chalet', value: Math.floor(Math.random() * 20) + 30 },
      { name: 'Apartment', value: Math.floor(Math.random() * 20) + 25 },
      { name: 'Villa', value: Math.floor(Math.random() * 15) + 15 },
      { name: 'Penthouse', value: Math.floor(Math.random() * 10) + 10 }
    ];

    // Mock properties by status
    const propertiesByStatus = [
      { name: 'Available', value: Math.floor(Math.random() * 20) + 30 },
      { name: 'Pending', value: Math.floor(Math.random() * 15) + 20 },
      { name: 'Sold', value: Math.floor(Math.random() * 10) + 15 }
    ];

    // Mock recent activities
    const activities = [
      { type: 'User Registration', content: 'New user John Doe registered', timestamp: '2 hours ago' },
      { type: 'Property Added', content: 'New chalet added in Zermatt', timestamp: '5 hours ago' },
      { type: 'Property Sold', content: 'Villa in St. Moritz marked as sold', timestamp: '1 day ago' },
      { type: 'User Update', content: 'User profile updated by admin', timestamp: '2 days ago' },
      { type: 'Property Update', content: 'Price reduced for property #45', timestamp: '3 days ago' }
    ];

    // Mock sales by month
    const salesByMonth = [
      { month: 'Jan', sales: Math.floor(Math.random() * 5) + 1, amount: Math.floor(Math.random() * 5000000) + 1000000 },
      { month: 'Feb', sales: Math.floor(Math.random() * 5) + 2, amount: Math.floor(Math.random() * 5000000) + 1200000 },
      { month: 'Mar', sales: Math.floor(Math.random() * 5) + 3, amount: Math.floor(Math.random() * 5000000) + 1500000 },
      { month: 'Apr', sales: Math.floor(Math.random() * 5) + 2, amount: Math.floor(Math.random() * 5000000) + 1300000 },
      { month: 'May', sales: Math.floor(Math.random() * 5) + 4, amount: Math.floor(Math.random() * 5000000) + 1800000 },
      { month: 'Jun', sales: Math.floor(Math.random() * 5) + 5, amount: Math.floor(Math.random() * 5000000) + 2000000 },
      { month: 'Jul', sales: Math.floor(Math.random() * 5) + 3, amount: Math.floor(Math.random() * 5000000) + 1600000 },
      { month: 'Aug', sales: Math.floor(Math.random() * 5) + 4, amount: Math.floor(Math.random() * 5000000) + 1900000 },
      { month: 'Sep', sales: Math.floor(Math.random() * 5) + 5, amount: Math.floor(Math.random() * 5000000) + 2100000 },
      { month: 'Oct', sales: Math.floor(Math.random() * 5) + 6, amount: Math.floor(Math.random() * 5000000) + 2300000 },
      { month: 'Nov', sales: Math.floor(Math.random() * 5) + 5, amount: Math.floor(Math.random() * 5000000) + 2200000 },
      { month: 'Dec', sales: Math.floor(Math.random() * 5) + 4, amount: Math.floor(Math.random() * 5000000) + 2000000 }
    ];

    // Mock property views
    const propertyViews = [
      { date: '2023-01-15', views: Math.floor(Math.random() * 50) + 100 },
      { date: '2023-02-15', views: Math.floor(Math.random() * 50) + 120 },
      { date: '2023-03-15', views: Math.floor(Math.random() * 50) + 150 },
      { date: '2023-04-15', views: Math.floor(Math.random() * 50) + 180 },
      { date: '2023-05-15', views: Math.floor(Math.random() * 50) + 220 },
      { date: '2023-06-15', views: Math.floor(Math.random() * 50) + 250 },
      { date: '2023-07-15', views: Math.floor(Math.random() * 50) + 280 },
      { date: '2023-08-15', views: Math.floor(Math.random() * 50) + 260 },
      { date: '2023-09-15', views: Math.floor(Math.random() * 50) + 290 },
      { date: '2023-10-15', views: Math.floor(Math.random() * 50) + 320 },
      { date: '2023-11-15', views: Math.floor(Math.random() * 50) + 350 },
      { date: '2023-12-15', views: Math.floor(Math.random() * 50) + 380 }
    ];

    return {
      totalUsers,
      totalProperties,
      totalSales,
      averagePrice,
      monthlyUsers,
      propertiesByType,
      propertiesByStatus,
      recentActivities: activities,
      salesByMonth,
      propertyViews
    };
  };

  // Function to format currency
  const formatCurrency = (value) => {
    return new Intl.NumberFormat('en-CH', { 
      style: 'currency', 
      currency: 'CHF',
      maximumFractionDigits: 0
    }).format(value);
  };

  // Activity icon based on type
  const getActivityIcon = (type) => {
    switch(type) {
      case 'User Registration':
        return <FiUsers className="activity-icon-inner" />;
      case 'Property Added':
        return <FiHome className="activity-icon-inner" />;
      case 'Property Sold':
        return <FiCheckCircle className="activity-icon-inner" />;
      case 'User Update':
        return <FiUsers className="activity-icon-inner" />;
      case 'Property Update':
        return <FiHome className="activity-icon-inner" />;
      default:
        return <FiActivity className="activity-icon-inner" />;
    }
  };

  // Dashboard filters
  const handleFilterChange = (e) => {
    setTimeRange(e.target.value);
    setStats(prev => ({ ...prev, isLoading: true }));
  };

  return (
    <div className="dashboard-overview">
      {/* Dashboard Title */}
      <div className="dashboard-section">
        <div className="section-header">
          <div className="section-title-row">
            <h2 className="section-title">
              <FiBarChart2 className="section-icon" />
              Dashboard Overview
            </h2>
            <div className="dashboard-filters">
              <div className="filter-container">
                <FiFilter className="filter-icon" />
                <select 
                  value={timeRange} 
                  onChange={handleFilterChange}
                  className="time-range-select"
                >
                  <option value="week">Last Week</option>
                  <option value="month">Last Month</option>
                  <option value="quarter">Last Quarter</option>
                  <option value="year">Last Year</option>
                </select>
              </div>
            </div>
          </div>
          <p className="section-subtitle">
            Key metrics and statistics for your real estate platform
          </p>
        </div>

        {/* Stats Overview Cards */}
        <div className="stats-grid">
          {/* Total Users Card */}
          <div className="stat-card">
            <div className="stat-label">
              <FiUsers className="stat-icon" /> Total Users
            </div>
            <div className="stat-value">{stats.totalUsers}</div>
            <div className="stat-trend positive">
              <FiTrendingUp /> +12.5%
            </div>
          </div>

          {/* Total Properties Card */}
          <div className="stat-card">
            <div className="stat-label">
              <FiHome className="stat-icon" /> Total Properties
            </div>
            <div className="stat-value">{stats.totalProperties}</div>
            <div className="stat-trend positive">
              <FiTrendingUp /> +8.3%
            </div>
          </div>

          {/* Total Sales Card */}
          <div className="stat-card">
            <div className="stat-label">
              <FiCheckCircle className="stat-icon" /> Properties Sold
            </div>
            <div className="stat-value">{stats.totalSales}</div>
            <div className="stat-trend positive">
              <FiTrendingUp /> +15.2%
            </div>
          </div>

          {/* Average Price Card */}
          <div className="stat-card">
            <div className="stat-label">
              <FiDollarSign className="stat-icon" /> Average Price
            </div>
            <div className="stat-value">{formatCurrency(stats.averagePrice)}</div>
            <div className="stat-trend negative">
              <FiTrendingDown /> -2.1%
            </div>
          </div>
        </div>

        {/* Charts Section */}
        <div className="charts-container">
          <div className="charts-row">
            {/* User Registration Chart */}
            <div className="chart-card">
              <h3 className="chart-title">
                <FiUsers className="chart-icon" /> User Registrations
              </h3>
              <div className="chart-container">
                <ResponsiveContainer width="100%" height={250}>
                  <AreaChart
                    data={stats.monthlyUsers}
                    margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis />
                    <Tooltip formatter={(value) => [`${value} Users`, 'Registrations']} />
                    <Area 
                      type="monotone" 
                      dataKey="users" 
                      stroke="#e63946" 
                      fill="#e63946" 
                      fillOpacity={0.3} 
                    />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Property Sales Chart */}
            <div className="chart-card">
              <h3 className="chart-title">
                <FiDollarSign className="chart-icon" /> Sales by Month
              </h3>
              <div className="chart-container">
                <ResponsiveContainer width="100%" height={250}>
                  <BarChart
                    data={stats.salesByMonth}
                    margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis yAxisId="left" orientation="left" stroke="#457b9d" />
                    <YAxis yAxisId="right" orientation="right" stroke="#e63946" />
                    <Tooltip formatter={(value, name) => {
                      if (name === 'amount') return [formatCurrency(value), 'Sales Amount'];
                      return [`${value} Properties`, 'Properties Sold'];
                    }} />
                    <Legend />
                    <Bar 
                      yAxisId="left" 
                      dataKey="sales" 
                      name="Properties Sold" 
                      fill="#457b9d" 
                    />
                    <Bar 
                      yAxisId="right" 
                      dataKey="amount" 
                      name="Sales Amount" 
                      fill="#e63946" 
                    />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>

          <div className="charts-row">
            {/* Properties by Type */}
            <div className="chart-card">
              <h3 className="chart-title">
                <FiHome className="chart-icon" /> Properties by Type
              </h3>
              <div className="chart-container pie-chart-container">
                <ResponsiveContainer width="100%" height={250}>
                  <PieChart>
                    <Pie
                      data={stats.propertiesByType}
                      cx="50%"
                      cy="50%"
                      labelLine={false}
                      outerRadius={80}
                      fill="#8884d8"
                      dataKey="value"
                      label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                    >
                      {stats.propertiesByType.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value) => [`${value} Properties`, 'Count']} />
                  </PieChart>
                </ResponsiveContainer>
              </div>
            </div>

            {/* Property Views Trend */}
            <div className="chart-card">
              <h3 className="chart-title">
                <FiActivity className="chart-icon" /> Property View Trends
              </h3>
              <div className="chart-container">
                <ResponsiveContainer width="100%" height={250}>
                  <LineChart
                    data={stats.propertyViews}
                    margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
                  >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis 
                      dataKey="date" 
                      tickFormatter={(value) => {
                        const date = new Date(value);
                        return date.toLocaleDateString('en-US', { month: 'short' });
                      }}
                    />
                    <YAxis />
                    <Tooltip 
                      formatter={(value) => [`${value} Views`, 'Property Views']}
                      labelFormatter={(value) => {
                        const date = new Date(value);
                        return date.toLocaleDateString('en-US', { 
                          year: 'numeric', 
                          month: 'long'
                        });
                      }}
                    />
                    <Line 
                      type="monotone" 
                      dataKey="views" 
                      stroke="#1d3557" 
                      activeDot={{ r: 8 }} 
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Activity and Properties by Status */}
        <div className="dashboard-bottom-row">
          <div className="recent-activity-container">
            <h3 className="section-subtitle">
              <FiActivity className="section-icon" /> Recent Activity
            </h3>
            <div className="activity-list">
              {stats.recentActivities.map((activity, index) => (
                <div className="activity-item" key={index}>
                  <div className="activity-icon">
                    {getActivityIcon(activity.type)}
                  </div>
                  <div className="activity-content">
                    <p className="activity-text">{activity.content}</p>
                    <p className="activity-time">
                      <FiClock className="time-icon" /> {activity.timestamp}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="property-status-container">
            <h3 className="section-subtitle">
              <FiHome className="section-icon" /> Properties by Status
            </h3>
            <div className="chart-container status-chart">
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={stats.propertiesByStatus}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                    label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                  >
                    {stats.propertiesByStatus.map((entry, index) => (
                      <Cell 
                        key={`cell-${index}`} 
                        fill={
                          entry.name === 'Available' ? '#4caf50' : 
                          entry.name === 'Pending' ? '#ff9800' : 
                          '#e63946'
                        } 
                      />
                    ))}
                  </Pie>
                  <Tooltip formatter={(value) => [`${value} Properties`, 'Count']} />
                </PieChart>
              </ResponsiveContainer>
              <div className="status-legend">
                <div className="status-legend-item">
                  <div className="status-dot available"></div>
                  <span>Available</span>
                </div>
                <div className="status-legend-item">
                  <div className="status-dot pending"></div>
                  <span>Pending</span>
                </div>
                <div className="status-legend-item">
                  <div className="status-dot sold"></div>
                  <span>Sold</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DashboardOverview;
