import React, { useState, useEffect } from 'react';

const ChaletMapEmbed = ({ latitude, longitude, title, address, city, canton }) => {
  const [mapSrc, setMapSrc] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Build the map URL using the simple but effective method
    const buildMapUrl = () => {
      if (latitude && longitude) {
        // Use coordinates directly - this method works perfectly for showing exact location with pin
        console.log(`Using coordinates for ${title}: ${latitude}, ${longitude}`);
        // This creates a map centered on the coordinates with a pin
        return `https://maps.google.com/maps?q=${latitude},${longitude}&hl=en&z=14&output=embed`;
      } else if (address && city) {
        // Use full address
        const query = `${address}, ${city}, ${canton || 'Switzerland'}`;
        console.log(`Using address for ${title}: ${query}`);
        const encodedQuery = encodeURIComponent(query);
        return `https://maps.google.com/maps?q=${encodedQuery}&hl=en&z=14&output=embed`;
      } else if (city) {
        // Use city only
        const query = `${city}, ${canton || 'Switzerland'}`;
        console.log(`Using city for ${title}: ${query}`);
        const encodedQuery = encodeURIComponent(query);
        return `https://maps.google.com/maps?q=${encodedQuery}&hl=en&z=12&output=embed`;
      } else {
        // Ultimate fallback
        console.log(`Using fallback for ${title}: Switzerland`);
        return `https://maps.google.com/maps?q=Switzerland&hl=en&z=8&output=embed`;
      }
    };

    const url = buildMapUrl();
    setMapSrc(url);
    setIsLoading(false);
  }, [latitude, longitude, address, city, canton, title]);

  const handleIframeLoad = () => {
    setIsLoading(false);
  };

  const handleIframeError = () => {
    setIsLoading(false);
    console.error('Failed to load Google Maps');
  };

  // Build the Google Maps search URL for the "Open in Maps" link
  const buildMapsUrl = () => {
    if (latitude && longitude) {
      return `https://www.google.com/maps/search/?api=1&query=${latitude},${longitude}`;
    } else if (address && city) {
      const query = `${address}, ${city}, ${canton || 'Switzerland'}`;
      return `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(query)}`;
    } else if (city) {
      const query = `${city}, ${canton || 'Switzerland'}`;
      return `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(query)}`;
    } else {
      return `https://www.google.com/maps/search/?api=1&query=Switzerland`;
    }
  };

  return (
    <div className="chalet-map-container">
      {isLoading && (
        <div className="map-loading">
          <div className="loading-spinner"></div>
          <p>Loading map for {title}...</p>
        </div>
      )}
      
      <iframe
        src={mapSrc}
        width="100%"
        height="300"
        style={{
          border: 0,
          borderRadius: '12px',
          display: isLoading ? 'none' : 'block'
        }}
        allowFullScreen=""
        loading="lazy"
        referrerPolicy="no-referrer-when-downgrade"
        title={`Map showing location of ${title || 'chalet'}`}
        onLoad={handleIframeLoad}
        onError={handleIframeError}
      />
      
      {/* Link to open in Google Maps */}
      <div className="map-link">
        <a
          href={buildMapsUrl()}
          target="_blank"
          rel="noopener noreferrer"
          className="open-maps-link"
        >
          üìç Open in Google Maps
        </a>
      </div>
    </div>
  );
};

export default ChaletMapEmbed;
