import React, { useState, useEffect } from 'react';

const ChaletMapEmbed = ({ latitude, longitude, title, address, city, canton }) => {
  const [mapSrc, setMapSrc] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Build proper Google Maps embed URL
    const buildMapUrl = () => {
      if (latitude && longitude) {
        // Use coordinates directly with proper embed format
        console.log(`Using coordinates for ${title}: ${latitude}, ${longitude}`);
        return `https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d2000!2d${longitude}!3d${latitude}!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2s!4v${Date.now()}`;
      } else if (address && city) {
        // Use full address
        const query = `${address}, ${city}, ${canton || 'Switzerland'}`;
        console.log(`Using address for ${title}: ${query}`);
        const encodedQuery = encodeURIComponent(query);
        return `https://www.google.com/maps/embed/v1/place?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dw2iVOGIU5EBhY&q=${encodedQuery}`;
      } else if (city) {
        // Use city only
        const query = `${city}, ${canton || 'Switzerland'}`;
        console.log(`Using city for ${title}: ${query}`);
        const encodedQuery = encodeURIComponent(query);
        return `https://www.google.com/maps/embed/v1/place?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dw2iVOGIU5EBhY&q=${encodedQuery}`;
      } else {
        // Ultimate fallback
        console.log(`Using fallback for ${title}: Switzerland`);
        return `https://www.google.com/maps/embed/v1/place?key=AIzaSyBFw0Qbyq9zTFTd-tUY6dw2iVOGIU5EBhY&q=Switzerland`;
      }
    };

    const url = buildMapUrl();
    setMapSrc(url);
    setIsLoading(false);
  }, [latitude, longitude, address, city, canton, title]);

  const handleIframeLoad = () => {
    setIsLoading(false);
  };

  const handleIframeError = () => {
    setIsLoading(false);
    console.error('Failed to load Google Maps');
  };

  // Build the Google Maps search URL for the "Open in Maps" link
  const buildMapsUrl = () => {
    let query = '';
    
    if (latitude && longitude) {
      query = `${latitude},${longitude}`;
    } else if (address && city) {
      query = `${address}, ${city}, ${canton || 'Switzerland'}`;
    } else if (city) {
      query = `${city}, ${canton || 'Switzerland'}`;
    } else {
      query = 'Switzerland';
    }
    
    return `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(query)}`;
  };

  return (
    <div className="chalet-map-container">
      {isLoading && (
        <div className="map-loading">
          <div className="loading-spinner"></div>
          <p>Loading map...</p>
        </div>
      )}
      
      <iframe
        src={mapSrc}
        width="100%"
        height="300"
        style={{
          border: 0,
          borderRadius: '12px',
          display: isLoading ? 'none' : 'block'
        }}
        allowFullScreen=""
        loading="lazy"
        referrerPolicy="no-referrer-when-downgrade"
        title={`Map showing location of ${title || 'chalet'}`}
        onLoad={handleIframeLoad}
        onError={handleIframeError}
      />
      
      {/* Link to open in Google Maps */}
      <div className="map-link">
        <a
          href={buildMapsUrl()}
          target="_blank"
          rel="noopener noreferrer"
          className="open-maps-link"
        >
          üìç Open in Google Maps
        </a>
      </div>
    </div>
  );
};

export default ChaletMapEmbed;
