import React from 'react';
import { Link } from 'react-router-dom';
import { FiMapPin, FiHome, FiLayers, FiDroplet, FiClock } from 'react-icons/fi';
const Property = ({ 
  property, 
  variant = 'default' // 'default' or 'detailed'
}) => {
  return (
    <Link to={`/chalet/${property.id}`} className="property-card-link">
      <div className="property-card">
        <div className="image-container">
          <img
            src={property.main_image}
            alt={property.title || property.address}
            className="property-image"
            loading="lazy"
          />
          <div className="property-badge">{property.status}</div>
        </div>
        
        <div className="property-details">
          <h3 className="property-title">{property.title}</h3>
          
          <span className="days-on-market">
            <FiClock /> {property.days_on_market} days listed
          </span>
          
          <div className="property-price">
            CHF {Number(property.price).toLocaleString()}
          </div>
          
          <div className="property-address">
            <FiMapPin /> {property.address}, {property.city}
          </div>
          
          <div className="property-meta">
            <span><FiHome /> {property.beds} beds</span>
            <span><FiDroplet /> {property.baths} baths</span>
            <span><FiLayers /> {property.sqft.toLocaleString()} sqft</span>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default Property;
