import React, { useState, useEffect } from 'react';
import '../styles/Navbar.css';
import { FiHome, FiSearch, FiUser, FiInfo, FiLogIn } from 'react-icons/fi';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [activeLink, setActiveLink] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 10) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav className={`navbar ${scrolled ? 'scrolled' : ''}`}>
      <div className="navbar-logo">
        <span className="navbar-logo-icon">üèîÔ∏è</span>
        Chalets Suisses
      </div>

      <div 
        className={`menu-toggle ${isMenuOpen ? 'active' : ''}`} 
        onClick={toggleMenu}
        aria-label="Toggle menu"
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
        <li>
          <a 
            href="#home" 
            className={activeLink === 'home' ? 'active' : ''}
            onClick={() => {
              setActiveLink('home');
              closeMenu();
            }}
          >
            <FiHome /> Home
          </a>
        </li>
        <li>
          <a 
            href="#buy" 
            className={activeLink === 'buy' ? 'active' : ''}
            onClick={() => {
              setActiveLink('buy');
              closeMenu();
            }}
          >
            <FiSearch /> Buy Chalets
          </a>
        </li>
        <li>
          <a 
            href="#rent" 
            className={activeLink === 'rent' ? 'active' : ''}
            onClick={() => {
              setActiveLink('rent');
              closeMenu();
            }}
          >
            <FiSearch /> Rent Chalets
          </a>
        </li>
        <li>
          <a 
            href="#about" 
            className={activeLink === 'about' ? 'active' : ''}
            onClick={() => {
              setActiveLink('about');
              closeMenu();
            }}
          >
            <FiInfo /> About Us
          </a>
        </li>
        <li>
          <a 
            href="#contact" 
            className={activeLink === 'contact' ? 'active' : ''}
            onClick={() => {
              setActiveLink('contact');
              closeMenu();
            }}
          >
            <FiUser /> Contact
          </a>
        </li>

        <div className="navbar-auth">
        <a href="#login" className="login-btn" onClick={closeMenu}>
            <FiLogIn /> Login / Sign Up
          </a>
        </div>
      </ul>
    </nav>
  );
};

export default Navbar;
