import React, { useState, useEffect } from 'react';

const ChaletMapEmbed = ({ chalet }) => {
  const [mapSrc, setMapSrc] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [customSearch, setCustomSearch] = useState('');

  useEffect(() => {
    if (!chalet) {
      setIsLoading(false);
      return;
    }

    const buildMapUrl = () => {
      // Default to Switzerland view
      let center = '46.8182,8.2275';
      let zoom = '8';
      let query = 'Switzerland';

      if (searchQuery) {
        // Use the search query if provided
        query = encodeURIComponent(searchQuery);
      } else if (chalet.city) {
        // Otherwise use the chalet's city if available
        query = encodeURIComponent(`${chalet.city}${chalet.canton ? ', ' + chalet.canton : ''}, Switzerland`);
      }

      return `https://www.google.com/maps/embed/v1/place?key=YOUR_API_KEY&q=${query}&center=${center}&zoom=${zoom}`;
    };

    const url = buildMapUrl();
    setMapSrc(url);
    setIsLoading(false);
  }, [chalet, searchQuery]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (customSearch.trim()) {
      setSearchQuery(customSearch);
    }
  };

  const handleReset = () => {
    setCustomSearch('');
    setSearchQuery('');
  };

  if (!chalet) {
    return (
      <div className="chalet-map-container">
        <p>Map not available</p>
      </div>
    );
  }

  return (
    <div className="chalet-map-container">
      {/* Search controls */}
      <div className="map-search-controls">
        <form onSubmit={handleSearch} className="map-search-form">
          <input
            type="text"
            value={customSearch}
            onChange={(e) => setCustomSearch(e.target.value)}
            placeholder="Search for a city or address in Switzerland"
            className="map-search-input"
          />
          <button type="submit" className="map-search-button">
            Search
          </button>
          <button 
            type="button" 
            onClick={handleReset}
            className="map-reset-button"
          >
            Reset
          </button>
        </form>
      </div>

      {/* Loading indicator */}
      {isLoading && (
        <div className="map-loading">
          <div className="loading-spinner"></div>
          <p>Loading map for {chalet.title}...</p>
        </div>
      )}
      
      {/* Map iframe */}
      <iframe
        src={mapSrc}
        width="100%"
        height="400"
        className="map-iframe"
        allowFullScreen=""
        loading="lazy"
        referrerPolicy="no-referrer-when-downgrade"
        title={`Map showing location of ${chalet.title || 'chalet'}`}
      />

      {/* Add some basic styling */}
      <style jsx>{`
        .chalet-map-container {
          width: 100%;
          margin: 20px 0;
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 4px 8px rgba(0,0,0,0.1);
        }
        
        .map-search-controls {
          padding: 15px;
          background: #f5f5f5;
          border-bottom: 1px solid #ddd;
        }
        
        .map-search-form {
          display: flex;
          gap: 10px;
        }
        
        .map-search-input {
          flex: 1;
          padding: 8px 12px;
          border: 1px solid #ddd;
          border-radius: 4px;
          font-size: 14px;
        }
        
        .map-search-button, .map-reset-button {
          padding: 8px 15px;
          border: none;
          border-radius: 4px;
          cursor: pointer;
          font-size: 14px;
        }
        
        .map-search-button {
          background-color: #4CAF50;
          color: white;
        }
        
        .map-reset-button {
          background-color: #f44336;
          color: white;
        }
        
        .map-iframe {
          border: 0;
          height: 400px;
        }
        
        .map-loading {
          text-align: center;
          padding: 40px;
          background-color: #f5f5f5;
        }
        
        .loading-spinner {
          width: 20px;
          height: 20px;
          border: 2px solid #ddd;
          border-top: 2px solid #333;
          border-radius: 50%;
          animation: spin 1s linear infinite;
          margin: 0 auto 10px;
        }
        
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default ChaletMapEmbed;
