import { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // <-- Add this
import '../styles/Login.css';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [isAdmin, setIsAdmin] = useState(null);
  const navigate = useNavigate(); // <-- Add this

  const handleLogin = async () => {
    const response = await fetch("http://localhost/REAL_ESTATE_PROJECT/api/login.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();

    if (data.success) {
      setMessage("Login successful!");
      setIsAdmin(data.is_admin);
      
      localStorage.setItem('user', JSON.stringify({
        isAdmin: data.is_admin,
        email: email,
      }));

      // Redirect based on role
      if (data.is_admin) {
        navigate('/admin-dashboard');
      } else {
        navigate('/user-dashboard');
      }

    } else {
      setMessage(data.message || "Login failed.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-content">
        <h2 className="login-title">Welcome Back</h2>
        <p className="login-subtitle">Sign in to access your account</p>

        <div className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
              type="email" 
              id="email" 
              className="login-input"
              placeholder="Enter your email" 
              onChange={e => setEmail(e.target.value)} 
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              className="login-input"
              placeholder="Enter your password" 
              onChange={e => setPassword(e.target.value)} 
            />
          </div>

          <button className="login-btn hero-btn hero-btn-primary" onClick={handleLogin}>
            Login
          </button>

          {message && (
            <p className={`login-message ${isAdmin !== null ? 'success' : 'error'}`}>
              {message}
            </p>
          )}

          {isAdmin !== null && (
            <p className="login-role">
              You are logged in as {isAdmin ? "an Admin" : "a User"}
            </p>
          )}
        </div>

        <div className="login-footer">
          <p>Don't have an account? <a href="/register" className="login-link">Sign up</a></p>
          <a href="/forgot-password" className="login-link">Forgot password?</a>
        </div>
      </div>

      <div className="login-image-container">
        <div className="login-image"></div>
      </div>
    </div>
  );
}

export default Login;
