// UserDashboard.jsx - With favorites functionality
import { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Property from '../components/Property';
import '../styles/UserDashboard.css';

function UserDashboard() {
  const [chalets, setChalets] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [showChaletModal, setShowChaletModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [favoritesLoading, setFavoritesLoading] = useState(true);

  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });

  useEffect(() => {
    // Fetch user's chalets
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_chalets.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Unauthorized');
        return res.json();
      })
      .then(data => {
        setChalets(data.chalets || data);
        setLoading(false);
      })
      .catch(() => {
        setChalets([]);
        setLoading(false);
      });

    // Fetch user's favorite chalets
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_favorites.php', {
      credentials: 'include',
    })
      .then(res => {
        if (!res.ok) throw new Error('Unauthorized');
        return res.json();
      })
      .then(data => {
        if (data.success) {
          setFavorites(data.favorites || []);
        }
        setFavoritesLoading(false);
      })
      .catch(() => {
        setFavorites([]);
        setFavoritesLoading(false);
      });
  }, []);

  const handleChangePassword = (e) => {
    e.preventDefault();
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/update_password.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ oldPassword, newPassword }),
    })
      .then(res => res.json())
      .then(data => {
        alert(data.message || 'Password updated!');
        setShowModal(false);
        setOldPassword('');
        setNewPassword('');
      })
      .catch(() => {
        alert('Error updating password.');
      });
  };

  const handleChaletFormSubmit = (e) => {
    e.preventDefault();

    // Send the form data to an API or email service
    fetch('http://localhost/REAL_ESTATE_PROJECT/api/send_chalet_request.php', {
      method: 'POST',
      credentials: 'include',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    })
      .then((res) => res.json())
      .then((data) => {
        alert('Request submitted! We will contact you soon.');
        setShowChaletModal(false);
        setForm({ name: '', email: '', phone: '', message: '' });
      })
      .catch(() => {
        alert('Error submitting request.');
      });
  };

  // Handle favorite changes
  const handleFavoriteChange = (chaletId, isFavorite) => {
    if (!isFavorite) {
      // If removed from favorites, update the favorites list
      setFavorites(favorites.filter(chalet => chalet.id !== chaletId));
    } else {
      // If a favorite was added, refresh the favorites list
      fetch('http://localhost/REAL_ESTATE_PROJECT/api/get_user_favorites.php', {
        credentials: 'include',
      })
        .then(res => res.json())
        .then(data => {
          if (data.success) {
            setFavorites(data.favorites || []);
          }
        })
        .catch(error => {
          console.error('Error refreshing favorites:', error);
        });
    }
  };

  return (
    <div className="user-dashboard-container">
      <Navbar />
      <div className="user-dashboard-scroll">
        <aside className="user-sidebar">
          <div className="sidebar-header">
            <h2 className="sidebar-title"><span className="gradient-text">User</span> Dashboard</h2>
          </div>
          <nav className="sidebar-nav">
            <ul>
              <li><a href="#home" className="nav-link">🏡 Home</a></li>
              <li><a href="#my-chalets" className="nav-link">🏠 My Chalets</a></li>
              <li><a href="#liked-chalets" className="nav-link">❤️ Liked Chalets</a></li>
            </ul>
          </nav>
        </aside>

        <main className="user-main">
          <header className="user-header">
            <div className="header-content">
              <div>
                <h1>Welcome, <span className="gradient-text">{(() => {
                  // Get user from localStorage
                  const userData = localStorage.getItem('user');
                  
                  if (!userData) return 'User';
                  
                  try {
                    // Parse user data
                    const user = JSON.parse(userData);
                    
                    // Check if email exists
                    if (!user.email) return 'User';
                    
                    // Extract username from email (part before @)
                    const emailParts = user.email.split('@');
                    if (emailParts.length > 1) {
                      return emailParts[0];
                    }
                    
                    return 'User';
                  } catch (error) {
                    // If any error occurs, fallback to 'User'
                    return 'User';
                  }
                })()}</span></h1>
                <p>{new Date().toLocaleDateString()}</p>
              </div>
              <button className="edit-btn" onClick={() => setShowModal(true)}>✏️ Edit Profile</button>
            </div>
          </header>

          <div className="user-content">
            <section id="home" className="dashboard-section">
              <h2>📊 Dashboard Overview</h2>
              
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Total Chalets</h3>
                  <p>{chalets.length}</p>
                </div>
                
                <div className="stat-card">
                  <h3>Liked Chalets</h3>
                  <p>{favorites.length}</p>
                </div>
                
                <div className="stat-card">
                  <h3>Last Login</h3>
                  <p>{new Date().toLocaleDateString()}</p>
                </div>
              </div>
            </section>

            <section id="my-chalets" className="dashboard-section">
              <h2>🏠 My Chalets</h2>
              <button className="add-chalet-btn" onClick={() => setShowChaletModal(true)}>
                ➕ Add New Chalet
              </button>

              {loading ? (
                <div className="loading-spinner">Loading your chalets...</div>
              ) : chalets.length === 0 ? (
                <div className="no-results">No chalets found. Add your first chalet!</div>
              ) : (
                <div className="chalet-list">
                  {chalets.map((chalet) => (
                    <Property 
                      key={chalet.id} 
                      property={chalet}
                      variant="dashboard"
                      onFavoriteChange={handleFavoriteChange}
                    />
                  ))}
                </div>
              )}
            </section>

            <section id="liked-chalets" className="dashboard-section">
              <h2>❤️ Liked Chalets</h2>
              <p>Chalets you've liked.</p>

              {favoritesLoading ? (
                <div className="loading-spinner">Loading your favorites...</div>
              ) : favorites.length === 0 ? (
                <div className="no-results">No liked chalets. Like some chalets to see them here!</div>
              ) : (
                <div className="chalet-list">
                  {favorites.map((chalet) => (
                    <Property 
                      key={chalet.id} 
                      property={chalet}
                      variant="dashboard"
                      onFavoriteChange={handleFavoriteChange}
                    />
                  ))}
                </div>
              )}
            </section>
          </div>
        </main>
      </div>
      <Footer />

      {/* Change Password Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Change Password</h3>
            <form onSubmit={handleChangePassword}>
              <input
                type="password"
                placeholder="Current Password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <div className="modal-actions">
                <button type="submit" className="save-btn">Save</button>
                <button type="button" className="cancel-btn" onClick={() => setShowModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Add Chalet Request Modal */}
      {showChaletModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Request to Add New Chalet</h3>
            <form onSubmit={handleChaletFormSubmit}>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Your Name"
                  value={form.name}
                  onChange={(e) => setForm({ ...form, name: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="email"
                  placeholder="Your Email"
                  value={form.email}
                  onChange={(e) => setForm({ ...form, email: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <input
                  type="tel"
                  placeholder="Phone Number"
                  value={form.phone}
                  onChange={(e) => setForm({ ...form, phone: e.target.value })}
                />
              </div>
              <div className="form-group">
                <textarea
                  placeholder="Message (Details about the chalet you want to add)"
                  rows="5"
                  value={form.message}
                  onChange={(e) => setForm({ ...form, message: e.target.value })}
                  required
                />
              </div>
              <div className="modal-actions">
                <button type="submit" className="save-btn">Send Request</button>
                <button type="button" className="cancel-btn" onClick={() => setShowChaletModal(false)}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
