import React, { useState, useEffect } from 'react';
import { FiEdit, FiTrash2, FiPlus, FiX, FiSave, FiSearch, FiRefreshCw, FiChevronDown, 
  FiChevronLeft, FiChevronRight, FiChevronsLeft, FiChevronsRight, FiArchive, FiRotateCcw, FiTrash, FiFilter } from 'react-icons/fi';

function PropertyModal({ isOpen, onClose, property, onSave }) {
  const [formData, setFormData] = useState({
    title: '',
    location: '',
    price: '',
    status: 'listed'
  });
  const [formErrors, setFormErrors] = useState({});

  // When the modal opens, set form data to property data if editing
  useEffect(() => {
    if (property) {
      setFormData({
        title: property.title || '',
        location: property.location || '',
        price: property.price || '',
        status: property.status || 'listed'
      });
    } else {
      // Reset form for new property
      setFormData({
        title: '',
        location: '',
        price: '',
        status: 'listed'
      });
    }
    // Clear errors when modal opens
    setFormErrors({});
  }, [property, isOpen]);

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const validateForm = () => {
    const errors = {};
    
    if (!formData.title.trim()) {
      errors.title = 'Title is required';
    }
    
    if (!formData.location.trim()) {
      errors.location = 'Location is required';
    }
    
    if (!formData.price.trim()) {
      errors.price = 'Price is required';
    } else if (isNaN(formData.price.replace(/[$,]/g, ''))) {
      errors.price = 'Price must be a valid number';
    }
    
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      onSave({
        ...formData,
        id: property ? property.id : null
      });
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>{property ? `Edit Property: ${property.title}` : 'Add New Property'}</h3>
          <button className="close-btn" onClick={onClose}>
            <FiX />
          </button>
        </div>
        
        <form onSubmit={handleSubmit} className="property-form">
          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className={formErrors.title ? 'error' : ''}
            />
            {formErrors.title && <div className="error-text">{formErrors.title}</div>}
          </div>
          
          <div className="form-group">
            <label htmlFor="location">Location</label>
            <input
              type="text"
              id="location"
              name="location"
              value={formData.location}
              onChange={handleChange}
              className={formErrors.location ? 'error' : ''}
            />
            {formErrors.location && <div className="error-text">{formErrors.location}</div>}
          </div>
          
          <div className="form-group">
            <label htmlFor="price">Price</label>
            <input
              type="text"
              id="price"
              name="price"
              value={formData.price}
              onChange={handleChange}
              className={formErrors.price ? 'error' : ''}
              placeholder="e.g. $1,500,000"
            />
            {formErrors.price && <div className="error-text">{formErrors.price}</div>}
          </div>
          
          <div className="form-group">
            <label htmlFor="status">Status</label>
            <select
              id="status"
              name="status"
              value={formData.status}
              onChange={handleChange}
            >
              <option value="listed">Listed</option>
              <option value="pending">Pending</option>
              <option value="sold">Sold</option>
            </select>
          </div>
          
          <div className="form-actions">
            <button type="submit" className="save-btn">
              <FiSave /> Save Property
            </button>
            <button type="button" className="cancel-btn" onClick={onClose}>
              <FiX /> Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

function PropertyManagement() {
  const [properties, setProperties] = useState([
    {
      id: 1,
      title: "Luxury Chalet",
      location: "Verbier, Switzerland",
      price: "$2,500,000",
      status: "listed",
      is_deleted: 0
    },
    {
      id: 2,
      title: "Mountain View Chalet",
      location: "Zermatt, Switzerland",
      price: "$1,800,000",
      status: "listed",
      is_deleted: 0
    },
    {
      id: 3,
      title: "Cozy Alpine Retreat",
      location: "Gstaad, Switzerland",
      price: "$3,200,000",
      status: "pending",
      is_deleted: 0
    },
    {
      id: 4,
      title: "Ski-in/Ski-out Chalet",
      location: "Chamonix, France",
      price: "$4,200,000",
      status: "listed",
      is_deleted: 0
    },
    {
      id: 5,
      title: "Modern Mountain Home",
      location: "Aspen, USA",
      price: "$5,800,000",
      status: "sold",
      is_deleted: 0
    },
    {
      id: 6,
      title: "Lakeside Chalet",
      location: "Interlaken, Switzerland",
      price: "$2,950,000",
      status: "listed",
      is_deleted: 0
    },
    {
      id: 7,
      title: "Historic Alpine Lodge",
      location: "St. Moritz, Switzerland",
      price: "$3,750,000",
      status: "pending",
      is_deleted: 0
    }
  ]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentProperty, setCurrentProperty] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('newest');
  const [showSortDropdown, setShowSortDropdown] = useState(false);
  const [viewMode, setViewMode] = useState('active'); // 'active' or 'trash'
  
  // Pagination states
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);

  // Initialize filtered properties
  useEffect(() => {
    // In a real application, you would fetch from an API
    // For this example, we're using the local state
    setFilteredProperties(properties.filter(property => 
      viewMode === 'active' ? property.is_deleted === 0 : property.is_deleted === 1
    ));
  }, [properties, viewMode]);

  // Filter and sort properties when search term or sort order changes
  useEffect(() => {
    if (!properties.length) return;
    
    let result = [...properties].filter(property => 
      viewMode === 'active' ? property.is_deleted === 0 : property.is_deleted === 1
    );
    
    // Filter based on search term
    if (searchTerm) {
      const lowercasedSearch = searchTerm.toLowerCase();
      result = result.filter(property => 
        property.title.toLowerCase().includes(lowercasedSearch) || 
        property.location.toLowerCase().includes(lowercasedSearch) ||
        property.price.toLowerCase().includes(lowercasedSearch)
      );
    }
    
    // Sort or filter based on sort order
    switch(sortOrder) {
      case 'price-high':
        result.sort((a, b) => {
          const priceA = parseFloat(a.price.replace(/[$,]/g, ''));
          const priceB = parseFloat(b.price.replace(/[$,]/g, ''));
          return priceB - priceA;
        });
        break;
      case 'price-low':
        result.sort((a, b) => {
          const priceA = parseFloat(a.price.replace(/[$,]/g, ''));
          const priceB = parseFloat(b.price.replace(/[$,]/g, ''));
          return priceA - priceB;
        });
        break;
      case 'name-asc':
        result.sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'name-desc':
        result.sort((a, b) => b.title.localeCompare(a.title));
        break;
      case 'status-listed':
        result = result.filter(property => property.status === 'listed');
        break;
      case 'status-pending':
        result = result.filter(property => property.status === 'pending');
        break;
      case 'status-sold':
        result = result.filter(property => property.status === 'sold');
        break;
      default:
        // 'newest' - default sort by ID descending
        result.sort((a, b) => b.id - a.id);
        break;
    }
    
    setFilteredProperties(result);
    // Reset to first page when filtering/sorting changes
    setCurrentPage(1);
  }, [properties, searchTerm, sortOrder, viewMode]);

  const handleEditClick = (propertyId) => {
    const property = properties.find(property => property.id === propertyId);
    if (property) {
      setCurrentProperty(property);
      setIsModalOpen(true);
    }
  };

  const handleAddClick = () => {
    setCurrentProperty(null);
    setIsModalOpen(true);
  };

  const handleMoveToTrash = (propertyId) => {
    if (window.confirm('Are you sure you want to move this property to trash?')) {
      // In a real application, you would call an API
      // For this example, we're updating the local state
      const updatedProperties = properties.map(property => 
        property.id === propertyId ? {...property, is_deleted: 1} : property
      );
      setProperties(updatedProperties);
    }
  };
  
  const handleRestoreProperty = (propertyId) => {
    if (window.confirm('Are you sure you want to restore this property?')) {
      // In a real application, you would call an API
      // For this example, we're updating the local state
      const updatedProperties = properties.map(property => 
        property.id === propertyId ? {...property, is_deleted: 0} : property
      );
      setProperties(updatedProperties);
    }
  };
  
  const handleDeletePermanently = (propertyId) => {
    if (window.confirm('Are you sure you want to permanently delete this property? This action cannot be undone.')) {
      // In a real application, you would call an API
      // For this example, we're updating the local state
      const updatedProperties = properties.filter(property => property.id !== propertyId);
      setProperties(updatedProperties);
    }
  };

  const handleSaveProperty = (propertyData) => {
    // In a real application, you would call an API
    // For this example, we're updating the local state
    if (propertyData.id) {
      // Update existing property
      const updatedProperties = properties.map(property => 
        property.id === propertyData.id ? {...propertyData, is_deleted: property.is_deleted} : property
      );
      setProperties(updatedProperties);
    } else {
      // Add new property
      const newProperty = {
        ...propertyData,
        id: Math.max(...properties.map(p => p.id)) + 1,
        is_deleted: 0
      };
      setProperties([...properties, newProperty]);
    }
    
    setIsModalOpen(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (value) => {
    setSortOrder(value);
    setShowSortDropdown(false);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const toggleViewMode = () => {
    setViewMode(prevMode => prevMode === 'active' ? 'trash' : 'active');
    // Reset filters when switching views
    setSearchTerm('');
    setSortOrder('newest');
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredProperties.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredProperties.length / itemsPerPage);

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);
  const nextPage = () => setCurrentPage(prev => Math.min(prev + 1, totalPages));
  const prevPage = () => setCurrentPage(prev => Math.max(prev - 1, 1));
  const firstPage = () => setCurrentPage(1);
  const lastPage = () => setCurrentPage(totalPages);

  // Helper function to render status badge
  const renderStatusBadge = (status) => {
    let className = "status ";
    
    switch(status) {
      case 'listed':
        className += "active";
        break;
      case 'pending':
        className += "pending";
        break;
      case 'sold':
        className += "sold";
        break;
      default:
        className += "active";
    }
    
    return (
      <span className={className}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  return (
    <section id="properties" className="dashboard-section">
      <div className="section-header">
        <div className="section-title-row">
          <h2 className="section-title">
            {viewMode === 'active' ? 'Property Management' : 'Property Trash'}
          </h2>
          <div className="header-actions">
            <button 
              className={`toggle-view-btn ${viewMode === 'trash' ? 'active' : ''}`} 
              onClick={toggleViewMode}
              title={viewMode === 'active' ? 'View Trash' : 'View Active Properties'}
            >
              {viewMode === 'active' ? (
                <><FiTrash2 /> View Trash</>
              ) : (
                <><FiArchive /> View Active Properties</>
              )}
            </button>
            
            {viewMode === 'active' && (
              <button className="add-chalet-btn" onClick={handleAddClick}>
                <FiPlus /> Add New Property
              </button>
            )}
          </div>
        </div>
        <p className="section-subtitle">
          {viewMode === 'active' 
            ? 'Manage all listed properties' 
            : 'View and manage deleted properties'}
        </p>
        
        <div className="search-card">
          <div className="search-input-wrapper">
            <FiSearch className="search-icon" />
            <input
              type="text"
              className="search-input"
              placeholder="Search by title, location or price..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
            {searchTerm && (
              <button className="clear-search-btn" onClick={clearSearch}>
                <FiX />
              </button>
            )}
          </div>
          
          <div className="sort-controls">
            <div className="sort-dropdown-container">
              <button 
                className="sort-dropdown-btn" 
                onClick={() => setShowSortDropdown(!showSortDropdown)}
              >
                {sortOrder === 'newest' && 'Newest First'}
                {sortOrder === 'price-high' && 'Price (High to Low)'}
                {sortOrder === 'price-low' && 'Price (Low to High)'}
                {sortOrder === 'name-asc' && 'Name (A-Z)'}
                {sortOrder === 'name-desc' && 'Name (Z-A)'}
                {sortOrder === 'status-listed' && 'Status: Listed'}
                {sortOrder === 'status-pending' && 'Status: Pending'}
                {sortOrder === 'status-sold' && 'Status: Sold'}
                <FiChevronDown />
              </button>
              
              {showSortDropdown && (
                <div className="sort-dropdown-menu">
                  <div className="dropdown-section">
                    <div className="dropdown-section-title">Sort</div>
                    <button 
                      className={`sort-option ${sortOrder === 'newest' ? 'active' : ''}`}
                      onClick={() => handleSortChange('newest')}
                    >
                      Newest First
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'price-high' ? 'active' : ''}`}
                      onClick={() => handleSortChange('price-high')}
                    >
                      Price (High to Low)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'price-low' ? 'active' : ''}`}
                      onClick={() => handleSortChange('price-low')}
                    >
                      Price (Low to High)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'name-asc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('name-asc')}
                    >
                      Name (A-Z)
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'name-desc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('name-desc')}
                    >
                      Name (Z-A)
                    </button>
                  </div>
                  
                  <div className="dropdown-divider"></div>
                  
                  <div className="dropdown-section">
                    <div className="dropdown-section-title">Filter by Status</div>
                    <button 
                      className={`sort-option ${sortOrder === 'newest' || sortOrder === 'price-high' || sortOrder === 'price-low' || sortOrder === 'name-asc' || sortOrder === 'name-desc' ? 'active' : ''}`}
                      onClick={() => handleSortChange('newest')}
                    >
                      All Statuses
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-listed' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-listed')}
                    >
                      Listed Only
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-pending' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-pending')}
                    >
                      Pending Only
                    </button>
                    <button 
                      className={`sort-option ${sortOrder === 'status-sold' ? 'active' : ''}`}
                      onClick={() => handleSortChange('status-sold')}
                    >
                      Sold Only
                    </button>
                  </div>
                </div>
              )}
            </div>
            
            <button className="refresh-btn" onClick={() => {}} title="Refresh Properties">
              <FiRefreshCw />
            </button>
          </div>
        </div>
      </div>
      
      <div className="section-content">
        {loading ? (
          <div className="loading-spinner">Loading properties...</div>
        ) : error ? (
          <div className="error-message">{error}</div>
        ) : filteredProperties.length === 0 ? (
          <div className="no-results">
            {viewMode === 'active' ? (
              searchTerm || sortOrder.startsWith('status-') ? 
                'No properties match your search criteria.' : 
                'No properties found.'
            ) : (
              'The trash is empty.'
            )}
          </div>
        ) : (
          <>
            <div className="table-container">
              <table className="data-table">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Location</th>
                    <th>Price</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {currentItems.map(property => (
                    <tr key={property.id}>
                      <td>{property.id}</td>
                      <td>{property.title}</td>
                      <td>{property.location}</td>
                      <td>{property.price}</td>
                      <td>{renderStatusBadge(property.status)}</td>
                      <td>
                        <div className="action-buttons">
                          {viewMode === 'active' ? (
                            <>
                              <button 
                                className="edit-btn"
                                onClick={() => handleEditClick(property.id)}
                                title="Edit Property"
                              >
                                <FiEdit />
                              </button>
                              <button 
                                className="trash-btn"
                                onClick={() => handleMoveToTrash(property.id)}
                                title="Move to Trash"
                              >
                                <FiTrash2 />
                              </button>
                            </>
                          ) : (
                            <>
                              <button 
                                className="restore-btn"
                                onClick={() => handleRestoreProperty(property.id)}
                                title="Restore Property"
                              >
                                <FiRotateCcw />
                              </button>
                              <button 
                                className="delete-permanently-btn"
                                onClick={() => handleDeletePermanently(property.id)}
                                title="Delete Permanently"
                              >
                                <FiTrash />
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {/* Pagination Controls */}
            <div className="pagination-container">
              <div className="pagination-info">
                Showing {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredProperties.length)} of {filteredProperties.length} properties
              </div>
              
              <div className="pagination-controls">
                <button 
                  className="pagination-btn first-page" 
                  onClick={firstPage}
                  disabled={currentPage === 1}
                  title="First Page"
                >
                  <FiChevronsLeft />
                </button>
                
                <button 
                  className="pagination-btn prev-page" 
                  onClick={prevPage}
                  disabled={currentPage === 1}
                  title="Previous Page"
                >
                  <FiChevronLeft />
                </button>
                
                <div className="pagination-pages">
                  {Array.from({ length: Math.min(5, totalPages) }).map((_, index) => {
                    // Show pages around current page
                    let pageToShow;
                    
                    if (totalPages <= 5) {
                      // If we have 5 or fewer pages, show all pages
                      pageToShow = index + 1;
                    } else {
                      // For more than 5 pages, show a window of pages around current page
                      const offset = Math.max(0, Math.min(totalPages - 5, currentPage - 3));
                      pageToShow = index + 1 + offset;
                    }
                    
                    return (
                      <button
                        key={pageToShow}
                        onClick={() => paginate(pageToShow)}
                        className={`pagination-btn page-number ${currentPage === pageToShow ? 'active' : ''}`}
                      >
                        {pageToShow}
                      </button>
                    );
                  })}
                </div>
                
                <button 
                  className="pagination-btn next-page" 
                  onClick={nextPage}
                  disabled={currentPage === totalPages}
                  title="Next Page"
                >
                  <FiChevronRight />
                </button>
                
                <button 
                  className="pagination-btn last-page" 
                  onClick={lastPage}
                  disabled={currentPage === totalPages}
                  title="Last Page"
                >
                  <FiChevronsRight />
                </button>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Property Modal for Adding/Editing */}
      <PropertyModal
        isOpen={isModalOpen}
        onClose={closeModal}
        property={currentProperty}
        onSave={handleSaveProperty}
      />
    </section>
  );
}

export default PropertyManagement;
